css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("leagueoflegends.com") {
.gs-container
{
  background-color: Transparent;
   color: #fff
}
  .champion-info
{
  background-color: Transparent;
}
  .primary-role
{
  background-color: Transparent;
}
  .content-border > *
{
  background-color: Transparent;
   color: #fff
}
  .riot-apollo div, .riot-apollo dl, .riot-apollo dt, .riot-apollo dd, .riot-apollo ul, .riot-apollo ol, .riot-apollo li, .riot-apollo h1, .riot-apollo h2, .riot-apollo h3, .riot-apollo h4, .riot-apollo h5, .riot-apollo h6, .riot-apollo pre, .riot-apollo form, .riot-apollo fieldset, .riot-apollo p, .riot-apollo blockquote, .riot-apollo th, .riot-apollo td, .riot-apollo textarea
{
  background-color: #060606;
   color: #fff
}
.section-wrapper .section-wrapper-content .section-wrapper-content-wrapper
{
  background-color: #060606;
   color: #fff
}
  html body p
{
  color: #fff
}
  .riot-apollo .riot-discussion .riot-comment
{
  background-color: #060606;
}
  .riot-apollo .riot-list-item
{
background-color: #060606;
  color: #fff
}
  .riot-apollo .riot-discussion {
    background-color: #060606;
}
  .riot-apollo .riot-discussion .riot-comment .riot-collection > .riot-comment {
    background-color: #060606;
}
  .riot-apollo .riot-discussion .riot-comment .riot-collection > .riot-comment > .riot-collection > .riot-comment {
    background-color: #060606;
}
  .riot-apollo .riot-discussion .riot-comment .riot-collection > .riot-comment > .riot-collection > .riot-comment > .riot-collection > .riot-comment {
    background-color: #060606;
}
  .riot-apollo .riot-discussion .riot-comment .riot-collection > .riot-comment > .riot-collection > .riot-comment > .riot-collection > .riot-comment > .riot-collection > .riot-comment
  {
    background-color: #060606;}
  .riot-apollo .riot-discussion .riot-more {
    background-color: #121212;
}
  .riot-apollo .riot-discussion .riot-more.bar {
    background-color: #121212;
}
  .white-stone {
    background-color: #121212;
}
  h1.article-title {
    color: #848484
}
  h1, h2, h3, h4
{
    color: #d9d9d9;
}
  .riot-apollo .riot-discussion .riot-comment .riot-body blockquote {
    background: #060606 none repeat scroll 0 0;
    border-left: 10px solid #060606;
}
  .medium-card-content .field-name-field-body-small
{
  color: #848484;
}
  #copa-latam-datos .standings .team-name
{
  color: #848484;
}
  .timeline-texto
{
  color: #848484;
}
  .index #last-news .article-table a, .index #last-news .article-table a:visited
{
  color: #9ec4de;
}
  .index #last-videos .default-1-3 strong a
{
  color: #257372;
}
  .box.main
{
  background-color: #060606;
}
  .box.secondary
{
  background-color: #060606;
}
  header.header-subtle
{
  background-color: #060606;
  font-size: 22px;
  color: #fff;
}
  .gray-stone
{
  background-color: #242424;
}
  .grid-list > li > *
{
  background-color: #242424;
}
  html body a, html body a:visited
{
  color: #76d3d2;
}
  table.table td, table.table th
{
  background-color: #242424;
  border-top-color: #484848;
  border-right-color: #484848;
  border-bottom-color: #484848;
  border-left-color: #484848;
}
  .content-border
{
  background-color: #242424;
}
  .wizard-pager li
{
  background-color: #060606;
  background-image: none;
  background-repeat: repeat-x;
  background-attachment: scroll;
  background-position: 0px -48px;
  background-clip: border-box;
  background-origin: padding-box;
  background-size: auto auto;
  padding-top: 12px;
  padding-right: 14px;
  padding-bottom: 6px;
  padding-left: 14px;
  cursor: pointer;
  position: relative;
}
  .wizard-pager span
{
  background-color: #060606;
  background-image: none;
  background-repeat: no-repeat;
  background-attachment: scroll;
  background-position: 0px 0px;
  background-clip: border-box;
  background-origin: padding-box;
  background-size: auto auto;
  display: block;
  position: absolute;
  top: 0px;
  width: 30px;
  height: 49px;
}
  .riot-apollo .riot-reply-form-container textarea, .riot-apollo .riot-edit-form-container textarea
{
  background-color: #060606;
}
  .riot-apollo .riot-reply-form-container textarea:focus, .riot-apollo .riot-edit-form-container textarea:focus
    {
  background-color: #060606;
}
  .toolbar_dropdown
{
  background-color: #242424;
}
  .pager a
  {
  background-color: #242424;
}
  .pager .active
  {
  background-color: #76d3d2;
}
  .latest-update
{
  background-color: #242424;
}
  .dropdown-menu
{
  background-color: #060606;
}
  .services .service .service-name
{
  color: #909090;
}
html body a, html body a:visited
{
  color: #909090;
}
  blockquote.blockquote
{
color: #909090;
}
  .jug-main p, .jug-champsection p
{
  color: #a6956a;
}
  .tab p
{
  background-color: #060606;
}
  .jug-champgradient2
  {background-image: none;}
.jug-champgradient
  {background-image: none;}
}
