css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("sta.sh") {

body, body.stash-page, body.stash-page #output, body.stash-page #output .dev-page-container.bubbleview{
  background:#d3dfd2 none repeat scroll 0% 0% !important;
}
body.stash-page #overhead-collect{
  background-color:#475c4d;
  height:50px;
}
tr > td, .oh-stashtoolbar, #overhead div.default-toolbar .headerMenu{
  bottom: -2px;
}
#overhead-collect #overhead tbody tr td.oh-stashinactivetab{
  bottom: -5px;
}
.oh-hasmenu.mmhover,.oh-stashmain.mmhover > a.oh-l,.oh-hasmenu.mmhover > a.oh-l > .oh-stashlogo{
  background-color:#313836;
}
.oh-stashmain > a.oh-l > .oh-stashlogo{
  background-image:url('http://i.imgur.com/8YhKPMU.png');
  background-size:110px;
  background-repeat:no-repeat;
  background-position:5px 0px;
  width:115px;
  height:30px;
}
.oh-stashmain > a.oh-l > .oh-stashlogo > img.avatar{
  display:none;
}
.oh-stashmain > a.oh-l > .oh-stashlogo > .oh-stashlogo-name{
  display:none;
}
.oh-stashmain > a.oh-l > span.oh-stashtitle{
  width:12px;
  height:19px;
  background-position: -388px -18px;
}
.oh-stashmain.mmhover > a.oh-l > span.oh-stashtitle{
  background-color:#313836;
  background-position: -388px -18px;
  width:12px;
  height:19px;
}
.oh-stashmain > #oh-mainmenu{
  background-color:#313836;
}
.oh-stashmain > #oh-mainmenu a.mi.mi-logout{
  background-color:#313836;
  background-image:none;
}
.oh-stashmain > #oh-mainmenu a.mi{
  color:#C0D0CA;
}
.oh-stashmain > #oh-mainmenu a.mi:hover, .oh-stashmain > #oh-mainmenu a.mi.mi-logout:hover{
  background:#508fa3 none repeat scroll 0 0;
/*  color:#C0D0CA; */
}
.oh-stashmain.mmhover > .oh-l{
  border-bottom:1px solid;
  border-right:medium none;
  border-color:#26332F -moz-use-text-color #52605D;
}
.oh-stashmain > #oh-mainmenu .oh-hr{
  border-bottom:1px solid;
  border-top:none;
  border-color:#26332F -moz-use-text-color #52605D;
}
.oh-stashmain > #oh-mainmenu .oh-hr.oh-hrlast{
  border-top:1px solid;
  border-color:#26332F -moz-use-text-color #52605D;
  display:block;
}
.default-toolbar .headerButton, .default-toolbar .headerButton b{
   color:#fff;
 }
.default-toolbar .headerButton:hover, .default-toolbar .headerButton b:hover{
   color:#434349;
 } 
  
}
