css:
@-moz-document domain("x.sankuai.com") {
::-webkit-scrollbar-track {
    background-color: initial;
    -webkit-border-radius: initial;
    border-radius: initial;
}
::-webkit-scrollbar {
    width: 0;
    height: initial;
}
::-webkit-scrollbar-thumb {
    background-color: initial;
    -webkit-border-radius: initial;
    border-radius: initial;
    outline: initial;
    outline-offset: initial;
    border: initial;
}
::-webkit-scrollbar-thumb:hover {
    background-color: initial;
    -webkit-border-radius: initial;
    border-radius: initial;
}
html,
body,
.panel-chat,
.panel-left-chat,
.chat,
.panel-chat-container,
.panel-chat-top,
#dx_chatMainPanelWrap {
    height: 100%;
    overflow: hidden;
    box-sizing: border-box;
}
body {
    background: #f3f3f3;
    font-family: "San Francisco", "Helvetica Neue", Helvetica, "Segoe UI", Tahoma, Arial, sans-serif;
}
::-moz-selection {
    background: #3f7cc3;
    color: #fff;
}
::selection {
    background: #3f7cc3;
    color: #fff;
}
.ft-chat {
    display: none !important;
}
.menu {
    box-shadow: 0 0 3px rgba(100, 100, 100, .5);
    border-radius: 2px;
}
.panel-chat {
    padding: 0;
    width: auto;
    overflow: auto;
    border-radius: 0;
}
#dx_chatMainPanelWrap {
    background: #fbfbfb;
    width: auto;
}
.panel-chat-wrap {
    height: 100% !important;
}
.panel-chat-container {
    padding-bottom: 50px;
}
.panel-chat .chatContent {
    padding-bottom: 60px;
}
.fb-man {
    display: none;
}
.panel-chat-bottom {
    position: absolute;
    z-index: 11;
    width: 100%;
    bottom: 0;
    left: 0;
}
.panel-chat-container .hd-chat-title,
.panel-chat .chat,
.panel-chat .panel-chat-container,
.panel-left-chat,
.panel-left-header {
    border-radius: 0;
    box-shadow: none;
    z-index: 2;
}
.panel-left-header {
    position: absolute;
