    box-shadow: none;
    float: left;
    margin: 4px 11px;
}
.panel-left-header .myProfile .info {
    margin: 0;
    width: auto;
    height: 44px;
    line-height: 44px;
}
.panel-left-header .split {
    visibility: hidden;
}
.panel-left-header a.globalMenu {
    background-position: -32px 0;
}
.panel-left-header .panel-global-menu {
    bottom: 30px;
    top: auto;
}
.panel-left-header .panel-global-menu.arrow-menu::after {
    bottom: -5px;
    top: auto;
    border: 0;
    border-bottom: 1px solid #abafb3;
    border-right: 1px solid #abafb3;
}
.panel-left-header .panel-global-menu .menu-item:last-child {
    border-bottom: none;
}
.panel-left-chat .list-info-content > section::after {
    display: table;
    content: ' ';
    clear: both;
    height: 1;
}
.panel-chat .chat {
    margin-left: 280px;
}
.panel-chat .panel-list-content {
    z-index: 1;
    height: 100% !important;
    border-radius: 0;
    background: none;
    overflow-y: auto;
}
.panel-left-chat .panel-list-menu {
    position: absolute;
    padding: 0;
    margin: 0;
    width: 100%;
    top: 0;
    left: 0;
    z-index: 2;
}
.panel-left-chat .list-search {
    max-width: 800px;
    min-width: 340px;
    max-height: 450px;
    overflow-y: auto;
    border-radius: 2px;
    top: 85px;
    left: 3px;
    z-index: 999999;
    position: absolute;
    background-color: #F5F5F5;
    border-left: 1px solid #afb4bc;
    border-right: 1px solid #afb4bc;
    box-shadow: 0 4px 6px rgba(51, 51, 51, .7);
}
.panel-chat .panel-search {
    position: absolute;
    left: 0;
    top: 40px;
    width: 100%;
    height: 45px;
    padding: 0;
    margin: 0;
    border: none;
    box-shadow: none;
    background: none;
}
.panel-left-chat .panel-search .icon-search {
    left: 7px;
    top: 13px;
}
.panel-left-chat .panel-search .searchClean {
    top: 12px;
    right: 10px;
}
.panel-search input {
    font: 13px/20px Arial;
    background-color: transparent;
    margin-left: 0;
    padding: 0 25px;
    border: none;
    width: 230px;
    margin-top: 0;
    border-radius: 4px;
    line-height: 18px;
    background: rgba(0, 0, 0, .2);
    color: #cacbcf;
    height: 45px;
    width: 350px;
    border-bottom: 1px solid #3d2c3b;
}
.panel-search input:hover,
.panel-search input:focus,
.panel-search input:active {
    background: rgba(0, 0, 0, .2) !important;
    color: #fff;
}
.panel-left-chat {
    background: #3f7cc3;
    padding: 86px 0 35px;
}
.css3-slide .panel-left-chat {
    -webkit-transition: left 10ms ease-in-out 0s;
    -moz-transition: left 10ms ease-in-out 0s;
    -ms-transition: left 10ms ease-in-out 0s;
    -o-transition: left 10ms ease-in-out 0s;
    transition: left 10ms ease-in-out 0s;
}
.panel-left-chat .panel-list-menu {
    background: rgba(0, 0, 0, .1);
    box-shadow: none;
    border: none;
}
.panel-left-chat .panel-list-menu .menu-tab {
    border: none;
    box-shadow: none;
    text-align: center;
}
.panel-left-chat .panel-list-menu .icon-tab {
    display: none;
}
.panel-left-chat .panel-list-menu .menu-tab > a::before {
    content: attr(title);
    color: #fff;
    line-height: 40px;
}
.panel-left-chat .panel-list-menu.tab-contact .menu-tab-item-contact,
.panel-left-chat .panel-list-menu.tab-room .menu-tab-item-room,
.panel-left-chat .panel-list-menu.tab-chat .menu-tab-item-chat {
    background: rgba(0, 0, 0, .1);
}
.panel-chat .item-info-chat {
    background: none;
    border: none;
    box-shadow: none;
    height: auto;
}
.panel-left-chat .item-info-chat {
    padding-top: 5px;
    padding-bottom: 5px;
}
.panel-left-chat .item-info-chat .avatar_wrap .avatar {
    border-radius: 50%;
    width: 20px;
    height: 20px;
}
.panel-left-chat .item-info-chat .info .nickName .name {
    color: #f9fafd;
    max-width: 121px;
}
.panel-left-chat .item-info-chat .info .desc {
    color: #f9fafd;
}
.panel-left-chat .item-info-chat .extend {
    color: #c6c7c9;
}
.panel-chat .panel-chat-top {
    background: none;
    overflow: hidden;
}
.panel-chat .panel-chat-wrap {
    background: none;
}
.panel-chat-container .hd-chat-title {
    background: none;
    border: none;
    box-shadow: none;
}
.panel-chat-container .btn-chat-setting a {
    background-position: -29px 0;
}
.panel-chat-container .btn-roster-list a {
    background-position: -31px 0;
}
.panel-chat-roster .btn-group-add a {
    background-position: -29px -1px;
}
.panel-chat .chatNameWrap {
    text-align: left;
    padding-left: 1em;
}
.panel-chat-container .glyphicon {
    color: #999999;
}
.panel-chat-container .glyphicon:hover {
    color: #333;
}
.hd-chat-title .editChatTitleWrap input,
.panel-chat .chatNameWrap .chatName {
    color: #333;
    font-weight: bold;
}
.panel-chat .chatNameWrap .chatName::before {
    content: '@';
    color: #999;
    padding-right: 5px;
}
.panel-chat .editChatTitleWrap input {
    width: auto;
    margin: 7px 0;
    text-align: left;
}
.panel-chat .editChatTitleWrap {
    left: 27px;
    right: auto;
    text-align: left;
}
.panel-chat-container .chatContent .time::before {
    border: none;
    border-top: 1px solid #e9eaec;
}
.panel-chat-container .chatContent .time {
    box-shadow: none;
    width: auto;
    text-shadow: none;
    margin-bottom: 20px;
}
.panel-chat-container .chatContent .time p {
    background: #fff;
    color: #555;
    font-size: 13px;
    font-weight: 700;
    padding: 0 1em;
    font-family: Lato, sans-serif;
}
.panel-chat-container .chatContent .item {
    overflow: hidden;
}
.panel-chat-container .chatContent .person {
    position: relative;
    overflow: visible;
}
.panel-chat-container .chatContent .person > img {
    border-radius: 2px;
}
.panel-chat-container .chatContent .talker-name {
    display: inline !important;
    position: absolute;
    font-size: 13px;
    font-weight: bold;
    left: 55px;
    top: 0;
    color: #000;
    margin-left: 0;
    text-align: left;
    text-indent: 0;
}
.panel-chat-container .chatContent .me .talker-name {
    display: inline !important;
    position: absolute;
    font-size: 13px;
    font-weight: bold;
    left: auto;
    right: 55px;
    top: 0;
    color: #000;
    margin-left: 0;
    text-align: right;
    text-indent: 0;
}
.panel-chat-container .chatContent .cont {
    margin: 22px 10px 0 55px;
    float: none;
    font-size: 13px;
    background: none;
    border: none;
    border-radius: 0;
    box-shadow: none;
    max-width: 100%;
    padding: 0;
    color: #444;
}
.panel-chat-container .chatContent .cont:before {
    display: none;
}
.panel-chat-container .chatContent .cont a {
    font-weight: 400;
    color: #3e86e8;
    text-decoration: underline;
}
.panel-chat-container .chatContent .cont .pic img {
    border: 1px solid #dadada;
    border-radius: 5px;
    box-shadow: 0 0 3px rgba(0, 0, 0, 0.1)
}
.panel-chat-container .chatContent .cont h4 a,
.panel-chat-container .chatContent .cont .pic a {
    font-weight: normal;
    color: #0070C9;
}
.panel-chat-container .chatContent .me .cont {
    float: right;
    text-align: left;
    margin: 22px 0 0 0;
}
.panel-chat-container .chatContent .me .person {
    float: right;
    margin: 0;
    margin-left: 15px;
}
.panel-chat-container .chatContent .groupchat em {
    color: #5a5b5f;
}
.panel-chat-container .chatContent .cont .pic img {
    min-height: 0;
    min-width: 0;
    max-width: 100%;
    max-height: 200px;
}
.panel-chat-container .chatContent .cont .pic-text img {
    width: 16px;
    height: 16px;
}
.panel-chat .panel-input-area {
    overflow: hidden;
}
.panel-input-area .attach {
    background: #2f3135;
}
.panel-input-area .chatInput {
    border-radius: 3px;
    box-shadow: none;
    background: rgba(0, 0, 0, .3);
    width: calc(100% - 200px);
    color: #fff;
    margin: 0 20px;
}
.panel-input-area .chatInput:hover,
.panel-input-area .chatInput:focus,
.panel-input-area .chatInput:active {
    background: rgba(0, 0, 0, .5);
}
.panel-input-area .btn-chatSend[disabled],
.panel-input-area .btn-chatSend[disabled]:hover,
.panel-input-area .btn-chatSend[disabled]:focus,
.panel-input-area .btn-chatSend[disabled]:active {
    background: rgba(255, 255, 255, .1);
    cursor: text;
    color: #999;
}
.panel-input-area .btn-chatSend {
    background: rgba(0, 0, 0, .5);
    font-size: 13px;
    border-radius: 3px;
}
#dx_updateTips,
#dx_logining {
    left: 280px;
    right: 0;
    width: auto;
    margin: 0;
    z-index: 100;
    background: #2A80B9;
    box-shadow: none;
    border: none;
    border-radius: 0;
    color: #fff;
    padding: 5px 0;
}
#dx_updateTips a,
#dx_logining a {
    color: #fff;
    text-decoration: underline;
}
.chatsplit #dx_chatMainPanelWrap {
    width: auto;
    margin-right: 173px;
}
.panel-chat .panel-chat-roster,
.panel-chat-container .chatsplit .panel-chat-roster {
    background: rgba(240, 240, 240, .2);
    border: none;
    box-shadow: none;
    border-left: 1px solid #fafafa;
    transition: width 10ms ease 0s;
    -webkit-transition: width 10ms ease 0s;
}
.panel-chat-container .chatsplit .btn-group-add a {
    -webkit-transition: 0;
    -moz-transition: 0;
    -o-transition: 0;
    -ms-transition: 0;
    transition: 0;
}
.panel-chat .groupNameWrap {
    color: inherit;
    font-family: inherit;
}
.panel-chat .hd-chat-roster-title {
    background: none;
    border: none;
    border-radius: 0;
    background: none;
}
.panel-chat .list-chat-roster {
    box-shadow: none;
    height: calc(100% - 42px) !important;
    border: none;
}
.panel-chat-container .list-roster .photo-roster {
    border-radius: 50%;
    width: 16px;
    height: 16px;
    float: left;
}
.panel-chat-container .list-roster .item-roster {
    border: none;
    height: 20px;
    line-height: 20px;
    padding: 5px 10px;
}
.panel-chat-container .list-roster .item-roster .roster-name {
    font-size: 13px;
    margin-left: 25px;
    margin-top: 0;
    width: auto;
    float: none;
}
.panel-chat-container .list-roster .group-moderator .item-roster::after {
    top: 15px;
    left: 17px;
}
.dialog {
    margin-left: -209px;
    box-shadow: none;
    border: none;
    border-radius: 0;
}
.dialog-image {
    margin-left: auto;
}
.window {
    background: #fff;
    border-radius: 0;
    box-shadow: none;
}
.window .header {
    border-radius: 0;
    background: none;
    border: none;
}
.window .header p {
    text-shadow: none;
    font-weight: 400;
}
.window .header .closeIconPanel {
    box-shadow: none;
    border: none;
}
a.btn-initiate,
a.btn-initiate-chat {
    background-image: none;
}
a.btn {
    background-image: none;
}
.dialog-person-vcard .window .content .panel-info-base .panel-info-text .name-vcard {
    font-size: 13px;
}
/* @ .dialog-person-vcard .window .content .panel-info-base .panel-info-text .name-vcard */

#genderPic.glyphicon {
    display: inline !important;
}
#genderPic.glyphicon::before {
    line-height: inherit;
    vertical-align: middle;
    font-size: inherit;
    margin: 0;
    padding: 0;
}
#genderPic.glyphicon::before {
    font-family: inherit;
}
#genderPic.glyphicon-male::before {
    content: '汉子 :(';
}
#genderPic.glyphicon-female::before {
    content: '妹子 :)';
}
/* 搜索框 - tab */

.panel-left-chat .search-tab span {
    padding: 2px 0;
    background-color: rgba(0, 0, 0, .1);
    color: #666;
}
.panel-left-chat .search-tab span::after {
    position: absolute;
    content: '';
    top: 0;
    left: 0;
    width: 1px;
    height: 100%;
    background: -webkit-linear-gradient(rgba(0, 0, 0, .05) 0, rgba(0, 0, 0, .1) 50%, rgba(0, 0, 0, .05) 100%);
    background: linear-gradient(rgba(0, 0, 0, .05) 0, rgba(0, 0, 0, .1) 50%, rgba(0, 0, 0, .05) 100%);
}
.panel-left-chat .search-tab span.selected::before {
    width: 7px;
    height: 7px;
    bottom: -4px;
    left: 50%;
    transform: translate(-50%, 0) rotate(45deg);
    -webkit-transform: translate(-50%, 0) rotate(45deg);
    -moz-transform: translate(-50%, 0) rotate(45deg);
    -ms-transform: translate(-50%, 0) rotate(45deg);
    -o-transform: translate(-50%, 0) rotate(45deg);
}
.panel-chat .item-info-chat {
    border-top: 1px solid #4d384b;
    border-bottom: 1px solid #3d2c3b;
}
/* 搜索框 - 结果提示 */

.list-info .tip {
    cursor: initial;
    height: auto;
}
.list-info .tip.more {
    cursor: pointer;
}
/* 搜索框 - 用户列表 */

.list-info .vcard {
    padding: 7px 5px;
    list-style: none;
    border: none;
    outline: none;
}
.list-info .vcard::before {
    position: absolute;
    content: '';
    top: 0;
    left: 0;
    width: 100%;
    background: -webkit-linear-gradient(left, rgba(0, 0, 0, .03) 0, rgba(0, 0, 0, .1) 1.5em, rgba(0, 0, 0, .03) 100%);
    background: linear-gradient(to right, rgba(0, 0, 0, .03) 0, rgba(0, 0, 0, .1) 1.5em, rgba(0, 0, 0, .03) 100%);
    height: 1px;
}
.list-info .vcard.selected {
    background: rgba(0, 0, 0, .03);
    color: inherit;
}
.list-info .vcard > .avatar {
    border-radius: 50%;
}
.list-info .vcard .info > .name {
    max-width: 200px;
}
/* 会话列表 - 会话详情 */

.list-info section.link.unread .descWrapper,
.list-info section.link.selected .descWrapper,
.list-info section.link.not-notify .descWrapper,
.list-info section.link[jid$='muc.meituan.com'] .descWrapper,
.list-info section.link .descWrapper {
    height: 0;
    line-height: 20px;
    font-size: 13px;
}
.list-info section.link[jid$='muc.meituan.com'] .descWrapper {
    height: 20px;
    color: #ccc;
}
.list-info section.link .descWrapper > .desc {
    color: inherit;
}
/* 会话列表 - 颜色 */

.panel-chat .selected .item-info-chat,
.panel-chat .selected .vcard {
    background: none;
}
.list-info section.link::before {
    position: absolute;
    content: '';
    bottom: 0;
    left: 0;
    width: 100%;
    background: -webkit-linear-gradient(left, rgba(255, 255, 255, .05) 0, rgba(255, 255, 255, .03) 1.5em, rgba(255, 255, 255, .05) 100%);
    background: linear-gradient(to right, rgba(255, 255, 255, .05) 0, rgba(255, 255, 255, .03) 1.5em, rgba(255, 255, 255, .05) 100%);
    height: 1px;
}
.list-info section.link {
    opacity: .9;
    transition: opacity .3s;
}
.list-info section.link.not-notify {
    opacity: .5;
}
.list-info section.link.unread,
.list-info section.link.selected,
.list-info section.link:hover {
    opacity: 1;
}
.list-info section.link {
    background: rgba(0, 0, 0, .09);
}
.list-info section.link[jid$='muc.meituan.com']:not(.not-notify) {
    background: rgba(0, 0, 0, .09);
}
.list-info section.link .extend {
    color: rgba(0, 0, 0, .6);
    transition: color .3s;
}
.list-info section.link:hover .extend {
    color: #fafbff;
}
.list-info section.link.selected::after {
    content: " ";
    width: 7px;
    height: 7px;
    display: block;
    position: absolute;
    right: -4px;
    top: 50%;
    background-color: #F5F5F5;
    z-index: 1;
    border-top: 1px solid #fff;
    border-left: 1px solid #fff;
    transform: translate(0, -50%) rotate(45deg);
    -webkit-transform: translate(0, -50%) rotate(45deg);
    -moz-transform: translate(0, -50%) rotate(45deg);
    -ms-transform: translate(0, -50%) rotate(45deg);
    -o-transform: translate(0, -50%) rotate(45deg);
}
body {
    -webkit-font-smoothing: antialiased;
}
.panel-left-chat {
    background: #4d394b;
    width: 350px;
}
.panel-left-chat .item-info-chat .info .nickName .name {
    max-width: 230px;
    font-family: "LanTingHei SC"
}
.list-info section.link .extend {
    color: rgba(255, 255, 255, 0.4)
}
.panel-left-chat .item-info-chat {
    padding-top: 8px;
    padding-bottom: 8px;
}
.panel-chat .chat {
    margin-left: 350px;
}
.panel-chat .top-session .item-info-chat::after {
    border: 6px solid #3a2238;
    border-bottom-color: transparent;
    border-left-color: transparent;
}
.panel-chat .selected .item-info-chat,
.panel-chat .selected .vcard {
    background: #4c9689;
}
.panel-chat .selected .item-info-chat::after {
    border: 6px solid #00deb7;
    border-bottom-color: transparent;
    border-left-color: transparent;
}
.list-info section.link[jid$='muc.meituan.com'] .avatar_wrap {
    background: #4dc27d;
    border-radius: 50%;
    width: 6px;
    height: 6px;
    margin-right: 24px;
    margin-top: 7px;
}
.list-info section.link[jid$='muc.meituan.com'] .avatar {
    opacity: 0;
}
.panel-chat section.link[jid$='muc.meituan.com'] .avatar_wrap .unreadDot {
    top: 0px;
    left: 16px;
}
.list-info-content section.not-notify[jid$='muc.meituan.com'] .avatar_wrap::after {
    right: -7px;
    bottom: -4px;
}
.panel-chat-container .list-roster .group-moderator .item-roster .group-identity {
    top: 3px;
    left: 90px;
}
.panel-chat .panel-chat-container {
    max-width: 600px;
    margin: 85px auto 0;
    border: 1px solid #dadada;
    border-radius: 5px;
    height: auto;
    box-shadow: 0 0 10px rgba(0, 0, 0, 0.2)
}
.panel-chat-wrap {
    height: 500px !important;
}
#dx_chatMainPanelWrap {
    height: auto;
}
@media screen and (max-width: 950px) {
    .panel-left-chat {
        width: 250px;
    }
    .panel-left-chat .item-info-chat .info .nickName .name {
        max-width: 130px;
    }
    .panel-chat .chat {
        margin-left: 250px;
    }
}
