css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("vivo.sx") { 


.regular_header_wrapper {
    background: #fff;
}

.regular_header nav.main_navigation > ul > li {
    line-height: 0px;
}

.regular_header {
    padding-top: 0%;
    padding-bottom: 00px;
    color: #fff !important;
}

section.app_overview {
    background: #fff none repeat scroll 0% 0%;
}

.regular_header nav.main_navigation > ul > li a {
    border: medium none;
    color: #fff;
    box-shadow: none;
    font-family: "Roboto",sans-serif;
    font-size: 0;
}



.regular_header_wrapper .logo > .text-logo {
    background: transparent  no-repeat scroll 0px 0px;
    height: 26px;
    width: 86px;
}

nav.main_navigation {
    float: right;
    margin-top: 0px;
}

.container {
    width: 100%;
}

.text-logo {
    background: transparent 0px 0px;
    height: 26px;
    width: 86px;
}

footer.regular_footer .footer_left {
    font-size: 0px;
    text-align: left;
}

footer.regular_footer .footer_right a {
    white-space: nowrap;
    font-size: 0px;
    margin-left: 10px;
    font-family: "Open Sans",sans-serif;
    line-height: 20px;
    color: #3BCBF6;
    transition: color 0.4s ease-in 0s;
}


Element {
    height: 728px;
    margin: -10px auto 10px;
}

nav.main_navigation {
    color: #FFF;
    position: fixed;
    background: #fff none repeat scroll 0% 0%;
    margin: 0px;
    height: 0%;
    top: 0px;
    right: 0px;
    width: 0px;
    padding-top: 0px;
    padding-left: 0px;
    z-index: -1;
}
}
