css:
@-moz-document domain('goodreads.com')
{
:root {
    --bg-input-search: #3D3C4C;
    --fg-input-search: #ccc;
    --bg-input-para: black;
    --fg-input-para: #6f0;
    --col-gray-3: #333;
    --col-gray-6: #666;
    --col-gray-9: #999;
    --col-gray-c: #ccc;
    --col-gray-e: #eee;
    --col-gray-dk: #151413;
    --col-gray-md: #252423;
    --col-gray-ml: #555453;
    --col-gray-lt: #959493;
    --col-laven-lt: #FFF;
    --col-laven-ml: #E6E3F1;
    --col-laven-md: #CEC7E3;
    --col-laven-dk: #B5ABD4;
    --col-purp-dk: #091520;
    --col-purp-md: #3A2F5B;
    --col-purp-ml: #6B58AA;
    --col-purp-lt: #9c90c6;
    --col-plumb-dk: #0F151A;
    --col-plumb-md: #352F45;
    --col-plumb-ml: #625881;
    --col-plumb-lt: #9690ba;
    --col-cold-dk: #0F151A;
    --col-cold-md: #313441;
    --col-cold-ml: #535467;
    --col-cold-lt: #75738E;
    --col-denim-dk: #113355;
    --col-denim-md: #476C87;
    --col-denim-ml: #7CA4B8;
    --col-denim-lt: #B2DDEA;
    --col-ocean-dk: #2A5554;
    --col-ocean-md: #2E8684;
    --col-ocean-ml: #33B7B4;
    --col-ocean-lt: #38E8E5;
    --col-gray-head: #3D3C4C;
    --col-yellow: #EBD388;
    --col-green: #88D388;
}
body,
p,
ol,
ul,
td,
th,
.readable,
.bookTitle,
.authorName,
.quoteText,
button,
input,
label,
select,
td,
textarea {
    font-family: sans-serif !important;
}
body {
    color: var(--col-gray-9);
    font-family: sans-serif !important;
    background: var(--col-gray-md);
}
.userIcon,
.review .imgcol,
.answererUserIcon img,
#aboutAuthor img,
.homePageProfileSize img,
.newsfeedUpdates td.firstcol a img {
    box-shadow: 5px 5px 5px #000000;
}
a.user {
    color: var(--col-denim-ml);
}
.otherEditionsActions a {
    color: var(--col-ocean-md);
}
#description a {
    color: var(--col-gray-6);
}
a.csct {
    color: var(--col-denim-md);
}
div.clearFloats a {
    color: var(--col-gray-lt);
}
div.elementList a {
    color: var(--col-gray-ml);
}
div.challengePic > a > img {
    filter: invert(85%) hue-rotate(180deg);
}
.tabs {
    border-bottom: 1px solid #D8D8D8;
}
.tabs .tab,
.tabs .selectedTab {
    border-image-outset: 0 0 0 0;
    border-image-repeat: stretch stretch;
    border-image-slice: 100% 100% 100% 100%;
    border-image-source: none;
    border-image-width: 1 1 1 1;
    border-left-color: #999;
    border-right-color: #999;
    border-style: solid solid none;
    border-top-color: #999;
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
    border-width: 1px 1px 0;
    xcolor: #382110;
}
.mediumTabs .tab,
.mediumTabs .selectedTab,
.bigTabs .tab,
.bigTabs .selectedTab {
    background-color: var(--col-plumb-md);
    background-image: linear-gradient(var(--col-plumb-md), var(--col-gray-dk));
    border-bottom-color: #f3f3e7;
    color: #999;
}
.newNewsfeed .bigTabs .selectedTab {
    background-color: #8B78CA;
    background-image: none;
    border-bottom-color: #f3f3e7;
}
.newNewsfeed .bigTabs .tab {
    background-color: var(--col-gray-ml);
    color: #999;
}
.newNewsfeed .bigTabs .selectedTab:hover,
.newNewsfeed .bigTabs .Tab:hover,
.newNewsfeed .bigTabs .tab:hover {
    background-color: #9B88DA;
    color: #000;
    transition-delay: 0;
    transition-duration: .1s;
    transition-property: background-color;
    transition-timing-function: ease;
}
.communityQAFooter .likeItContainer a.like_it,
.communityQuestionPermalink a,
.questionText a,
.moreReaderQA a,
a.gr-button,
a#js-kcrPreviewButton {
    background-color: var(--col-cold-md);
    border-color: var(--col-cold-ml) var(--col-cold-dk) var(--col-cold-dk) var(--col-cold-ml);
    border-radius: 5px;
    border-style: solid;
    border-width: 1px;
    box-shadow: 0 1px 1px 0 var(--col-gray-md);
    color: var(--col-gray-9);
}
.communityQAFooter .likeItContainer a.like_it:hover,
.communityQuestionPermalink a:hover,
.questionText a:hover,
.moreReaderQA a:hover,
a.gr-button:hover,
#js-kcrPreviewButton:hover {
    background-color: var(--col-cold-ml);
    border-color: var(--col-cold-lt) var(--col-cold-md) var(--col-cold-md) var(--col-cold-lt);
    border-radius: 5px;
    border-style: solid;
    border-width: 1px;
    color: var(--col-gray-e);
}
th,
#myReview td {
    color: var(--col-ocean-md);
}
h2.brownBackground {
    background-color: var(--col-gray-3);
    background-image: none;
    border-top: 1px solid var(--col-denim-ml);
    border-left: 1px solid var(--col-denim-md);
    border-right: 1px solid var(--col-denim-dk);
    border-bottom: 1px solid var(--col-denim-dk);
    xborder-bottom-left-radius: 5px;
    border-top-left-radius: 10px;
    border-top-right-radius: 10px;
    padding-left: 10px;
}
h2.brownBackground a,
.h2Container h2,
div.h2Container.gradientHeaderContainer > h2 {
    color: var(--col-denim-ml);
    padding-left: 10px;
}
td > div.updateAction {
    color: var(--col-gray-lt);
}
a.updateUser.csct,
div.updateAction > a.csct {
    color: var(--col-laven-md);
}
strong > a.csct,
a.authorName.csct,
a.authorTitle.csct {
    color: var(--col-purp-ml);
}
div.title > a.csct,
a.bookTitle.csct,
a.authorFollowTitle.csct,
a.bookInspirationJustification.csct,
td > a.csct {
    color: var(--col-denim-md);
}
span.by.smallText {
    color: var(--col-gray-9);
}
div.textRight.stacked.actionLinkLite a {
    color: var(--col-denim-ml);
}
html.desktop .wtrButtonContainer .wtrLeft button.wtrToRead {
    color: white;
    background-color: var(--col-purp-md);
    background-image: none;
    background-size: 100%;
    background-image: -webkit-gradient(linear, 50% 0%, 50% 100%, color-stop(0%, var(--col-purp-ml)), color-stop(100%, var(--col-purp-md)));
    background-image: -moz-linear-gradient(var(--col-purp-ml), var(--col-purp-md));
    background-image: -webkit-linear-gradient(var(--col-purp-ml), var(--col-purp-md));
    background-image: linear-gradient(var(--col-purp-ml), var(--col-purp-md));
    xborder-color: var(--col-purp-lt) #000 var(--col-purp-md) var(--col-purp-lt);
    font-size: inherit;
    -webkit-font-smoothing: antialiased;
    height: 30px;
    xpadding: 5px 0 5px 5px;
}
.wtrButtonContainer .wtrShelfMenu {
    border-top-color: var(--col-plumb-lt);
    border-left-color: var(--col-plumb-lt);
    border-right-color: var(--col-plumb-md);
    border-bottom-color: var(--col-plumb-md);
}
.wtrButtonContainer .wtrShelfMenu {
    background-color: var(--col-cold-lt);
}
.wtrButtonContainer .wtrShelfMenu > .wtrShelfList {
    background-color: var(--col-cold-lt);
}
.wtrButtonContainer .wtrShelfMenu > .wtrShelfList > ul.wtrExclusiveShelves > li {
    background-color: var(--col-cold-lt);
}
.wtrButtonContainer .wtrShelfMenu button.wtrExclusiveShelf,
.wtrButtonContainer .wtrShelfMenu button.wtrAddShelf,
.wtrButtonContainer .wtrShelfMenu label.wtrExclusiveShelf,
.wtrButtonContainer .wtrShelfMenu .wtrNonExclusiveShelves label,
.wtrButtonContainer .wtrShelfMenu .wtrShelfSearchAddShelf button[type="submit"] {
    background-color: var(--col-cold-lt);
}
.wtrButtonContainer .wtrShelfMenu button.wtrExclusiveShelf:hover,
.wtrButtonContainer .wtrShelfMenu button.wtrAddShelf:hover,
.wtrButtonContainer .wtrShelfMenu label.wtrExclusiveShelf:hover,
.wtrButtonContainer .wtrShelfMenu .wtrNonExclusiveShelves label:hover,
.wtrButtonContainer .wtrShelfMenu .wtrShelfSearchAddShelf button[type="submit"]:hover {
    background-color: #fff;
}
.wtrButtonContainer .wtrShelfMenu > .wtrShelfList > ul.wtrExclusiveShelves > li a.wtrExclusiveShelf,
.wtrButtonContainer .wtrShelfMenu > .wtrShelfList > ul.wtrExclusiveShelves > li button.wtrExclusiveShelf {
    color: #000;
}
.wtrButtonContainer .wtrShelfMenu button.wtrExclusiveShelf,
.wtrButtonContainer .wtrShelfMenu button.wtrAddShelf,
.wtrButtonContainer .wtrShelfMenu label.wtrExclusiveShelf,
.wtrButtonContainer .wtrShelfMenu .wtrNonExclusiveShelves label,
.wtrButtonContainer .wtrShelfMenu .wtrShelfSearchAddShelf button[type="submit"] {
    color: #000;
}
html.desktop .wtrButtonContainer .wtrRight.wtrUp,
html.desktop .wtrButtonContainer .wtrRight.wtrDown,
html.desktop .yearChallengeModule .viewChallenge {
    color: white;
    background-color: var(--col-purp-md);
    background-image: none;
    background-size: 100%;
    background-image: -webkit-gradient(linear, 50% 0%, 50% 100%, color-stop(0%, var(--col-purp-ml)), color-stop(100%, var(--col-purp-md)));
    background-image: -moz-linear-gradient(var(--col-purp-ml), var(--col-purp-md));
    background-image: -webkit-linear-gradient(var(--col-purp-ml), var(--col-purp-md));
    background-image: linear-gradient(var(--col-purp-ml), var(--col-purp-md));
    border-color: var(--col-purp-lt) #000 var(--col-purp-md) var(--col-purp-lt);
    border-style: solid;
    border-width: 1px 1px 1px 0;
}
html.tablet .wtrButtonContainer .wtrRight.wtrUp,
html.tablet .wtrButtonContainer .wtrRight.wtrDown,
html.mobile .wtrButtonContainer .wtrRight.wtrUp,
html.mobile .wtrButtonContainer .wtrRight.wtrDown {
    color: white;
    background-color: var(--col-purp-md);
    background-image: none;
    background-size: 100%;
    background-image: -webkit-gradient(linear, 50% 0%, 50% 100%, color-stop(0%, var(--col-purp-ml)), color-stop(100%, var(--col-purp-md)));
    background-image: -moz-linear-gradient(var(--col-purp-ml), var(--col-purp-md));
    background-image: -webkit-linear-gradient(var(--col-purp-ml), var(--col-purp-md));
    background-image: linear-gradient(var(--col-purp-ml), var(--col-purp-md));
    border-color: var(--col-purp-lt) #000 var(--col-purp-md) var(--col-purp-lt);
    border-style: solid;
    border-left-color: var(--col-purp-lt);
    border-width: 1px;
}
html.desktop .wtrButtonContainer .wtrRight.wtrUp:hover,
html.desktop .wtrButtonContainer .wtrRight.wtrDown:hover,
html.desktop .yearChallengeModule .viewChallenge:hover {
    background-image: none;
    background-size: 100%;
    background-image: -webkit-gradient(linear, 50% 0%, 50% 100%, color-stop(0%, var(--col-purp-lt)), color-stop(100%, var(--col-purp-ml)));
    background-image: -moz-linear-gradient(var(--col-purp-lt), var(--col-purp-ml));
    background-image: -webkit-linear-gradient(var(--col-purp-lt), var(--col-purp-ml));
    background-image: linear-gradient(var(--col-purp-lt), var(--col-purp-ml));
    border-color: var(--col-purp-lt) #000 var(--col-purp-md) var(--col-purp-lt);
}
html.tablet .wtrButtonContainer .wtrRight.wtrUp:hover,
html.tablet .wtrButtonContainer .wtrRight.wtrDown:hover,
html.mobile .wtrButtonContainer .wtrRight.wtrUp:hover,
html.mobile .wtrButtonContainer .wtrRight.wtrDown:hover {
    background-image: none;
    background-size: 100%;
    background-image: -webkit-gradient(linear, 50% 0%, 50% 100%, color-stop(0%, var(--col-purp-lt)), color-stop(100%, var(--col-purp-ml)));
    background-image: -moz-linear-gradient(var(--col-purp-lt), var(--col-purp-ml));
    background-image: -webkit-linear-gradient(var(--col-purp-lt), var(--col-purp-ml));
    background-image: linear-gradient(var(--col-purp-lt), var(--col-purp-ml));
    border-color: var(--col-purp-lt) #000 var(--col-purp-md) var(--col-purp-lt);
}
.quoteSearchForm input.text {
    background-color: var(--bg-input-search);
    color: var(--fg-input-search);
    border-top-left-radius: 5px;
    border-top-right-radius: 5px;
    border-bottom-right-radius: 5px;
    border-bottom-left-radius: 5px;
    border-top-width: 1px;
    border-right-width: 1px;
    border-bottom-width: 1px;
    border-left-width: 1px;
    border-top-style: solid;
    border-right-style: solid;
    border-bottom-style: solid;
    border-left-style: solid;
    border-top-color: #385d4a;
    border-left-color: #385d4a;
    border-right-color: #88ad9a;
    border-bottom-color: #88ad9a;
}
div#quoteoftheday {
    background-image: linear-gradient(#000, var(--col-gray-md) 66%);
    padding: 15px;
    border-top-left-radius: 15px;
    border-top-right-radius: 15px;
}
div#quoteoftheday div.stacked.mediumText,
h1.quoteText {
    color: var(--col-yellow);
}
.mediumText {
    color: var(--col-gray-6);
}
div#quoteoftheday div.textRight {
    color: var(--col-gray-lt);
}
.tooltipTriggerText .tooltip,
.tooltipTrigger .tooltip {
    background: var(--col-gray-dk);
    border: 5px solid var(--col-gray-ml);
}
.tooltipTriggerText .tooltip a,
.tooltipTrigger .tooltip a {
    color: var(--col-denim-dk);
}
.tooltip.book-tooltip .paddedRating {
    color: var(--col-gray-ml);
    xcolor: black;
}
.tooltip.book-tooltip .addBookTipDescription {
    color: var(--col-gray-ml);
    xcolor: black;
}
.tooltip.book-tooltip .ratingStars .myRating {
    color: var(--col-gray-ml);
    xcolor: black;
}
.tooltip.book-tooltip .addBookTipPreview .button {
    color: var(--col-gray-ml);
    xcolor: #222222;
}
.tooltipCaret--top-right {
    border-bottom: 10px solid var(--col-gray-ml);
}
.prototip_StemImage {
    filter: invert(80%);
}
.prototip .goodreads .toolbar .title {
    color: var(--col-purp-ml);
}
.prototip canvas {
    visibility: hidden;
}
.prototip_CornerWrapper,
.prototip .borderCenter {
    background-color: var(--col-gray-ml);
}
.prototip .clearfix {
    background-image: none;
    background-color: transparent;
}
.prototip .addbook,
.prototip .content,
.prototip .goodreads .toolbar .title {
    background-color: var(--col-gray-dk);
}
.wtrButtonContainer .wtrPrompt {
    color: var(--col-gray-lt);
    background-color: var(--col-gray-md);
    border-color: var(--col-purp-ml);
    background-image: none;
}
.wtrButtonContainer .wtrPrompt a {
    color: var(--col-ocean-ml);
}
.wtrButtonContainer .wtrPrompt:before {
    border-color: var(--col-purp-ml) transparent;
}
.wtrButtonContainer .wtrPrompt:after {
    border-color: var(--col-purp-ml) transparent;
}
.addbook .myRating {
    color: #999999;
}
li.borderTop.borderRow .prototip_CornerWrapper {
    border-top-left-radius: 15px;
    border-top-right-radius: 15px;
}
li.borderBottom.borderRow .prototip_CornerWrapper {
    border-bottom-right-radius: 15px;
    border-bottom-left-radius: 15px;
}
table#rating_distribution,
table#rating_distribution td,
table#rating_distribution th {
    background-color: black;
    color: var(--col-ocean-lt);
}
.bookMeta.darkGreyText {
    background-color: var(--col-gray-md);
    color: var(--col-ocean-dk);
}
.bookMeta,
.bookMeta td,
.bookMeta th {
    background-color: black;
    color: var(--col-ocean-dk);
}
table#rating_distribution td div {
    background-color: var(--col-ocean-dk) !important;
}
.bookMeta .value,
.keyValue .value {
    color: var(--col-ocean-ml);
}
.user_status_review_lightbox .bookMeta,
#hp_status_editor .bookMeta {
    color: var(--col-ocean-md);
    ;
    background-color: var(--col-gray-md) !important;
}
.user_status_review_lightbox .bookMeta:hover,
#hp_status_editor .bookMeta:hover {
    color: var(--col-ocean-lt);
    ;
    background-color: var(--col-gray-md) !important;
}
h1,
h2,
h3,
h4,
h5,
h6 {
    color: var(--col-denim-ml);
    xcolor: #382110;
    background-color: transparent;
}
#details {
    border-bottom: 1px solid #ddd;
    color: var(--col-gray-c);
    xcolor: #333;
}
#bookAuthors .smallText,
#bookAuthors .authorName {
    color: var(--col-denim-md);
    xcolor: #333;
}
#aboutAuthor h2 a {
    color: var(--col-denim-ml);
    xcolor: black;
}
.miniBookContent > .title {
    background-color: var(--col-gray-md);
}
.fader {
    background: -webkit-gradient(linear, left center, right center, from(rgba(255, 255, 255, 0)), to(var(--col-gray-md)));
    background: -moz-linear-gradient(left, rgba(255, 255, 255, 0), var(--col-gray-md));
}
.bookTitle {
    color: var(--col-denim-md);
    xcolor: #333333;
}
.by {
    color: #000;
}
.authorName {
    color: var(--col-gray-lt);
}
.actionLink {
    color: var(--col-ocean-md);
}
.actionLinkGreyedOut {
    color: var(--col-gray-9);
    xcolor: #999999;
}
.actionLinkLite {
    color: var(--col-denim-ml);
    xcolor: #00635d;
}
.actionLinkLites a {
    color: var(--col-denim-ml);
    xcolor: #00635d;
}
.actionLinkLites a.current {
    color: var(--col-denim-ml);
    xcolor: #999;
}
.tableList th a {
    color: var(--col-gray-ml);
}
.infoBoxRowTitle {
    color: var(--col-yellow);
    xcolor: #382110;
}
.updateActionLinks a {
    color: var(--col-denim-ml);
    xcolor: #00635d;
}
.updateActionLinks a.greyText {
    color: var(--col-gray-9);
    xcolor: #999;
}
.updateActionLinks a.button {
    color: var(--col-denim-ml);
    xcolor: #222;
}
.brown_comment {
    color: var(--col-gray-ml);
    background-color: var(--col-gray-dk);
    xbackground-color: #f4f1ea;
    border-bottom: 1px solid var(--col-gray-ml);
    xborder-bottom: 1px solid #e5e5d6;
}
.placeholder_text,
.xhr_comment_form textarea {
    xcolor: #777777;
    background: var(--bg-input-para);
    color: var(--fg-input-para);
    xborder-style: none;
    border-top-color: #666;
    border-right-color: #333;
    border-bottom-color: #333;
    border-left-color: #666;
}
.readerQAForm .textInput {
    border: 1px solid #777777;
    border-radius: 3px;
}
.readerQAForm .textInput {
    background: var(--bg-input-para);
    color: var(--fg-input-para);
    xborder-style: none;
    border-top-color: #666;
    border-right-color: #333;
    border-bottom-color: #333;
    border-left-color: #666;
}
H1,
H2,
H3,
H4,
H5,
H6 {
    color: var(--col-denim-ml);
    xcolor: #aaa;
}
H1,
.h2Container H2,
.pageHeader h2 {
    color: var(--col-denim-ml);
    xcolor: #382110;
}
#userStatuses a {
    color: var(--col-denim-md);
}
.buttonAsActionLinkSecondary {
    color: var(--col-denim-ml);
    xcolor: #aaaaaa;
    background: none;
    border: none;
}
.actionLinkLites a.button {
    color: var(--col-denim-ml);
    xcolor: #222;
}
.buttonBar {
    color: black;
    xbackground-color: var(--col-purp-lt);
    background-color: var(--col-ocean-ml);
    background-image: none;
    background-size: 100%;
    background-image: -webkit-gradient(linear, 50% 0%, 50% 100%, color-stop(0%, var(--col-purp-lt)), color-stop(100%, var(--col-purp-ml)));
    background-image: -moz-linear-gradient( var(--col-purp-lt), var(--col-laven-dk) 33%, var(--col-purp-md));
    background-image: -webkit-linear-gradient( var(--col-purp-lt), var(--col-laven-dk) 33%, var(--col-purp-md));
    xbackground-image: linear-gradient( var(--col-purp-lt), var(--col-laven-dk) 33%, var(--col-purp-md));
    background-image: linear-gradient( var(--col-ocean-md), var(--col-ocean-ml) 33%, var(--col-cold-dk));
    xborder-color: var(--col-purp-lt) #000 var(--col-purp-md) var(--col-purp-lt);
    border-color: var(--col-ocean-lt) #000 var(--col-ocean-md) var(--col-ocean-lt);
    border-style: solid;
    border-width: 1px 1px 1px 0;
}
.prototip {
    color: var(--col-gray-ml);
    xcolor: #000;
}
.buttonAsActionLinkSecondary {
    color: var(--col-denim-ml);
    xcolor: #aaaaaa;
    background: none;
    border: none;
}
.actionLinkLites a.button {
    color: var(--col-denim-ml);
    xcolor: #222;
}
tr.update .bookTitle,
tr.update .authorTitle {
    color: var(--col-denim-ml);
    xcolor: #333;
}
.yearChallengeBooks .challengeLink .actionLinkLite {
    color: var(--col-denim-ml);
    xcolor: #4F227A;
}
