css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document url-prefix("https://e.mail.ru") {
body {background-color:transparent!important;}
.b-datalist, .b-nav,.b-datalist {
  -webkit-box-shadow: 0 0 0 0px transparent;
  box-shadow: 0 0 0 0px transparent;
  background: rgba(0, 0, 0, .5)!important;
}

.b-datalist__empty__text, .addressbook-patron-v2 .messagelist__empty-message,.b-nav__item__text,.b-nav__link,.b-nav__link__text {
    color: white;
}
.b-nav__item:hover, .b-nav__item_hover {
  background: rgba(0, 0, 0, .5);
}
.b-datalist__item:hover .b-datalist__item__cbx,.b-datalist__item__cbx {background-color: transparent;}
.b-nav__item_active, .b-nav__item_active:hover,.b-datalist__item:hover,.b-datalist__item_selected .b-datalist__item__cbx {
  color: white;
  background-color: rgba(0, 0, 0, .5);
}
.b-nav__item_active .b-nav__item__text {
  color: white;
}
.b-datalist__item__link, .b-datalist__item__link:hover, .b-datalist__item__attachment__link, .b-datalist__item__attachment__link:hover, .b-datalist__item__filename__link, .b-datalist__item__filename__link:hover, .b-datalist__item__download__link, .b-datalist__item__download__link:hover {
  color: white;
}
.b-datalist__item__subj__snippet,.b-datalist__item:hover .b-datalist__item__subj__snippet {
  color: #ddd;
}
.b-datalist__item_selected, .b-datalist__item_selected.b-datalist__item:hover {
  color: white;
  background-color: rgba(0, 0, 0, .5);
}
.b-datalist__item_selected .b-datalist__item__link, .b-datalist__item_selected .b-datalist__item__link:hover, .b-datalist__item_selected .b-datalist__item__filename__link, .b-datalist__item_selected .b-datalist__item__filename__link:hover, .b-datalist__item_selected .b-datalist__item__download__link, .b-datalist__item_selected .b-datalist__item__download__link:hover {
  color: white;
}
.footer__theme-widget {height: 260px;}
.portal-footer_portal-footer, #b-prefoot, #leftcol-banners, #b-sticky_left_banners, .b-datalist__head, .direct_top, .b-datalist__head, #b-sticky_left_banners__wrapper, .b-context, .b-context__row, #b-slot_left_direct {display:none!important;visibility:hidden !important;position:absolute;left:-9999px;overflow:hidden!important;opacity:0!important;}
}
