css:
@-moz-document domain("archlinux.org") {
  body,
  html {
    background: #202020 !important;
    font-size: 1.1rem !important;
    color: #eee !important;
  }
  * {
    color: #aaa !important;
  }
  .add {
    color: #15d9b1 !important;
  }
  .del {
    color: #be2906 !important;
  }
  .hunk {
    color: #2a8be3 !important;
  }
  #p-cactions li,
  #p-cactions li a:link {
    background-color: #444 !important;
    border: none !important;
    top: -1em !important;
  }
  .portlet a:link {
    color: #999 !important;
  }
  .portlet a:visited {
    color: #777 !important;
  }
  .portlet a:hover {
    color: #ccc !important;
  }
  a:link {
    color: #8a0 !important;
    filter: greyscale(100%);
  }
  a:visited {
    color: #980 !important;
  }
  a:hover {
    color: #cd0 !important;
    text-decoration: none !important;
  }
  b {
    color: white !important;
  }
  .editOptions {
    background-color: #222222 !important;
    border: 0px solid #333333 !important;
  }
  div.blockform h2 span,
  div#quickpost h27 span {
    display: none !important;
  }
  div#viewprofile div.box {
    background: #3e3e3e !important;
    border: none !important;
  }
  div#viewprofile div.inform * {
    border-color: #262626 !important;
  }
  div.pun,
  div#punsearch,
  div#punprofile,
  div#punuserlist,
  div#punpost,
  div#quickpost,
  div.postbody,
  p.pagelink *,
  div.pagepost,
  dl#onlinelist,
  div#brdstats,
  div#brdmain,
  div#brdmenu ul li *,
  div#brdmenu,
  div#punindex,
  div#punviewforum,
  div#punviewtopic,
  div.punwrap,
  h4.mw-specialpagesgroup,
  div#footer,
  div#content,
  div#container div#wrapper div#content h2,
  div#content div.daygroup h2,
  div#subs h2,
  div.boxSoftTitle,
  span.boxSoftTitle,
  body p#footer,
  body p table tbody tr td,
  div.pgboxbody form table,
  div.pgboxbody form table tbody tr td,
  div.pgbox table[width*="100%"],
  form div.pgbox table[width*="100%"] tbody tr td[align*="left"],
  form div.pgbox table[width*="100%"] tbody tr td[align*="right"],
  div#maincontent div.pgbox form div.pgboxtitle,
  div#maincontent div.pgbox div.pgboxtitle,
  p.pgbox,
  div.pgbox,
  div#search,
  div#menu,
  div#pm-menu,
  div#dev_nav,
  div#main_nav,
  div#sub_nav,
  div#arch_nav,
  div#pkgsearch,
  div#pkglist-about,
  div.pBody,
  div#brdstats div.box,
  div#brdfooter div.box,
  div.blockform div.box,
  div.blockform h2,
  div#archnavbar {
    background: none !important;
    border: none !important;
  }
  div.blocktable div.box {
    border: none !important;
  }
  div#profile h2,
  div#viewprofile h2,
  div#postreview h2,
  .pun span.email,
  .pun span.website,
  .pun .required strong,
  .pun .blockpost .usercontacts .website,
  .pun .blockpost .usercontacts .email,
  .pun .blockform .bblinks li span,
  div#main_nav li.selected,
  div#main_nav a:hover,
  div#arch_nav li.selected,
  div#arch_nav a:hover {
    background: none !important;
  }
  .pun .blockmenu a:hover,
  .pun .blockmenu a:active,
  .pun .blockmenu a:focus,
  li.isactive a,
  div.box div.inbox div.postleft,
  div.box div.inbox div.postright,
  div.box div.inbox div.postfootleft,
  div.box div.inbox div.postfootright,
  div.box div.inbox div.postfoot,
  div.blockpost div.box div.inbox {
    background: #3e3e3e !important;
  }
  div.infldset,
  fieldset.box,
  div#cgit div.content,
  div#cgit div.content *,
  body div#content div.box,
  div#comments,
  div#taskdetails,
  div.comment-header,
  div#tasklist,
  div#maincontent form div.pgbox div.pgboxtitle,
  div.pgbox table,
  div.pgbox table tbody tr td,
  div.pgbox table tbody tr th,
  div.box thead tr th,
  div#arch-downloads,
  div#intro,
  div#intro h2,
  div#pkg-updates,
  div#news-article,
  div#news-article h2,
  div#news-article-list,
  div#news-article-list h2,
  div#pkglist-search,
  div#pkglist-search h2,
  div#pkglist-results,
  div#pkglist-results h2,
  div#pkgdetails,
  div#pkgdetails h2,
  div#pkgdetails h3,
  div.blockmenu div.box,
  div#profile div.blockmenu div.box *,
  div#globalWrapper div#column-content div#content,
  div#globalWrapper div#column-content div#content h2 {
    background: #151515 !important;
    border: none !important;
  }
  fieldset.box legend,
  table.tabs tbody tr td a,
  a[href*="#related"],
  a[href*="#comments"],
  li#ca-nstab-category a,
  li#ca-nstab-category,
  li#ca-nstab-special,
  li#ca-nstab-special a,
  li#ca-nstab-main,
  li#ca-nstab-main a,
  li#ca-talk a,
  li#ca-talk,
  li#ca-viewsource,
  li#ca-viewsource a,
  li#ca-history a,
  li#ca-history {
    background: #3e3e3e !important;
    border-color: #404040 !important;
    z-index: 3 !important;
  }
  div.blockpost div.box,
  fieldset.box input,
  fieldset.box select,
  fieldset.box button,
  body div#content div.box pre,
  div.noarticletext,
  table tbody tr td[bgcolor*="#99ccff"],
  table tbody tr td[bgcolor*="#99ccff"] h2,
  table tbody tr[bgcolor*="#dddddd"],
  div#bodyContent textarea#wpTextbox1 *,
  form div table#pagenumbers tr td,
  ul#pagehistory li,
  div.codebox,
  code,
  div#bodyContent code,
  div#bodyContent table,
  div#bodyContent table td code,
  div#bodyContent pre,
  table[style*="margin-left: 5px; padding: 5px; border-spacing: 0px 4px; float: right; clear: right; width: 25%;"],
  table[style*="margin-left: 5px; padding: 5px; border-spacing: 0px 4px; float: right; clear: right; width: 25%;"] th,
  div#globalWrapper div#column-content div#content div.toc,
  div#globalWrapper div#column-content div#content div.toc hr,
  div#globalWrapper div#column-content div#content table.toc,
  div#globalWrapper div#column-content div#content table.toc h2,
  div.catlinks,
  .mw-code {
    opacity: 1;
    border: none !important;
    border-radius: 0.3rem;
    color: #ccc !important;
    line-height: 140%;
    background-color: #222222 !important;
    font-size: 100% !important;
  }
  pre {
    border: 1px solid #151515 !important;
  }
  #toc,
  .toc,
  .mw-warning,
  .toccolours {
    background-color: initial !important;
  }
  /**** Forum ****/
  blockquote,
  div.blockpost,
  table#tasklist_table tbody tr td,
  table#tasklist_table thead tbody,
  table.pretty1 {
    border-color: #151515 !important;
  }
  div#punindex div#brdmain h2,
  div#punredirect div#brdmain h2,
  div.postsignature hr {
    background: #282828 !important;
  }
  div#punindex div#brdmain div.box thead,
  div#punindex div#brdmain div.box thead *,
  div#punviewforum div#brdmain div.box thead,
  div#punviewforum div#brdmain div.box thead * {
    background: #262626 !important;
    border-bottom: 1px solid #262626 !important;
  }
  div.box tbody tr td {
    background: #151515!important;
    border-color: #262626 !important;
  }
  div.list,
  div#punredirect div.block div.box {
    background: #3e3e3e !important;
    border: none !important;
  }
  div#punredirect * {
    border: none !important;
  }
  div.pgbox div.pgboxtitle {
    background: #262626 !important;
    border: none !important;
  }
  div#legend * {
    border: none !important;
  }
  /**** Buglist ****/
  div#tasklist {
    background-color: #282828 !important;
    padding: 1rem;
    border: 1px solid #383838 !important;
  }

  /**** Bip - Green ****/
  div.archwiki-template-box-tip {
    background: #32443b !important;
    border: 1px solid #41574f !important;
    border-left: 4px solid #41574f !important;
    color: #ccc !important;
    font-size: 100% !important;
    opacity: 0.8;
  }

  /**** Tip - green ****/
  div.archwiki-template-message {
    background: #450 !important;
    border: 1px solid #890 !important;
    border-left: 4px solid #890 !important;
    color: #bc0 !important;
    opacity: 0.8;
  }

  /**** NOTES - blue ****/
  div.archwiki-template-box-note {
    background: #045 !important;
    border: 1px solid #067 !important;
    border-left: 4px solid #067 !important;
    color: #0cd !important;
    opacity: 0.8;
  }

  /**** Warning - red ****/
  div.archwiki-template-box-warning {
    background: #520 !important;
    border: 1px solid #840 !important;
    border-left: 4px solid #840 !important;
    color: #b80 !important;
    opacity: 0.8;
  }

  /**** AUR/BUGS (severity, outofdate) ****/
  .severity1,
  .severity1 td,
  .severity1 td a {
    background-color: #0d1f3c !important;
    color: #327cf2 !important;
  }
  .severity1:hover {
    background-color: #0e2242 !important;
    border-color: #142c55 !important;
  }
  .severity2,
  .severity2 td,
  .severity2 td a {
    background-color: #122226 !important;
    color: #66c5dd !important;
  }
  .severity2:hover {
    background-color: #1c3339 !important;
    border-color: #1f3b42 !important;
  }
  .severity3,
  .severity3 td,
  .severity3 td a {
    background-color: #202d10 !important;
    color: #99d94b !important;
  }
  .severity3:hover {
    background-color: #273713 !important;
    border-color: #2f4217 !important;
  }
  .severity4,
  .severity4 td,
  .severity4 td a {
    background-color: #422f06 !important;
    color: #e8a411 !important;
  }
  .severity4:hover {
    background-color: #4f3807 !important;
    border-color: #573e08 !important;
  }
  .severity5,
  .severity5 td,
  .severity5 td a {
    background-color: #571a08 !important;
    color: #ec4413 !important;
  }
  .severity5:hover {
    background-color: #681f09 !important;
    border-color: #b7360f !important;
  }


  /***************** INPUT ******************/
  input[type*="text"],
  textarea,
  input.button,
  input[type*="submit"],
  input[type*="reset"],
  input[type*="password"],
  input.password {
    border: 1px solid #222 !important;
    background-color: #333 !important;
    border-radius: 0px !important;
  }
  fieldset {
    border: none !important;
  }
  select {
    border: 1px solid #222 !important;
    background-color: #333 !important;
    border-radius: 0px !important;
  }
  button {
    background: none !important;
    background-image: none !important;
    border-color: #3e3e3e !important;
  }
  div.txtarea label textarea,
  div.txtarea label textarea * {
    background: #262626 !important;
    border: 1px solid #3e3e3e !important;
  }
  input[type="text"],
  input[type="password"],
  textarea {
    background-color: #333333;
    border: 1px solid #444444;
    border-radius: 5px !important;
  }

  /***************** AUR - DETAILS ******************/
  tbody {
    color: #432 !important;
    background-color: #222 !important;
    padding: 0px;
  }
  input,
  #pkgdetails #detailslinks > div {
    padding: 5px;
    margin-bottom: 5px;
    background-color: #282828 !important;
    border: 1px solid #444444 !important;
    border-radius: 0px !important;
  }

  /***************** ICONS ******************/
  div.icon {
    border-color: #3e3e3e #3e3e3e #3e3e3e #262626 !important;
  }
  tr.inew div.icon {
    border-color: #3e3e3e #3e3e3e #3e3e3e #1793d1 !important;
  }
  a[title="Arch Linux (Home)"],
  a[title="Arch Linux (Planet)"],
  div#logo h1 a[title="Arch Linux"] {
    width: 0px !important;
    height: 40px !important;
    padding-left: 190px !important;
    background: url("https://bbs.archlinux.org/style/ArchLinux/archlogo.png") no-repeat !important;
  }
  td.diff-context {
    background-color: #222;
    border-color: #222;
    color: #ccc;
  }
  td.diff-deletedline .diffchange {
    background: none repeat scroll 0% 0% #500 !important;
  }
  td.diff-deletedline {
    background: none repeat scroll 0% 0% #300 !important;
    border: 1px solid #500 !important;
    border-radius: 3px !important;
  }
  td.diff-addedline .diffchange {
    background: none repeat scroll 0% 0% #050 !important;
  }
  td.diff-addedline {
    background: none repeat scroll 0% 0% #030 !important;
    border: 1px solid #050 !important;
    border-radius: 3px !important;
  }
  hr {
    background-color: #222 !important;
  }
  table.wikitable {
    margin: 1em 0px;
    background-color: #222 !important;
    border: 1px solid #111 !important;
    border-collapse: collapse;
    color: #ccc;
  }

  /******************** Template warnings *******************/
  div.archwiki-template-message {
    position: relative;
    overflow: hidden;
    min-height: 4.6em;
    margin-bottom: 1em;
    background-color: #022 !important;
    border: 1px solid #044 !important;
    border-left: 4px solid #044 !important;
    text-align: center;
  }
  div.archwiki-template-message * {
    color: #066 !important;
  }
  td.diff-context {
    background: none repeat scroll 0% 0% #333 !important;
    border: 0px solid #050 !important;
  }
  .mw-headline {
    counter-reset: section-1 1 section-2 section-3 section-4 !important;
  }
  h2 {
    color: #dc6 !important;
    counter-increment: section-1;
    counter-reset: section-2 section-3 section-4;
  }
  h2 span {
    font-size: 150%;
    line-height: 140%;
    color: #dc6 !important;
  }
  h3 {
    color: #ba4 !important;
    counter-increment: section-2;
    counter-reset: section-3 section-4;
  }
  h3 span {
    font-size: 140%;
    color: #ba4 !important;
    line-height: 130%;
  }
  h4 {
    color: #982 !important;
    counter-increment: section-3;
    counter-reset: section-4;
  }
  h4 span {
    font-size: 120%;
    line-height: 110%;
    color: #982 !important;
  }
  h5 {
    color: #760 !important;
    counter-increment: section-4;
  }
  h5 span {
    font-size: 110%;
    line-height: 100%;
    color: #870 !important;
  }
  h6 {
    color: #760 !important;
  }
  h6 span {
    color: #760 !important;
  }
  h2:before {
    content: counter(section-1) ". " !important;
  }
  h3:before {
    content: counter(section-1) "." counter(section-2) " " !important;
  }
  h4:before {
    content: counter(section-1) "." counter(section-2) "." counter(section-3) " " !important;
  }
  h5:before {
    content: counter(section-1) "." counter(section-2) "." counter(section-3) "." counter(section-4) " " !important;
  }
  h2 .conr {
    font-size: 120%;
  }
  .blockpost,
  .blockpost h2 {
    background-color: #333 !important;
    color: #ddd !important;
  }
  #bodyContent #toc a {
    font-size: 120% !important;
  }
  /**** Table of Contents ***/
  #toc,
  .toc,
  .mw-warning,
  .toccolours {
    background-color: #222;
    border: none;
  }
  .tocnumber {
    color: #bbb !important;
    padding-left: 0.5rem;
  }
  .toctext {
    color: #9b0 !important;
    font-size: 120% !important;
    line-height: 140% !important;
  }
  .diff-contentalign-left {
    line-height: 100%;
  }
  .diff-otitle div,
  .comment div {
    color: #ccc !important;
  }
  .diff-deletedline div {
    color: #c00 !important;
  }
  ins.diffchange-inline {
    color: #0c0 !important;
    border-radius: 0px;
    padding: 0px 5px;
  }
  del.diffchange-inline {
    color: #c00 !important;
    border-radius: 0px;
    padding: 5px;
  }
  .diff-addedline div {
    color: #0c0 !important;
  }
  .diff-context div {
    color: #aaa !important;
    font-size: 110% !important;
  }
  #mw-content-text {
    -moz-column-count: 3;
    -webkit-column-count: 3;
    column-count: 3;
    -moz-column-gap: 1.5rem;
    -webkit-column-gap: 1.5rem;
    column-gap: 1.5rem;
    text-align: left;
    padding: 0.1rem;
  }
  /*** Wikitable ****/
  table.wikitable > tr > th,
  table.wikitable > tr > td,
  table.wikitable > * > tr > th,
  table.wikitable > * > tr > td {
    border: 1px solid #222;
    padding: 0px;
  }
  table.wikitable > tr > th,
  table.wikitable > * > tr > th {
    background-color: #444;
    text-align: center;
  }
  /**** Tables ****/
  tbody {
    color: #432 !important;
    background-color: #151515 !important;
    padding: 0.1rem;
  }
  #content {
    font-size: 100%;
  }
  div#brdmain p {
    font-size: 100% !important;
  }
  #p-search {
    position: initial;
    z-index: 3;
  }
  #p-search .pBody {
    padding: initial;
    text-align: center;
  }
  #searchInput {
    width: 20em;
    margin: 0;
    font-size: 100%;
  }
  .portlet > h3 {
    font-size: 120%;
  }
  div#bodyContent pre {
    background-color: #151515 !important;
  }
  dl.roadmap dt {
    border: solid 1px #2b2b2b;
  }
  div.quotebox {
    background: #151515 !important;
    border-color: #3e3e3e !important;
  }
  div.blockpost h2 {
    background: #151515 !important;
    border: initial !important;
  }
  code,
  .article-content pre {
    background: #161616 !important;
    font-size: 90% !important;
  }
  div#comments fieldset {
    background: none;
    border: 1px solid #ccd;
  }
}
