/* Clear the body*/
#mw-page-base,
#mw-head-base,
#footer{
	display:none !important
}

/* Navigation menu */
div#mw-panel {
	/*font-size: inherit;*/
	font-size: larger;
	position: absolute;
	top: 0px;
	padding-top: 0em;
	/*width: 10em;*/
	width: 100%;
	text-align: right;
	left: 0;
}

#mw-head,
#p-logo,
#p-navigation,
#p-participation,
#p-interaction,
#p-tb,
#p-coll-print_export,
#p-wikibase-otherprojects {
