css:
@-moz-document domain("youtube.com")
{
	/*barva nacitani progressu*/
	.html5-load-progress, .ytp-load-progress {
   background: #8AA0B8 !important;
}
	/*barva play progressu */
	.ytp-play-progress{
	background:#0099FF !important;	
	/*vice barev
  background: linear-gradient(to right, #191970 0%,#006400 50%,#8b0000 100%)*/
}
	/*barva volume */
  .ytp-volume-slider-handle:before{
	background:#0099FF !important;
}
	/*pozadí volume */
	.ytp-volume-slider-handle:after{
	background:#4B4B4B !important;
}
	/* barva play buttonu (tečka)*/
	.ytp-scrubber-button, .html5-scrubber-button:hover, .ytp-scrubber-button:hover {
   background: #FFFFFF !important;
   border-color: #66CCFF !important;
   box-shadow: 0px 0px 6px #0066FF, 0px 0px 8px #00CCFF, 0px 0px 10px #00CCFF !important;
}	
	/*play buttonu (tečka) HOVER */
  .html5-scrubber-button:hover, .ytp-scrubber-button:hover{
   background: #0066FF !important;
   border-color: #1B1B1B !important;
   box-shadow: 0px 0px 6px #0066FF, 0px 0px 8px #00CCFF, 0px 0px 10px #00CCFF !important;
}	
	/*barva buttonů*/
  /* .ytp-playlist-menu-button-icon .ytp-svg-fill,.ytp-chrome-top .ytp-button:not(.ytp-playlist-menu-button) .ytp-svg-fill,.ytp-chrome-controls .ytp-button .ytp-svg-fill{
	/*	fill:#00CCFF
}	
	/*barva HD(720p..), pokud je dostupné*/
   .ytp-settings-button.ytp-hd-quality-badge:after,.ytp-settings-button.ytp-4k-quality-badge:after,.ytp-settings-button.ytp-5k-quality-badge:after,.ytp-settings-button.ytp-8k-quality-badge:after{
	background-color:#0099FF;
}
	/*barva HD(720p..), pokud je dostupné*/
	.ytp-menuitem sup{
	color:red;
}
	/* nabídka, poznámky, titulky atd */
	.ytp-panelpopup{
	background:rgba(0, 52, 131, 0.63);
}
	.ytp-menuitem[aria-checked="true"] .ytp-menuitem-toggle-checkbox{
	background: rgba(0, 227, 254, 0.4) none repeat scroll 0% 0%
}
	/* barva  volume*/
		.ytp-big-mode .ytp-volume-slider-handle::before {
    background: #0099FF none repeat scroll 0% 0%;
}
	/* barva času v preview + tooltip popisů*/		
	.ytp-tooltip.ytp-bottom,.ytp-tooltip{
		color: #00CCFF
} 
	/* barva času*/
	.ytp-time-display, .ytp-time-duration{
	color: #00CCFF
}
}
