css:
@namespace url(http://www.w3.org/1999/xhtml);
/* made by Lakshmanan on userstlyes.org for android.stackexchange.com */

@-moz-document url-prefix("http://android.stackexchange.com/"){
  .container { box-shadow : 0 150px 0 #585858 inset !important }
li.youarehere{ background-color: #585858 !important}
.question-page #content #question-header { border-radius: 20px 20px 20px 20px; }
body{ 
    background-color: #0F0E0D !important ; 
    background-image: url(http://fc05.deviantart.net/fs70/f/2015/030/6/f/_minflat__dark_material_design_wallpaper__4k__by_dakoder-d8fjqzu.jpg) 
}
#footer { background-color: #0F0E0D !important ; }
div#question-header { background-color: #2D9098 !important}
#content{border-radius:24px; background-color:silver;}
.question-page #content{box-shadow:0 1px 5px rgba(0,0,0,0.26),0 65px 0 #585858 inset;}
.answer-hyperlink:hover, .question-hyperlink:hover, .answer-hyperlink:active, .question-hyperlink:active {color: #2D9098}
.tagged-interesting{background-color:rgba(223, 229, 136, 1); border-radius: 0px 0px 18px 18px;}
.answered-accepted{background-color: #627C0F;}
a{ color: #992929 }
blockquote { background-color: #91BE7B }
/* Nav Questions */
  li.youarehere a#nav-questions{ background-color: #277B61 !important; }
  li.youarehere a#nav-questions:hover,li.youarehere a#nav-questions:active{ background-color: #1CAE82 !important; }
/* Nav Tags */
  li.youarehere a#nav-tags{ background-color: #277B61 !important; }
  li.youarehere a#nav-tags:hover, li.youarehere a#nav-tags:active{ background-color: #1CAE82 !important; }
/* Nav Users */
  li.youarehere a#nav-users{ background-color: #277B61 !important; }
  li.youarehere a#nav-users:hover, li.youarehere a#nav-users:active{ background-color: #1CAE82 !important; }
/* Nav Badges */
  li.youarehere a#nav-badges{ background-color: #277B61 !important; }
  li.youarehere a#nav-badges:hover, li.youarehere a#nav-badges:active{ background-color: #1CAE82 !important; }
/* Nav Unanswered */
  li.youarehere a#nav-unanswered{ background-color: #277B61 !important; }
  li.youarehere a#nav-unanswered:hover, li.youarehere a#nav-unanswered:active{ background-color: #1CAE82 !important; }
/* Nav Ask Question */
  li.youarehere a#nav-askquestion{ background-color: #277B61 !important; }
  li.youarehere a#nav-askquestion:hover{ background-color: #1CAE82 !important; }
  li.youarehere a#nav-askquestion:active{ background-color: #1CAE82 !important; }
}
