@namespace url(http://www.w3.org/1999/xhtml);@-moz-document domain("get-tune.eu") {  
  body{background: url(/*[[URL]]*/)!important;
  background-attachment: fixed !important;background-color: #111 !important;	background-clip: border-box !important;
	background-origin: padding-box !important;	background-position: center center !important;	background-repeat: no-repeat !important;	background-size: cover !important}
     *{font-family:/*[[font]]*/!important;color: /*[[color1]]*/ !important; text-shadow: 0 0 2px /*[[shadow1]]*/ !important}       
  a:hover, * a:hover{color: /*[[color2]]*/ !important; text-shadow: 0 0 2px /*[[shadow1]]*/ !important}   
    /**/    
  header#header {background: rgba(0, 0, 0,  .85) url(http://i1164.photobucket.com/albums/q575/Sanya4ever/gif/2_zpsvcejrk3f.gif)!important; background-size: contain !important;background-position: right center !important;background-repeat: no-repeat !important;border-radius:0px 0px 25px 25px!important} 
  ul.playlist > li, .xblock {background-color:transparent!important;border:none!important}
  ul.playlist > li em:before, ul.playlist > li em, #fixplayer:before, #fixplayer:after{background-color:transparent!important;background:transparent!important}
  .xblock {box-shadow: 0px 0px 15px #fff!important;background: rgba(0, 0, 0,  0.35) url(/*[[pl]]*/)!important;background-position: center bottom !important;opacity:.87!important;transition:all 700ms ease-in-out !important} 
  .xblock:hover, .social_knopki:hover, #supersite h3#supersite-title:hover{opacity:1!important;transition:all 700ms ease-in-out !important} 
  .social_knopki, #supersite h3#supersite-title{opacity:.03!important;transition:all 700ms ease-in-out !important}     
  header#header #he-search input[type="text"], header#header #he-search input[type="submit"] {background: rgba(0, 0, 0, 0.4)!important;box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;   -o-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;   -webkit-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;transition:all 500ms ease-in-out !important}
  header#header #he-search input[type="text"]:hover, header#header #he-search input[type="submit"]:hover{background: rgba(0, 191, 255, 0.15)!important;transition:all 700ms ease-in-out !important}
  #he-logo > img{opacity:.0!important}    
  header#header > #header_x1{background: rgba(0, 0, 0,  0.0) url(http://i1164.photobucket.com/albums/q575/Sanya4ever/other%20logo/GetTune_zpsekbui7hd.png)!important; background-size: contain !important;background-position: left center !important;background-repeat: no-repeat !important} 
  #fixplayer{background: linear-gradient(to bottom , rgba(0, 0, 0, 0.95) 3%, rgba(55, 55, 55,  0.91) 100%)!important; background:-webkit-linear-gradient(to bottom , rgba(0, 0, 0, 0.95) 3%, rgba(55, 55, 55,  0.91) 100%)!important;box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;   -o-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;   -webkit-box-shadow: 0 2px 6px rgba(0,0,0,0.5), inset 0 1px rgba(255,255,255,0.3), inset 0 10px rgba(255,255,255,0.2), inset 0 10px 20px rgba(255,255,255,0.25), inset 0 -15px 30px rgba(0,0,0,0.3)!important;padding-left:30px!important;padding-right:30px!important;border-radius:15px  !important}
  #fixplayer-foo{border-radius:9px!important}
  .more_link a {background-color: transparent !important}
  #style_menu {background-color: rgba(0,0,0, .2) !important;border-radius:15px!important}  
  /* ads */
  #banner-header {display:none!important}}
