css:
@-moz-document domain('www.upwork.com'){
  
  body{
    background-color: #3e3d3d;
    color: #e1e3de;
  }
  
  label, legend {
    color: white;
    
  }
  
    
  .oBtn, .alert {
    color: white;
    background-color: #585858;
  }
  
  a.isDisabled {
    color: #e2e3e4;
    background-color: #1c1c1c;
  }
  
  a.oPager, .o-tag-skill {
    color: white;
    background-color: #1c1c1c;
  }
  
  
  
  article.oWidget, article.jsSearchFormFilters, .oTable td, .oModeMessage {
    background-color: #585858;
  }
  
  .air-card-group, .air-card {
    background-color: #585858;
    color: #f0f1f2;
  }
  
  .air-card p {
    background-color: #585858;
    color: #f0f1f2;
  }
  
  .oSupportInfo, .oSegmentationInfo, .o-support-info, .m-0-bottom, .m-md-bottom, .text-muted {
    color: #f0f1f2;
  }
  
  h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6, .oH1, .oH2, .oH3, .oH4, .oH5, .oH6 {
    color: #f0f1f2;
  }
  
  .oMute, .oH2High, .jsContractorRate, .oRateLarge, .oRateTime, .oFieldValue, .oNull, .oHMin, thead.nowrap th, .table > thead > tr > th {
    color: #e2e3e4;
  }
}
