css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

menubar, menubutton, menulist, menu, menuitem, toolbar, tab,
tree, tooltip /*,textbox*/
{
    font-family: "Segoe UI" !important;
    font-size: 11px !important;
}

#main-window #navigator-toolbox #toolbar-menubar
{
	background:none !important;
	padding:2px 0px 2px !important;
}

#main-window #navigator-toolbox #toolbar-menubar>*:first-child
{
	background-color: rgba(0,0,0,.333) !important;
	-moz-border-radius-topright: 7px !important;
	-moz-border-radius-bottomright: 7px !important;
	-moz-outline: 1px solid rgba(0,0,0,.167) !important;
	-moz-outline-radius-topright: 9px !important;
	-moz-outline-radius-bottomright: 9px !important;
}

#throbber-box
{
	background-color: rgba(0,0,0,.333) !important;
	-moz-border-radius: 10px !important;
	width:24px !important;
	padding:3px 0px 2px 0px !important;
	margin:-2px 0px -4px 0px !important;
	overflow:hidden !important;
}

#main-window #navigator-toolbox #nav-bar
{
	padding: 0px 0px 3px 0px !important;
}

#main-window #navigator-toolbox #PersonalToolbar
{
	-moz-appearance: none !important;
	border: none !important;
	color: white !important;
	background: black url("data:image/png;base64,") repeat-x center !important;
}



toolbox {
	-moz-appearance: none !important;
	background-color: black !important;
	color: #F1F1FF !important;
}



toolbar .toolbarbutton-text,
.menubar-text {
	color: #F1F1FF !important;
}

#content .tabs-container {
	-moz-appearance: none !important;
	background-color: black !important;
}



statusbarpanel
{
	-moz-appearance: none !important;
	border: none !important;
	color: white !important;
	background: black url("data:image/png;base64,") repeat-x center !important;
}

/*Tabs*/

.tabbrowser-tabs,
.tabbrowser-tabs .tabs-frame
{
	margin:0px 0px -1px 0px !important;
	background: none transparent !important;
	border: none !important;
}

.tabbrowser-tabs > .tabbrowser-tab
{
	height: 25px !important;
	min-height: 25px !important;
	max-height: 25px !important;
	padding: 0 !important;
	margin: 2px 0px 1px 1px !important;
	border: 1px solid rgba(255,255,255,.5) !important;
	border-bottom:none !important;
	-moz-outline: 1px solid black !important;
	-moz-border-radius-topleft: 6px !important;
	-moz-border-radius-topright: 6px !important;
	-moz-outline-radius-topleft: 7px !important;
	-moz-outline-radius-topright: 7px !important;
}

.tabbrowser-tabs > .tabbrowser-tab:not([selected="true"]):hover
{
	height: 27px !important;
	min-height: 27px !important;
	max-height: 27px !important;
	margin: 0px 0px 1px 1px !important;
}

.tabbrowser-tabs > .tabbrowser-tab[selected="true"]
{
	height: 28px !important;
	min-height: 28px !important;
	max-height: 28px !important;
	margin: 0px 0px 0px 1px !important;
	-moz-border-radius-topleft: 8px !important;
	-moz-border-radius-topright: 8px !important;
	-moz-outline-radius-topleft: 9px !important;
	-moz-outline-radius-topright: 9px !important;
}


toolbarbutton.bookmark-item /*padding der bookmarks*/
{
	padding:0px 2px 0px 2px !important;
}
toolbarbutton.bookmark-item * /*innenabstÃ¤nde der bookmarks*/
{
	margin:0px !important;
}


#urlbar>* /*favicon neben url-leiste zurechtrÃ¼cken*/
{
	margin-left:-3px !important;
}

/*Status-Bar-Icons*/

#full-screen-statusbar-item>* /*fuller-screen-icon anzeigen*/
{
	background-color:rgba(255,255,255,.8) !important;
	border:2px solid transparent !important;
	-moz-border-radius:6px !important;
}

#zotero-status-bar-icon /*zotero-icon zurechtrÃ¼cken*/
{
	margin-top:-1px !important;
}

#ffe-box * /*forecastbar-trennlinien entfernen*/
{
	border:none !important;
}
#ffe-box /*forecastbar-begrenzungen links und rechts weg*/
{
	margin:0px !important;
}
