css:
@namespace url(http://www.w3.org/1999/xhtml);
@namespace x url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);
@-moz-document url-prefix('about:stylish-edit') {
  x|*#main-area {
		background: #222;
		color: #aaa;
	}
  x|button.devtools-toolbarbutton {
		color: inherit;
		border-color: #aaa !important;
	}
  x|*.devtools-textinput {
		background-color: inherit;
		color: inherit;
	}
  x|*.devtools-textinput:not(:hover):not(:focus) {
		border-color: transparent;
	}
}
@-moz-document
  url-prefix("chrome://stylish/content/edit.xul"),
/* so Userstyles sorts it as an editor style */
  url-prefix("about:") { }
/* so Stylish popup lists this style on any about: page */
@-moz-document regexp("data:text/html.*rel='stylesheet'%20href='chrome://browser/content/devtools/codemirror/codemirror.css'.*") {
	html, body, div, span {
		color: #aaa !important;
		font-size: 14px !important;
	}
	.cm-s-mozilla.CodeMirror {
		background-color: #000 !important;
		color: #f00 !important;
		line-height: 1.2em !important;
		font-size: 1em !important;
	}
	.CodeMirror-focused .cm-matchhighlight {
		text-decoration: underline !important;
		text-decoration-color: #0f0 !important;
		text-decoration-style: wavy !important;
	}
	.cm-trailingspace {
		text-decoration: line-through !important;
		text-decoration-color: #f00 !important;
		text-decoration-style: dotted !important;
	}
	.cm-tab {
		text-decoration: line-through !important;
		text-decoration-color: #404040 !important;
		text-decoration-style: dotted !important;
	}
	.cm-s-mozilla .CodeMirror-gutters {
		background-color: #262626 !important;
		border-right: 1px solid #505050 !important;
	}
	.cm-s-mozilla .CodeMirror-gutter-elt div {
		font-size: 1.2em !important;
	}
	.cm-s-mozilla .CodeMirror-guttermarker {  }
	.cm-s-mozilla .CodeMirror-guttermarker-subtle {  }
	.cm-s-mozilla .CodeMirror-linenumber {
		color: #606060 !important;
		padding-left: 0 !important;
	}
	.cm-s-mozilla .CodeMirror-cursor {
		border-left: 1px solid #eee !important;
	}
	.cm-s-mozilla span.cm-comment {
		color: #008000 !important;
	}
	.cm-s-mozilla span.cm-def {
		color: #ffaf40 !important;
		font-weight: bold !important;
	}
	.cm-s-mozilla span.cm-keyword {
		color: #c080ff !important;
		font-weight: bold !important;
	}
	.cm-s-mozilla span.cm-builtin {
		color: #ffaf40 !important;
		font-weight: bold !important;
	}
	.cm-s-mozilla span.cm-variable {
		color: #5967ff !important;
		font-weight: bold !important;
	}
	.cm-s-mozilla span.cm-string {
		color: #ff8000 !important;
	}
	.cm-s-mozilla span.cm-number {
		color: #0f0 !important;
		font-weight: bold !important;
	}
	.cm-s-mozilla span.cm-atom {
		color: #bf4c4c !important;
		font-weight: bold !important;
	}
	.cm-s-mozilla span.cm-variable-2 {
		color: #007f7f !important;
		font-weight: bold !important;
	}
	.cm-s-mozilla span.cm-variable-3 {
		color: #c080ff !important;
		font-weight: bold !important;
	}
	.cm-s-mozilla span.cm-property {
		color: #999 !important;
		font-weight: bold !important;
	}
	.cm-s-mozilla span.cm-property.cm-error {
		color: #60bfbf !important;
		font-weight: bold !important;
	}
	.cm-s-mozilla span.cm-operator {
		color: #fff !important;
	}
	.cm-s-mozilla span.cm-meta {
		color: #0f0 !important;
	}
	.cm-s-mozilla span.cm-qualifier {
		color: #fff700 !important;
		font-weight: bold !important;
	}
	.cm-s-mozilla span.cm-bracket {
		color: #cc7 !important;
	}
	.cm-s-mozilla span.cm-tag {
		color: #ff0 !important;
		font-weight: bold !important;
	}
	.cm-s-mozilla span.cm-attribute {
		color: #c080ff !important;
		font-weight: bold !important;
	}
	.cm-s-mozilla span.cm-error {
		color: #ff0 !important;
		font-weight: bold !important;
	}
	.cm-s-mozilla .CodeMirror-selected {
		background-color: rgba(255, 0, 0, 0.25) !important;
	}
	.cm-s-mozilla span.cm-compilation {
		background-color: rgba(255, 255, 255, 0.12) !important;
	}
	.cm-s-mozilla .CodeMirror-activeline-background {
		background-color: rgba(0, 255, 0, 0.15) !important;
	}
	/* Default styles for common addons */
	div.CodeMirror span.CodeMirror-matchingbracket {
		color: #0f0 !important;
		font-weight: bold !important;
	}
	div.CodeMirror span.CodeMirror-nonmatchingbracket {
		color: #f00 !important;
		font-weight: bold !important;
	}
	.CodeMirror-matchingtag {
		background-color: rgba(150, 255, 0, .3) !important;
	}
	/* Scrollbars */
	.CodeMirror-vscrollbar {
		background-color: #262626 !important;
	}
	.CodeMirror-vscrollbar, .CodeMirror-vscrollbar div {
		min-width: 10px !important;
		width: 10px !important;
	}
	.CodeMirror-vscrollbar div {
		background-color: rgba(0, 0, 0, 0) !important;
		border: 1px solid #404040 !important;
		border-top: 1px solid #404040 !important;
		border-bottom: 1px solid #404040 !important;
		border-radius: 5px !important;
	}
}
