css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("roosterteeth.com"), 
               domain("www.roosterteeth.com"), 
               domain("www.redvsblue.com"), 
               domain("redvsblue.com"), 
               domain("achievementhunter.com"), 
               domain("theknow.tv") {
  body {
    background-color: #202020;
  }
                
  .header .main.menu a:not(:hover) {
    color: whitesmoke!important;
  }
                 
  .left-sidebar a:not(:hover) {
    color:#19578B!important;
  }
  
  .left-sidebar a:hover {
    text-decoration:underline!important;
    color:blue!important;
  }

  #body-block, #hero-block, .ac-container > .accordion, #body-block > main > div.col-lg-12, 
  #body-block > main > div.col-lg-12 > div.row > h1 {
    background: #202020 none repeat scroll 0% 0%;
  }
                 
  body#show-index #body-block main.row:first-of-type {
    border-top: 50px solid #202020;
  }
  
  body#show #body-block .media-content .share-button {
    float:unset!important;
  }
                 
  #body-block > main, #hero-block > .hero-home {
    background: #202020 none repeat scroll 0% 0%;
    padding: 10px;
  }
  
  .media-content, .post-tag-label {
    padding-right:10px;
  }

  a.edit-button {
    color:transparent!important;
  }

  a.edit-button:not(:hover) {
    color: #9f9f9f !important;
  }

  a.edit-button:hover {
    color: transparent !important;
  }
  
  .topics-list {
    padding-top:15px !important;
  }
  
  a.button-primary {
    box-shadow:none!important;
  }
  
  .megamenu, .minimenu > ul {
    background-color:#202020!important;
    border: thin solid white;
  }
  
  .minimenu:not(#notification-menu) > ul {
    border: unset !important;
  }
  
  .megamenu p, .minimenu > ul p {
    color:whitesmoke!important;
  }
  
  .megamenu a, .minimenu > ul a {
    color:snow!important;
  }

  nav .social-icons a {
    color: whitesmoke !important;
  }
  
  .social-bg, .post-reply, .post-quote, .reaction, .arrow, .share-button, 
  .share-button-list, .share-button > a, .share-button-list > li:not(:hover), 
  .button, .media-list-sm-bg > li:not(:hover), a.edit-button:not(:hover) {
    background: #9f9f9f none repeat scroll 0% 0% !important;
    border: 1px white !important;
    /*box-shadow: 0px 1px 2px 0px #9f9f9f!important;*/
  }
  
  .redactor-editor {
    color: darkgray!important;
  }
  
  input[type="submit"] {
    box-shadow: 0px 1px 2px 0px #9f9f9f !important;
  }
  
  .media-list-sm-bg > li {
    background: #9f9f9f none repeat scroll 0% 0% !important;
    color: whitesmoke!important;
  }
  
  .media-list-sm-bg > li > a:not(:hover) p.media-header *, .media-list-sm-bg > li:not(:hover) > a p.media-header {
    transition-property: color, opacity;
    transition-duration: 0.2s;
    transition-timing-function: ease-in-out;
    color: whitesmoke !important;
  }
  
  .media-list-sm-bg > li:hover > a p.media-header *, .media-list-sm-bg > li:hover > a p.media-header {
    transition-property: color, opacity;
    transition-duration: 0.2s;
    transition-timing-function: ease-in-out;
    color: #c30000 !important;
  }
  
  form.modcontrols > select {
    background: #9f9f9f url("http://roosterteeth.com/images/interface-assets/droplist-triangles.png") no-repeat scroll 90% center !important;
    color: whitesmoke !important;
  }
  
  .post-body, h1, h2, h3, h4, .nav-strip-links > li.active, .post-stamp, .media-content {
    color: whitesmoke;
  }
  
  *:not(.topics--featured) p.media-header > span {
    color: whitesmoke !important;
  }
  
  .media-content a:not(:hover) {
    color: lightblue;
  }
  
  p.post-body {
    border-style: solid !important;
    border-width: 5px !important;
    border-color: black !important;
  }
  
  .left-sidebar {
    background: #808080 !important;
  }
                 
  .comment-input {
    background:unset!important;
    border-bottom: 3px solid #9E1D22;
    margin-bottom:10px;
  }
  
  .feed-list, .topics-list {
    background: #202020 !important;
    padding-left: 10px;
    padding-right: 10px;
  }
                 
  #tab-content-activity > .droplist + a + .feed-list {
  }
                 
  .featured-updates > .feed-list, .featured-updates > .feed-list > li  {
    background-color:unset !important;
    box-shadow: unset!important;
  }
  
  .profile-header > .media-header > a, h1.content-title, .accordion > label > h3, .feed-list > li > .media-content > .media-header > a {
    color: whitesmoke !important;
  }
  

  
  #tab-content-activity, div[id="tab-content-about me"], .about-me-copy, .accordion, #tab-content-comments, #tab-content-images  {
    background: #F9F9F9;
  }
  .accordion .title {
    color:black;
  }
  .content-title, #tab-content-images form + h3 {
    color: black;
    padding-left: 15px;
  }
  .comments-list, .image-blocks {
    background: #202020 none repeat scroll 0% 0% !important;
    padding-left:10px;
  }
  .image-blocks button {
    background: #F9F9F9 !important;
    box-shadow: 0px 1px 2px 0px #DDD !important;
  }
  .image-blocks button:hover {
    background: #818181 !important;
    box-shadow: 0px 1px 2px 0px #DDD !important;
  }
  
  div.forum-content > h1.content-title, .trendline-data, div.profile-img + div.media-content > p.media-header > span {
    color: whitesmoke !important;
  }
  
  .details {
    color: black !important;
  }
  
  #sf-resetcontent.sf-reset > * {
    color: black !important;
  }
                 
  .topics--featured div.box-container {
    background-color: inherit!important;
    box-shadow: none !important;
  }
                 
  .forum-content > h1.content-title {
    padding-top: 10px;
  }
                 
  .topics--following > a.button:first-child {
    float:right!important;
  }
                 
  .tabs > li [id^="tab"] + label {
    border-top-left-radius: 3px;
    border-top-right-radius: 3px;
  }
                 
  .tabs > li [id^="tab"]:checked + label {
    background-color: #7A7A7A !important;
    border: unset !important;
    border-top-left-radius: 3px;
    border-top-right-radius: 3px;
  }
                 
  .tabs > li h3.content-title {
    background-color: #7A7A7A !important;
  }
                 
  .tabs > li .tab-content {
    background-color: #7A7A7A !important;
    border-top: 1px solid #7a7a7a !important;
    padding-left:10px;
    padding-right:10px;
  }
                 
  .tabs > li .tab-content ul {
    background-color: #7A7A7A !important;
  }
                 
  .tabs > li .tab-content .delete-button:not(:hover)::before {
    color:#5D1618!important;
  }
                 
  .tabs > li .tab-content .delete-button:hover::before {
    color: white!important;
  }
                 
  .tabs > li .tab-content .delete-button:not(:hover) {
    background-color:white!important;
  }
                 
  .tabs > li .tab-content .delete-button:hover {
    background-color: #5D1618!important;
  }
                 
  .about-me-copy + section.ac-container > section.accordion {
    background-color: #7A7A7A !important;
  }
                 
  .about-me-copy + section.ac-container > section.accordion a:not(:hover){
    color: #19578B !important;
  }
                 
  .about-me-copy + section.ac-container > section.accordion a:hover {
    text-decoration:underline!important;
    color:blue!important;
  }
                 
  .shows--following p {
    color:whitesmoke !important;
  }
                 
  .right-sidebar, .episodes--all-time-favs {
    background-color:#808080!important;
    padding: 10px;
  }
                 
  .comment-input #redactor-toolbar-0 li {
    background-color:whitesmoke;
  }
                 
  .new-image-form {
    background-color:whitesmoke!important;
    box-shadow: 2px 2px 2px 2px rgba(0.2, 0.2, 0.2, 0.2);
    margin-bottom: 15px;
    padding: 5px;
  }
                 
  #picture-previews {
    background-color:lightgray!important;
  }
                 
  .new-image-form input {
    box-shadow:unset!important;
    border:1px black!important;
  }
                 
  #picture-previews {
    padding:5px!important;
  }
                 
  .ac-container section.accordion:not(:last-child)  {
    border-bottom: medium dashed green;
  }
                 
  .post-breadcrumb, .post-breadcrumb > a:not(:hover) {
    color:whitesmoke!important;
  }
                 
 .post-breadcrumb > a:hover {
    color:#9E1D22!important;
  }
                 
  .social-controls + form {
    float:left;
  }
                 
  .social-controls + form + .edit-button {
    margin-bottom:10px!important;
  }
                 
  .list-group-item:not(:hover):not(:focus) {
    background-color: unset;
    color:whitesmoke!important;
  }
}
