css:
@-moz-document domain("kissanime.to") {
html,
body {
    color: #393939 !important;
    background-color: RGBA(255, 255, 255, 0);
}
#head h1 a.logo {
    display: block;
    width: 460px;
    height: 120px;
    border: none;
}
#head h1 {
    background: transparent url(http://i.imgur.com/bXAzxMB.png) no-repeat;
}
#adsIfrme div div div:nth-child(11) {
    background: rgba(0, 0, 0, 0) !important;
    border-color: #D7D7D7 !important;
}
#divComments > div > div:nth-child(2) {
    color: #000000 !important;
}
.listing tr.odd {
    background: none repeat scroll 0 0 #E6E6E6;
}
.video-js {
    background-color: #F9F9F9;
    position: relative;
    padding: 0;
    font-size: 10px;
    font-weight: 400;
    font-style: normal;
    font-family: Arial, sans-serif;
    -webkit-user-select: none;
}
.vjs-default-skin .vjs-play-progress {
    background: #54D770 -50% 0 repeat;
}
.vjs-default-skin .vjs-slider {
    outline: 0;
    position: relative;
    cursor: pointer;
    padding: 0;
    background-color: rgba(180, 180, 180, 1);
}
.vjs-default-skin {
    color: #FFF;
}
.specialButton {
    box-shadow: inset 0px 1px 0px 0px #c1ed9c;
    background: -webkit-gradient(linear, left top, left bottom, color-stop(0.05, #42AA58), color-stop(1, #75CE88));
    background-color: #9dce2c;
    border-top-left-radius: 0px;
    border-top-right-radius: 0px;
    border-bottom-right-radius: 0px;
    border-bottom-left-radius: 0px;
    text-indent: 0;
    border: 1px solid #D7D7D7;
    display: inline-block;
    color: #ffffff;
    font-family: Arial;
    font-size: 15px;
    font-weight: bold;
    font-style: normal;
    height: 49px;
    line-height: 49px;
    width: 200px;
    text-decoration: none;
    text-align: center;
    text-shadow: 1px 1px 0px #000000;
}
#divComments {
    background-color: #F9F9F9 !important;
    border-color: #D7D7D7 !important;
}
.vjs-default-skin .vjs-control-bar {
    display: none;
    position: absolute;
    bottom: 0px;
    left: 0;
    right: 0;
    height: 3.3em;
    width: 100.1%;
    background-color: rgba(75, 75, 75, 0.91) !important;
}
.tooltip {
    display: none;
    background: #F9F9F9 !important;
    height: 180px;
    padding: 25px 10px 0px 30px !important;
    width: 450px;
    font-size: 11px;
    color: #636363;
}
#aDropDown span small {
    color: #fff !important;
}
#rightside {
    padding: 36px 0px 0px 0px;
    width: 220px;
    text-align: left;
    float: right;
}
div.pagination ul.pager li a:hover,
div.pagination ul.pager li a:active {
    background: #F4F4F4 !important;
    margin: -1px;
    border-top: 1px solid #cccccc!important;
    border-left: 1px solid #bebebe!important;
    border-right: 1px solid #b1b1b1 !important;
    border-bottom: 1px solid #afafaf !important;
}
div.pagination ul.pager li.current {
    padding: 4px;
    background: #E6E6E6;
    border-top: 1px solid #D7D7D7 !important;
    border-left: 1px solid #bebebe !important;
    border-right: 1px solid #b1b1b1 !important;
    border-bottom: 1px solid #afafaf !important;
    color: #515151;
}
.listing tr:hover td {
    background: none repeat scroll 0 0 #C6C6C6;
}
div.items img:hover {
    border: 2px solid #25FF9E;
}
div.items img {
    border: 2px solid #F9F9F9;
    height: 150px !important;
    width: 125px !important;
    transition: border 0.5s;
}
#containerRoot > div:nth-child(8) {
    display: none;
}
.arrow-general {
    margin: 0px;
    padding: 0px;
    height: 0px;
    background: RGBA(0, 0, 0, 0) !important;
    top: -12px;
    left: -10px;
}
#leftside > div:nth-child(12) {
    display: none !important;
}
div.items a {
    display: block !important;
    float: left !important;
    margin-right: 10px !important;
    width: 135px !important;
    height: 180px !important;
    text-decoration: none !important;
    text-align: left !important;
    font-size: 10px !important;
    cursor: pointer !important;
}
.banner {
    margin: 0 0 10px 0 !important;
    padding: 0px !important;
    width: 100% !important;
    background: #F9F9F9 !important;
    border: 1px solid #D7D7D7 !important;
    height: 208px !important;
}
.barTitle {
    height: 25px !important;
    padding-left: 10px !important;
    background: #62ba4a !important;
    font: normal 17px/23px "Tahoma", Arial, Helvetica, sans-serif;
    letter-spacing: -1px;
    color: #fff !important;
}
#leftside div:nth-child(7) div.barContent.episodeList div:nth-child(2) div:nth-child(1) {
    color: #62BB5F !important;
}
#navsubbar p a {
    max-width: 64rem;
    padding: 2rem 6rem;
    margin: 0 auto;
    color: #000;
}
#navsubbar p {
    margin: 0px auto;
    padding: 4px 0;
    position: relative;
    top: 27px;
    text-indent: -720px;
    width: 100%;
    background: rgba(0, 0, 0, 0.07);
    color: #000;
    text-align: left;
}
#adsIfrme > div > div > div:nth-child(9) {
    background: rgba(0, 0, 0, 0) !important;
    border-color: #D7D7D7 !important;
}
#adsIfrme > div > div > div:nth-child(7) {
    background: rgba(0, 0, 0, 0) !important;
    border-color: #D7D7D7 !important;
}
#headnav {
    margin: 9px 0px 0px 0px;
    padding: 0px;
    text-align: left;
    width: 108%;
}
#navbar {
    width: 68%;
    background: #161616 !important;
    height: 0px;
}
#navcontainer a {
    position: relative;
    bottom: 23px;
    display: block;
    width: 103px;
    height: 26px;
    margin-right: 2px;
    transition: background-color 0.5s;
}
#navsubbar {
    width: 100%;
    background: #62BA4A;
    height: 27px;
    line-height: 20px;
}
#tab-newest div:nth-child(-n+10) div:nth-child(5) {
    display: none;
}
#tab-trending div:nth-child(-n+10) div:nth-child(5) {
    display: none;
}
#tab-mostview div:nth-child(-n+10) div:nth-child(5) {
    display: none;
}
#navcontainer ul {
    margin: 0px 0px -10px 51px;
    padding: 0;
    list-style-type: none;
    text-align: center;
    font: 12px/27px Arial, Helvetica, sans-serif;
}
#navcontainer li a#currentTab {
    background: #c6c6c6 !important;
    background-position: 0% -27px;
    color: #000000;
}
#navcontainer a:link,
#navcontainer a:visited {
    background: none;
    background-color: #62ba4a !important;
}
#formSearch > div:nth-child(4) {
    width: 407px;
    height: 20px;
}
#navcontainer a:hover {
    background: none;
    background-color: #c6c6c6 !important;
}
#tabmenucontainer li a {
    transition: background-color 0.5s;
}
#tabmenucontainer li a.tabactive {
    color: #000000;
    background: none;
    background-color: #62ba4a !important;
}
#tabmenucontainer a:link,
#tabmenucontainer a:visited {
    color: #000000;
    background: none;
    background-color: #c6c6c6 !important;
}
#tabmenucontainer a:hover {
    color: #000000;
    background: none;
    background-color: #9a9a9b !important;
}
.rightBox {
    display: block;
    width: 213px;
    padding: 0px;
    background: #F9F9F9 !important;
    border: 1px solid #D7D7D7 !important;
}
.alphabet {
    background: #F9F9F9 !important;
    font-size: 12px!important;
}
input.text {
    margin: 0 0 0 0;
    border: 1px solid #E0DDDD;
    background: #F9F9F9;
    font: normal 15px "Tahoma", Arial, Helvetica, sans-serif;
    color: #191919;
}
#result_box {
    position: absolute;
    top: 29px;
    left: 0px;
    width: 250px;
    padding: 5px;
    color: #2C2C2C;
    background: #F9F9F9;
    opacity: 0.9;
    filter: alpha(opacity=90);
    font-family: Tahoma, Verdana, Arial;
    font-size: 13px;
    -webkit-border-bottom-right-radius: 6px;
    -webkit-border-bottom-left-radius: 6px;
    -moz-border-radius-bottomright: 6px;
    -moz-border-radius-bottomleft: 6px;
    border-bottom-right-radius: 6px;
    border-bottom-left-radius: 6px;
    z-index: 99;
    text-align: left;
}
#result_box a {
    color: #000;
    display: inline-block;
    width: 100%;
    line-height: 16px;
    text-decoration: none;
}
.txtSignUp {
    margin: 0 0 10px 0;
    padding: 2px;
    border: 1px solid #D7D7D7;
    background: #F9F9F9;
    font: normal 12px "Tahoma", Arial, Helvetica, sans-serif;
    color: #ccc;
    width: 220px;
    height: 15px;
}
select {
    background-color: #F9F9F9;
    color: #000;
    border: 1px solid #D7D7D7;
    font: normal 15px "Tahoma", Arial, Helvetica, sans-serif;
}
#result_box a:hover {
    color: #000;
    display: inline-block;
    width: 100%;
    line-height: 16px;
    background: #54D770;
    text-decoration: none;
}
}

@-moz-document domain("kissanime.to") {
#leftside div:nth-child(7) div.barContent.episodeList div:nth-child(2) div:nth-child(1) {
    color: #000000
}
.specialButton {
    -moz-box-shadow: inset 0px 1px 0px 0px #c1ed9c;
    -webkit-box-shadow: inset 0px 1px 0px 0px #c1ed9c;
    box-shadow: inset 0px 1px 0px 0px #c1ed9c;
    background: #62BA4A;
    background-color: #62BA4A;
    -webkit-border-top-left-radius: 20px;
    -moz-border-radius-topleft: 20px;
    border-top-left-radius: 0px;
    -webkit-border-top-right-radius: 20px;
    -moz-border-radius-topright: 20px;
    border-top-right-radius: 0px;
    -webkit-border-bottom-right-radius: 20px;
    -moz-border-radius-bottomright: 20px;
    border-bottom-right-radius: 0px;
    -webkit-border-bottom-left-radius: 20px;
    -moz-border-radius-bottomleft: 20px;
    border-bottom-left-radius: 0px;
    text-indent: 0;
    border: 0px solid #83c41a;
    display: inline-block;
    color: #ffffff;
    font-family: sans-serif;
    font-size: 15px;
    font-weight: bold;
    font-style: normal;
    height: 40px;
    line-height: 41px;
    width: 200px;
    text-decoration: none;
    text-align: center;
    text-shadow: 1px 1px 0px #000000;
    transition: background 0.5s, background-color 0.5s;
}
.specialButton:hover {
    background: #E3E3E3;
}
.bigBarContainer {
    display: block;
    width: 100%;
    background: #F9F9F9;
    border: 1px solid #D7D7D7;
    margin-left: auto;
    margin-right: auto;
    margin-top: 5px;
    padding: 0px;
}
a {
    color: #62BA4A;
    text-decoration: none;
    outline: medium none;
}
#topHolderBox {
    color: #626262;
    background: #FFFFFF !important;
    font-family: Tahoma, Verdana, Arial;
    font: normal 13px Arial;
    font-size: 16px;
    border-top-right-radius: 6px;
    border-top-left-radius: 6px;
    border-bottom-right-radius: 6px;
    border-bottom-left-radius: 6px;
    z-index: 99;
    display: inline-block;
    text-align: center;
    padding-top: 8px;
}
.scrollable {
    position: relative;
    overflow: hidden;
    width: 100%;
    height: 230px;
}
a.next {
    background: url(http://i.imgur.com/KRSHaSE.png);
    float: left;
    transition: background 0.5s;
}
a.prev {
    background: url(http://i.imgur.com/DfKFcsT.png);
    float: left;
    transition: background 0.5s;
}
a.next:hover {
    background: url(http://i.imgur.com/UV1Df47.gif);
}
.barContent {
    padding: 10px;
    margin-top: 2px;
    background: none;
}
a.prev:hover {
    background: url(http://i.imgur.com/SP89zYR.gif);
}
#footcontainer p {
    margin: 0 10px;
    padding: 0px;
    color: #FFFFFF;
}
.boxDropDown {
    border: none;
    border-top-right-radius: 0px;
    border-top-left-radius: 0px;
    border-bottom-right-radius: 0px;
    border-bottom-left-radius: 0px;
    background: #54D770;
    display: inline-block;
    width: auto;
    height: 20px;
    font-size: 12px;
    font-weight: bold;
    color: #FBFBFB;
    text-align: center;
    padding-top: 2px;
}
#subcontent {
    margin: 15px 0 0 0;
    padding: 0px;
    border: 1px solid #D7D7D7;
    width: 100%;
    float: left;
}
#subcontent div div {
    margin: 0px;
    padding: 10px;
    float: left;
    display: block;
    width: 710px;
    background: #F9F9F9;
    font: normal 12px "Tahoma", Arial, Helvetica, sans-serif;
}
#subcontent div div.blue {
    margin: 0;
    padding: 10px;
    float: left;
    display: block;
    background: #EBEBEB;
    width: 710px;
    font: normal 12px "Tahoma", Arial, Helvetica, sans-serif;
}
#footer {
    padding-top: 10px;
    background: #EBEBEB;
    clear: both;
    position: relative;
    z-index: 10;
    height: 64px;
}
#footcontainer p {
    margin: 0 10px;
    padding: 0px;
    color: #000000;
}
#footcontainer p a {
    margin: 0px;
    border: none;
    text-decoration: none;
    color: #424AB1;
}
#menu_box {
    position: absolute;
    top: 29px;
    left: 221px;
    width: 175px;
    color: #2E2E2E;
    background: #54D770;
    font-family: Arial;
    font-size: 12px;
    z-index: 100;
    text-align: left;
    border-top: none;
    border-left: none;
    border-right: none;
}
#leftside {
    margin: 36px 0px 0px 0px;
    padding: 0px;
    width: 730px;
    text-align: left;
    float: left;
}
#menu_box a {
    color: #fff;
    display: inline-block;
    width: 100%;
    line-height: 30px;
    text-decoration: none;
    border-bottom: none;
    transition: background 0.5s;
}
#menu_box a:hover {
    background: #c6c6c6;
}
}
