@-moz-document url-prefix("http://tieba.baidu.com/f?kw="),
url-prefix("http://tieba.baidu.com/f?ie="),
url-prefix("http://tieba.baidu.com/f?fp="),
url-prefix("http://tieba.baidu.com/f?tp="),
