    #side_bar ol li .left_row:hover{ background: #000; }
    
   #page_header .right, #page_header .left, #pageHeader .header_back, #page_header, #pageHeader {
        background: url("http://savepic.net/8375958.png") no-repeat !important;
        width: 100%;
    }  
    
    #mv_more, .pad_msg_lnk, #pv_wide .wall_module .author, .wall_module .mv_comm .author, .wk_likes_liker_lnk{ color: blue; }

    .top_home_logo{ visibility: hidden; }

    #side_bar .left_label, box_title, .page_actions_dd_label, .page_actions_header_inner{ color: #fff; }

    .post_author .author, .wall_module .author, .wall_module .copy_author, .ts_contact_title { color: #4686bd; }

    .button_gray button.hover, .button_gray button, .flat_button.secondary.hover, .flat_button.secondary, .flat_button, #mv_like_wrap {
       color: #fff;
       background-color: #35A2E7;
    }
    
    .online::after, .nim-dialog.nim-dialog_selected:not(.nim-dialog_deleted) .nim-dialog--photo .online::after, .chat_tab_imgcont.online::after { background-color: #0091FF; border: 2px solid #fff; }
    .top_nav_btn:hover, .top_audio_player:hover, .top_nav_link:hover, .button_gray button.hover, .button_gray button:hover, .flat_button.secondary.hover, .flat_button.secondary:hover, .flat_button:hover{ background-color: #277BB0; }
    .box_title_wrap.box_grey, .box_title_wrap { background-color: #35A2E7; }
    .ui_tab_sel, .ui_tab_sel:hover, .ui_tabs .ui_tab_sel, .ui_tabs .ui_tab_sel:hover, .ui_tabs_box .ui_tab_sel, .ui_tabs_box .ui_tab_sel:hover{ border-bottom: 2px solid #000; }
