css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("payback.pl") {
.menu ul li a, .menu-kategorie-nagrod ul li a, .menu-kategorie-nagrod-small ul li a {
    font-family: Arial!important;
    font-size: 0.8rem!important;
    line-height: 1.3rem!important;
}
.items-list .item .cost .value.main {
    font-family: Verdana!important;
    font-size: 1.1rem!important;
    line-height: 1.3rem!important;
}
.main-content .title {
    font-family: "Arial Narrow"!important;
    font-size: 1.5rem!important;
}
#footer.pb-common {
    font: 100 16px  "Arial Narrow"!important;
}
body.new-aff #app {
    font: 100 16px "Arial Narrow";
}
.top-bar .top-bar-section ul li:not(.has-form) a:not(.button) {
    font-family: Arial!important;
    font-size: 0.8rem!important;
}
.top-bar ul.right a {
    font-family: Arial!important;
    font-size: 0.8rem!important;
}
.items-list .item .cost .value {
    font-family: Arial!important;
    font-size: 0.8rem!important;
}
.sub-nav > dd > a {
    font-family: "Arial Narrow"!important;
    font-size: 0.93rem!important;
    line-height: 1rem!important;
}
.menu ul, .menu-kategorie-nagrod ul, .menu-kategorie-nagrod-small ul {
    padding-left: 0.5rem!important;
    margin: 0.5rem 0px!important;
}
#outlet .menu ul li a, #outlet .menu-kategorie-nagrod ul li a, #outlet .menu-kategorie-nagrod-small ul li a {
    font-family:  "Arial Narrow"!important;
    font-size: 0.875rem!important;
    line-height: 1.375rem!important;
    position: relative!important;
}
}
