css:
@-moz-document url-prefix("https://retrospring.net") {
body {
	font-family: "Droid Sans", Arial, "Open Sans", "Times New Roman";
  	line-height: 2;
    background-color: #424242;
    color: #ddd;
    font-size:13px;
}

.j2-page {
    background-color: #303030;
}

.panel-heading {
    background-color: #404040 !important;
    padding: 10px 15px;
    border-bottom: 0px solid transparent;
    border-top-right-radius: -1;
    border-top-left-radius: -1;
}

.profile--displayname {
    color: #9C9C9C;
}

.profile--username {
    color: #7A7A7A;
}

.entry-text {
    color: #9C9C9C;
}

.answerbox .text-muted a,
.answerbox .text-muted a:hover {
    color: #999999;
}

h1,
h2,
h3,
h4,
h5,
h6,
.h1,
.h2,
.h3,
.h4,
.h5,
.h6 {
    color: #ddd;
}

.answerbox--question-text {
    color: #ccc;
}

.answerbox--answer-text {
    color: #eee;
}

.panel-title {
    color: #eee;
    margin-top: 0;
    margin-bottom: 0;
    font-size: 13px;
    color: #ddd;
}

.btn-default {
    color: white;
    background-color: #4F4A4B;
    border-color: #4F4A4A;
}

.modal-content {
    background-color: #171717;
}

.close {
    color: #eee;
}

.dropdown-menu {
    background-color: #262626;
}

.dropdown-menu .divider {
    background-color: #171717;
}

.dropdown-menu>li>a {
    color: #ddd;
}

.notification--dropdown-text {
    color: #ccc;
}

.nav-pills>li.active>a,
.nav-pills>li.active>a:hover,
.nav-pills>li.active>a:focus {
    background-color: #7E57C2;
}

.entry-subtext {
    color: #7E57C2;
}

.profile--header {
    background-color: #673AB7;
}

.sweet-alert {
    background-color: #171717;
}

.sweet-alert h2 {
    color: #eee;
}

.sweet-alert p {
    color: #eee;
}

.sweet-alert button.cancel {
    background-color: #4F4A4A;
}

.form-control {
    border: transparent;
    background-color: transparent;
}

.panel-primary {
    border: transparent;
    border-color: #5777C2;
}

/* Non vanilla code */


.img-rounded:hover,
.img-rounded:focus,
.img-rounded:active {
    -webkit-transform: scale(1.1);
    transform: scale(1.1);
}



.profile--image-avatar.hidden-xs:hover,
.profile--image-avatar.hidden-xs:focus,
.profile--image-avatar.hidden-xs:active {
    -webkit-transform: scale(1.1);
    transform: scale(1.1);
}

.profile--avatar {
    margin-bottom: 0px;
}

.profile--avatar:hover,
.profile--avatar:focus,
.profile--avatar:active {
    -webkit-transform: scale(1.05);
    transform: scale(1);
}

.panel {
    background: transparent;
    box-shadow: 0px 0px 0px rgba(87, 119, 194, 0.99);
}

.input-group-addon {
    padding: 10px 18px;
    font-size: 14px;
    font-weight: normal;
    line-height: 1;
    color: #ddd;
    text-align: center;
    background-color: #505050;
    border: 1px solid #404040;
    border-radius: 0;
}
/* My own code*/

.list-group-item {
    background-color: #4F4A4A;
    border-color: #4F4A4A;
    color: #aaa !important;
}

.panel-body {
    padding: 13px;
    background-color: #404040;
}

.navbar-inverse .navbar-nav>.active>a, .navbar-inverse .navbar-nav>.active>a:hover, .navbar-inverse .navbar-nav>.active>a:focus {
    background-color: #4D68A7;
}

.inbox--panel .panel-heading {
    color: #5777C2;
    border-bottom: 2px solid #5777C2;
    background-color: #fff;
    text-transform: uppercase;
}

.btn-info {
    color: white;
    background-color: #5777C2;
    border-color: #5777C2;
}

.btn-primary {
    color: white;
    background-color: #5777C2;
    border-color: #5777C2;
}

.form-control {
    border: transparent;
    background-color: rgb(72, 72, 72);
    color: white;
}

.modal-content {
    background-color: #404040;
}

label {
    display: inline-flex;
    max-width: 100%;
    margin-bottom: 5px;
    font-weight: bold;
}

.j2-jumbo {
    background-color: #404040;
    color: #fff;
}

.jumbotron .h1 {
    font-size: xx-large;
}

.entry-subtext {
    color: #ddd;
}

.list-group-item.active:focus {
    z-index: 2;
    color: white;
    background-color: #5777C2;
    border-color: #5777C2;
}

.list-group-item {
    color: #ddd !important;
}

.btn-link {
    color: #FFFFFF;
    font-weight: normal;
    cursor: pointer;
    border-radius: 0;
}

.list-group-item.active:focus {
    z-index: 2;
    color: white;
    background-color: #5777C2;
    border-color: #5777C2;
}

a {
    color: #5777C2;
}

.list-group-item {
    border: 0px solid #dddddd;
    color: #FFF !important;
    background-color: #3C3C3C;
    border-color: #3C3C3C;
}

.list-group-item.active, .list-group-item.active:hover, .list-group-item.active:focus {
    z-index: 2;
    color: white;
    background-color: #5777C2;
    border-color: #5777C2;
}

.list-group-item-warning {
    color: white;
    background-color: #505050;
}

.navbar .nav .active .badge, li.dropdown.open a.dropdown-toggle span.badge {
    padding: 3px 6px 3px;
    background-color: #5777C2;
    color: #fff;
}

a.list-group-item:hover, a.list-group-item:focus {
    text-decoration: none;
    color: #555555;
    background-color: #3A4E80;
}

.badge {
    background-color: #5777C2;
}

a.list-group-item.active>.badge, .nav-pills>.active>a>.badge {
    color: #5777C2;
    background-color: white;
}

.notification--icon {
    position: absolute;
    right: 0px;
    bottom: 0px;
    padding: 5px;
    font-size: 36px;
    color: #FFFFFF;
    opacity: 0.4;
    z-index: 0;
}

.panel-badge-default {
    background-color: #423E3E;
}

.panel-default {
    border-color: transparent;
}

button, input, optgroup, select, textarea {
    color: #5777C2;
    font: inherit;
    margin: 0;
}

.btn-success {
    color: white;
    background-color: #5777C2;
    border-color: #5777C2;
}

.btn-danger:hover, .btn-danger:focus, .btn-danger:active, .btn-danger.active, .open>.btn-danger.dropdown-toggle {
    color: white;
    background-color: #FF0039;
    border-color: #FF0039;
}

.panel-question {
    position: fixed;
    border-top: 1px solid #fff;
    width: 100%;
    z-index: 999;
    border-color: #404040;
}

.panel-primary>.panel-heading {
    color: white;
    background-color: #5777C2;
    border-color: #5777C2;
}

a:hover, a:focus {
    color: #FFFFFF;
    text-decoration: underline;
}

.navbar-brand {
    font-weight: normal;
}

label {
    display: block;
    max-width: 100%;
    margin-bottom: 5px;
    font-weight: bold;
}

.entry-text {
    color: #FFFFFF;
}

.text-muted {
    color: #FFFFFF;
}

.modal-header {
    padding: 15px;
    border-bottom: 0px solid #e5e5e5;
    min-height: 16.428571429px;
}

.modal-footer {
    padding: 20px;
    text-align: right;
    border-top: 0px solid #e5e5e5;
}

.panel-footer {
    border-top: 0px;
    background-color: #404040;
    color: #ccc;
}

.navbar-toggle {
    border-color: #404040;
    background-color: transparent;
}

.navbar-inverse  {
  background-color: #ff9100;
}

.icon-bar {
  background-color: #fff;
}

.panel>.list-group .list-group-item {
    border-width: 0px;
    border-radius: 0;
}

.navbar-inverse .navbar-toggle {
    border-color: transparent;
}

.navbar-inverse .navbar-toggle .icon-bar {
    background-color: #fff;
}

.navbar-inverse .navbar-toggle:hover, .navbar-inverse .navbar-toggle:focus {
    background-color: #404040;
}

nav.navbar .nav>li.active a {
    background-color: #5777C2 !important;
}

.navbar-inverse .navbar-nav .open .dropdown-menu .divider {
    background-color: #404040;
}

.navbar-inverse .navbar-collapse, .navbar-inverse .navbar-form {
    border-color: #303030;
}
}
