css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("slaent.com") {
body {
        background-color: rgba(40,40,40,255) !important;
        color: rgba(200,200,200,255) !important;
    }
blockquote{
    background-color: rgba(30,30,30,255) !important;
    border-color: rgba(30,30,30,255) !important;
}
#footer{
    background-color: rgba(40,40,40,255) !important;
}    
#id_body{
    color: rgba(200,200,200,255) !important;
}
.thread__topic-body-container{
    color: rgba(200,200,200,255) !important;
}
.thread__topic-body{
    background-color: rgba(30,30,30,255) !important;
}
div.thread__item{
    color: rgba(200,200,200,255) !important;
}
.home-forums{
    background-color: rgba(30,30,30,255) !important; 
}
.col--welcome{
    border-right-color: rgba(0,0,0,0) !important;
}
.welcome__title{
    color: rgba(255,255,255,255) !important;;
}
.welcome__description{
     color: rgba(255,255,255,255) !important;;
}
#id_username{
    background-color: rgba(255,255,255,255) !important;
}
#id_email{
    background-color: rgba(255,255,255,255) !important;    
}
#id_tag{
    background-color: rgba(255,255,255,255) !important;    
}
#id_location{
    background-color: rgba(255,255,255,255) !important;    
}
#id_old_password{
    background-color: rgba(255,255,255,255) !important;    
}
#id_new_password1{
    background-color: rgba(255,255,255,255) !important;    
}
#id_new_password2{
    background-color: rgba(255,255,255,255) !important;    
}
#id_password{
    background-color: rgba(255,255,255,255) !important;    
}
.navigation__icon span{
    color: rgba(255,255,255,255) !important;
}
.spoiler{
    color: rgba(200,200,200,255) !important;
    background-color: rgba(200,200,200,255) !important;
    padding-left: 5px !important;
    padding-right: 5px !important;
}
.spoiler.is--revealed{
  color: rgba(34,34,34,255) !important;
}
.navigation{
    left: 0px !important;    
}
.layout--wrapper::after{
    background-color: rgba(0,0,0,0) !important;
}
.navigation__background::before{
    background-image: linear-gradient(to left, rgba(0, 0, 0, 0.39), rgba(14, 14, 14, 0.52)) !important;
}
.promoted-threads{
    background-color: rgba(30,30,30,255) !important;
    border-color: rgba(221,221,221,255) !important;
}    
.promoted-thread__item::after{
    background-image: linear-gradient(rgba(30, 30, 30, 0), rgba(30,30,30,50)) !important;
}    
.thread__cover::after {
    background-color: rgba(0,0,0,0) !important;
}
.thread__background::before{
    background-image: linear-gradient(rgba(255, 255, 255, 0), rgba(30, 30, 30, 255)) !important;
}    
.thread__cover{
    background-color: rgba(30,30,30,255) !important;
}    
.navigation__icon--user::before{
    margin-top: -5px !important;
}    
    
    
    
    
#layout{
    background-color: rgba(40,40,40,255) !important;
}
#user-cp{
	  background-color: rgba(40,40,40,255) !important;
}
}
