css:
@-moz-document domain(web.groupme.com) {
/* -- Table Of Contents ------------ 

     0. Color Palette
     1. Centering
     2. Page Content
     3. Scrollbar
     4. Chat Style
     5. Likes
     6. Message Composer
     7. Pillar Sidebar
     8. Pop out Menu (Left)
     9. Chat Menu (Top)
     10. Modal Content
     11. Member List
     12. Calendar
     13. Popular List
     14. Chat Settings
     15. Image Upload
     16. Emoji Picker
     17. Sign In Page

------------------------------------ */



/* -- 0. COLOR PALETTE --

Black: #000000
Dark Grey: #181D1F
Grey: #232a2d
Light Grey: #CCCCCC
Gold: #C29147
Cream: #FEE4B8

----------------------------- */



/* -- 1. CENTERING -- */

#app {
  height: 100%;
  width: 70%;
  margin-left: auto;
  margin-right: auto;
}

.app-sidebar {
	position: absolute;
	left: auto;
}


/* -- 2. PAGE CONTENT -- */

body {
	color: #CCCCCC;
	background-color: #000000;
	overflow: hidden;
}

h1, h2, h3, h4, h5, h6, .h1, .h2, .h3, .h4, .h5, .h6 {
	color: #C29147;
}

a {
  color: #C29147;
}

a:hover, a:focus {
  color: #FEE4B8;
}

.btn-link {
    color: #C29147;
}

.btn:hover, .btn:focus, .btn.focus {
  color: #FEE4B8;
}

.btn-default {
    color: #181D1F;
    background-color: #cccccc;
    border-color: #cccccc;
}

.btn-default:hover {
    color: #181D1F;
}

.btn-primary {
    color: #181D1F;
    background-color: #cccccc;
    border-color: #181D1F;
}

.btn-primary:hover {
  color: 181D1F;
  background-color: #C29147;
  border-color: #C29147;
}

.btn-primary:active:hover, .btn-primary:active:focus, .btn-primary:active.focus, .btn-primary.active:hover, .btn-primary.active:focus, .btn-primary.active.focus, .open > .btn-primary.dropdown-toggle:hover, .open > .btn-primary.dropdown-toggle:focus, .open > .btn-primary.dropdown-toggle.focus {
  color: 181D1F;
  background-color: #C29147;
  border-color: #C29147;
}

.btn-secondary {
    color: #181D1F;
    background-color: #cccccc;
    border-color: #cccccc;
}

.btn-secondary:hover {
    color: #181D1F;
    background-color: #C29147;
    border-color: #C29147;
}

.btn-secondary:active:hover, .btn-secondary:active:focus, .btn-secondary:active.focus, .btn-secondary.active:hover, .btn-secondary.active:focus, .btn-secondary.active.focus, .open > .btn-secondary.dropdown-toggle:hover, .open > .btn-secondary.dropdown-toggle:focus, .open > .btn-secondary.dropdown-toggle.focus {
  color: #181D1F;
  background-color: #FEE4B8;
  border-color: #FEE4B8;
}

.btn-danger {
    color: #B5322E;
    background-color: #cccccc;
    border-color: #181D1F;
}

.btn-danger:hover {
  color: #181D1F;
  background-color: #B5322E;
  border-color: #181D1F;
}

.text-muted {
    color: #828283;
}



/* -- 3. SCROLLBAR -- */

::-webkit-scrollbar-track {
	-webkit-box-shadow: inset 0 0 0px rgba(0,0,0,0);
	background-color: black;
	display: none;
}

::-webkit-scrollbar {
	background-color: black;
	display: none;
}

::-webkit-scrollbar-thumb {
	border-radius: 2px;
	-webkit-box-shadow: inset 0 0 0px rgba(0,0,0,0);
	background-color: black;
	display: none;
}



/* -- 4. CHAT STYLE -- */

#chats .onboard .empty_group p.poundie {
color: #C29147;
}

#chats .chat {
        border-right: 1px solid #000000;
        border-left: 5px solid #000000;
}  

#chats .chat-header {
	background-color: #181D1F;
	border-bottom: 5px solid #000000;
}

#chats .chat-header .menu-toggle .chat-name {
        color: #C29147;
}

#chats .chat-header .close-chat {
        color: #C29147;
}

.chat-messages {
	background-color: #232a2d;
}

.chat-messages .message.me {
	background-color: #181D1F;
}

.chat-messages .message.highlight {
  background-color: #181D1F;
}

.chat-messages .message.me.highlight {
  background-color: #181D1F;
}

.chat-messages .message .nickname {
  color: #C29147;
}

.chat-messages .message.system {
   background-color: #181D1F;
}

.chat-messages .message.system .message-header .avatar span {
   color: #C29147;
}

.chat-messages .message.system .message-body .message-text {
   color: #C29147;
}

.attachment {
  background: #181D1F;
  border: 5px solid #232a2d;
  border-radius: 0px;
}

.chat-messages .timestamp {
  font-size: 11px;
  color: #cccccc;
}

.chat-messages .timestamp-divider {
background-color: #181D1F;
	}

.chat-messages .message .pasted {
    border-left: 5px solid rgba(194, 145, 71, 1);
    color: #cccccc;
}

.message-inline-downloader {
    background: #181D1F;
    border: 5px solid #232a2d;
    border-radius: 0px;
}

.message-inline-downloader.loading {
    background: #181D1F;
}

#typing {
  color: #181D1F;
  background-color: rgba(194, 145, 71, 1);
}



/* -- 5. LIKES -- */

.chat-messages .count {
    color: #cccccc;
}

.pop-over {
    background-color: #C29147;
}

.chat-messages .message.highlight {
	background-color: #181D1F;
}



/* -- 6. MESSAGE COMPOSER -- */

.message-composer {
    background-color: #181D1F;
    border-top: 5px solid #000000;
}

.message-composer .composer-layout .upload-image,
.message-composer .composer-layout .emoji-button {
  color: #C29147;
}

.message-composer .composer-layout .upload-image:hover,
.message-composer .composer-layout .emoji-button:hover {
  color: #FEE4B8;
}



/* -- 7. PILLAR SIDEBAR -- */

.pillar {
	background-color: #000000;
}

.pillar h1 .tab {
    color: #C29147;
}

.pillar h1 .tab:hover, .pillar h1 .tab:focus, .pillar h1 .tab.active {
  color: #FEE4B8;
}

.pillar .tab {
    display: block;
    position: relative;
    color: #C29147;
}

.pillar .tab.active {
    color: #CCCCCC;
}

.pillar .tab:hover, .pillar .tab:focus {
  color: #FEE4B8;
}

.pillar .tab .badge {
    color: #000000;
    background-color: #CCCCCC;
    border: 2px solid #000000;
}

.pillar .tab.logo i {
    color: #cccccc;
}


/* -- 8. POPOUT MENU (LEFT) -- */

.groupme-list p {
    color: #cccccc;
}

.groupme-list .alpha-header {
background-color: #181D1F;
}

.groupme-list .list-item {
    background-color: #181D1F;
    border-bottom: 5px solid #000000;
}

.groupme-list .list-item.active {
    background-color: #232a2d;
}

.groupme-list .list-item:hover, .groupme-list .list-item.selected, .groupme-list .list-item.active {
    background-color: #cccccc;
}

#tray {
    background-color: #000000;
    border-right: 1px solid #000000;
}

#tray .tray-header h3 {
    color: #cccccc;
}

#tray .tray-header .row {
    background-color: #000000;
    border-bottom: 0px solid #000000;
}

#tray .tray-header .actions .mark-as-read {
    color: #C29147;
}

#tray .tray-header .actions .mark-as-read:hover {
  color: #FEE4B8;
}

#tray .tray-header .actions .new-chat .dropdown-toggle {
    color: #C29147;
}

#tray .tray-header .actions .new-chat .dropdown-toggle:hover {
  color: #FEE4B8;
}

#tray .list-search {
border-bottom: 2px solid #000000;
}

#tray .list-search input {
    background-color: #232a2d;
    border-bottom: 5px solid #000000;
}

.groupme-list .media:hover, .groupme-list .media.selected, .groupme-list .media.active {
    background-color: #181D1F;
}

.groupme-list .media {
    border-bottom: 5px solid #181D1F;
}



/* -- 9. CHAT MENU (TOP) -- */

.chat-menu {
background-color: #181D1F;
border-bottom: 5px solid #000000;
}

.chat-menu .topic-control {
border-bottom: 5px solid #232A2D;
}

.gm-group-topic .topic {
color: #C29147;
}



/* -- 10. MODAL CONTENT -- */

.modal-content {
  background-color: #181D1F;
  border: 1px solid #999999;
  border: 1px solid rgba(0, 0, 0, 0.2);
  border-radius: 0px;
}

.modal .modal-content {
    border-radius: 0px;
}

.close {
    float: right;
    font-size: 21px;
    font-weight: bold;
    line-height: 1;
    color: #C29147;
    text-shadow: 0 1px 0 #232a2d;
    opacity: 0.8;
}

.close:hover, .close:focus {
  color: #FEE4B8;
  opacity: 1;
}



/* -- 11. MEMBER LIST -- */

.group-members .members .add-members .avatar {
    color: #FEE4B8;
    background-color: #C29147;
}

.group-members .members .add-members h5 {
    color: #CCCCCC;
}

.group-members .members .member .creator {
    color: #C29147;
}



/* -- 12. CALENDAR -- */

.calendar-modal .modal-header {
    border-bottom: 0px solid #cccccc;
}

.calendar-modal .modal-body {
    background-color: #232a2d;
    border-bottom-left-radius: 0px;
    border-bottom-right-radius: 0px;
}

.calendar-nav > span.active {
    color: #cccccc;
    border-bottom: 0px;
}

.calendar-nav > span {
    float: left;
    color: #C29147
}

.calendar-event {
    background-color: #181D1F;
}

.calendar-event .header .title {
    color: #cccccc;
}

.calendar-event.event-detail .header .created-by {
    color: #cccccc;
}

.calendar-event .date-badge {
    width: 65px;
    color: #181D1F;
    text-align: center;
    background-color: #cccccc;
    padding: 2px;
    border: 2px solid #C29147;
    border-top: 8px solid #C29147;
}

.calendar-event .date-badge.multi-day:after {
    border-top: 2px solid #C29147;
    border-right: 2px solid #C29147;
}

.calendar-create .time-select .date [readonly] {
    background-color: #cccccc;
}

.calendar-create .category-block > i {
    color: #C29147;
}

.calendar-event .info-section {
    background-color: #181D1F;
    border-top: 0px solid #e9e9e9;
}

.calendar-create .form-footer {
    background-color: #181D1F;
    border-top: 0px solid #cccccc;
    border-bottom-left-radius: 0px;
    border-bottom-right-radius: 0px;
}

/* -- 13. POPULAR LIST -- */

.popular .categories .btn-link.active {
    color: #C29147;
}

.popular .categories .btn-link.active:before {
    background-color: #C29147;
}

.popular .popular-nav {
    border-top: 1px solid #181D1F;
    border-bottom: 1px solid #181D1F;
}

.popular .all-likes-filter {
    padding: 16px;
    border-top: 5px solid #232A2D;
}

.popular .chat-messages .popular-message {
    border-bottom: 5px solid #181D1F;
}

.dropdown-menu {
    background-color: #cccccc;
}

.popular .all-likes-filter .dropdown-menu > li > a i {
    color: #C29147;
}

.popular .chat-messages .popular-message .popular-timestamp.me {
    background-color: #181D1F;
}

.popular .chat-messages .popular-message {
    border-top: 5px solid #232a2d;
    background-color: #181D1F;
}

/* -- 14. CHAT SETTINGS -- */

.edit-field .field-label {
    color: #C29147;
}

.group-settings .setting-block .field-label {
    color: #C29147;
}

.fancy-toggle input[type=checkbox] + span .off {
    display: inline;
    color: #cccccc;
}

.fancy-toggle input[type=checkbox] + span .on {
    color: #C29147;
}

.edit-field form {
    color: #181D1F;
    background-color: #cccccc;
}

.edit-field form button[type="submit"] {
    color: #181D1F;
}

.edit-field form button[type="submit"]:hover {
    color: #C29147;
}

.edit-field form button[type="submit"][disabled], .edit-field form button[type="submit"][disabled]:hover {
  color: #cccccc;
}

.edit-field form button {
    color: #181D1F;
}

.edit-field form button:hover {
  color: #C29147;
}



/* -- 15. IMAGE UPLOAD -- */

.image-upload .modal-shadow {
    background-color: #181D1F;
}

.upload_pic {
    background-color: #232a2d;
    border-radius: 0px;
    border: 0px solid #ebebeb;
}

.text-muted {
    color: #cccccc;
}



/* -- 16. EMOJI PICKER -- */

.emoji-menu .emoji-preview {
    background-color: rgba(24, 29, 31, 1);
}

.emoji-menu .emoji-preview .emoji-label {
  color: #C29147;
}

.emoji-menu .pack-switcher .slider-window .slider .switcher.current, .emoji-menu .pack-switcher .slider-window .slider .switcher.current:hover {
    background-color: #cccccc;
}

.emoji-menu .pack-container {
    background: #232a2d;
    border-top: 0px solid #dddddd;
    border-bottom: 0px solid #dddddd;
}

.emoji-menu .pack-switcher {
    background-color: #181D1F;
}

.emoji-menu .pack-switcher .slider-control {
    background-color: #C29147;
}

.emoji-menu .pack-switcher .slider-control i {
    color: #181D1F;
}

.emoji-menu .emoji-menu-pointer {
  background-color: #181D1F;
}



/* -- 17. SIGN IN PAGE -- */

.well {
    background-color: #000000;
    border: 1px solid #cccccc;
    border-radius: 4px;
}

#signin .account {
    background: #000000;
    border-radius: 4px;
    border: 1px solid #cccccc;
}

#signin .or {
    top: 11px;
    background: #000000;
    color: #cccccc;
}

#signin .facebook-login {
    border-bottom: 2px solid #cccccc;
    margin-bottom: 20px;
}



/* -- END ---------------------------------- */
}
