css:
@-moz-document
 url-prefix(http://www2.maidanua.org/news),
 url-prefix(http://www2.maidan.org.ua/news),
 domain(maidanua.org) {

p, center {overflow: auto !important}

#forumpost td center {width: 960px !important}

#forumlist {
 font-size: 97%;
 margin-top: 1.5em !important;
 margin-bottom: 2em !important;
 }

#forumlist>ul>li {
 margin: 0 !important;
 padding: 0.1ex 0 0.1ex 0 !important;
 }

_#forumlist li {
 text-indent: -1.5em !important;
 padding-left: 1.5em !important;
 }

#forumlist ul li font {
 font-weight: bold;
 font-size: 75%;
 }

#forumlist ul li small {
 white-space: nowrap !important;
 }

#forumlist ul li a {
 color: #0041c4 !important;
 }

#forumlist ul li a:hover {
 color: #34a3d1 !important;
 }

#forumlist ul ul ul ul ul ul ul ul ul ul ul ul ul ul ul ul ul ul ul li a {
 color: hotpink !important;
 }

#forumlist ul ul ul ul ul ul ul ul ul ul ul ul ul ul ul ul ul ul ul li a:hover {
 color: lightpink !important;
 }

#forumlist ul ul ul ul ul ul ul ul ul ul ul ul ul ul ul ul ul ul ul ul li a {
 color: peru !important;
 }

#forumlist ul ul ul ul ul ul ul ul ul ul ul ul ul ul ul ul ul ul ul ul li a:hover {
 color: orange !important;
 }

#forumlist ul li img {
 vertical-align: middle !important;
 }

#forumbtns {
 padding-top: 0 !important;
 padding-bottom: 0 !important;
 margin-top: 1em !important;
 margin-bottom: 1em !important;
 margin-left: 0 !important;
 border-bottom: 1px solid #002d89 !important;
 font-size: 85% !important;
 width: auto !important;
 }

#forumbtns a {
 color: #79c !important;
 }

#forumbtns a:hover {
 color: #346 !important;
 }

#forumbtns div b {
 color: #07f !important;
 }

#mainforum h1 {
 font-size: 140% !important;
 font-family: arial, tahoma;
 margin-bottom: 0.4em !important;
 font-weight: normal;
 }

#mainforum p {
 font-size: 12pt !important;
 line-height: normal !important;
 }

#mainforum {
 border-top-style: none !important;
 width: 100% !important;
 }

ul {
 overflow: auto;
 padding-left: 1.5em;
 }

ul ul {
 overflow: visible;
 }

#forumpost p em {
 background-color: #f0f7ff;
 color: #079;
 }

small em {
 background-color: gold !important;
 color: #000;
 }

#hldhdSubjectNavKey + #hdcontentNav {
 border-top: 1px solid #a7a7a7 !important;
 border-bottom: 1px solid #a7a7a7 !important;
 margin-bottom: 0.2em !important;
 }

#hdcontentNav {
 margin-top: 0 !important;
 padding: 0.2em !important;
 font-size: 85%;
 }

#hdcontentNav1 {
 font-size: 85% !important;
 margin-bottom: 0.2em !important;
 margin-top: 0.2em !important;
 }

#hdcontentNav1 img {
 height: 12px;
 }

#hdcontentNav1 select {
 margin: 0 !important;
 padding: 0 !important;
 border-style: none !important;
 background-color: transparent !important;
 }

#hldmain {
 height: auto !important;
 margin-top: 0 !important;
 margin-bottom: 0 !important;
 }

#forumfooter {
 border-top: 1px solid #775300 !important;
 border-bottom: 1px solid #775300 !important;
 }

#forumfooter * {
 background-color: gold !important;
 font-size: 85%;
 font-weight: bold !important;
 color: #775300 !important;
 }

#forumfooter a:hover {
 color: #bb9400 !important;
 }

#ftsearch {
 margin-top: 0 !important;
 }

#ftsearch * {
 display: inline !important;
 padding-top: 0 !important;
 padding-bottom: 0 !important;
 }

#ftsearch form * {
 }

#ftsearch form table {
 font-size: 110%;
 }

#ftsearch input[type="text"][class="textinput"][name="pattern"] {
 color: black !important;
 border-color: black !important;
 background-color: transparent !important;
 }

#searchform_0_ft {
 height: auto !important;
 vertical-align: middle;
 }

#forumpost {
 border-bottom-style: none !important;
 margin: 0 !important;
 }

#forumpost ul {
 margin-bottom: 0.5em !important;
 }

#forumpost ul li b:first-child {
 font-weight: normal;
 font-size: 85%;
 color: #37d;
 }

#forumpost ul li b:last-child a {
 font-style: italic;
 color: #05c !important;
 font-size: 97%;
 }

#forumpost p {
 margin: 0 !important;
 padding: 0 !important;
 }

#forumpost ul + p a {
 color: #05c !important;
 }

#skcolCover,
#skcolCover2,
#skycol,
#hdkey,
#ftdivider,
#hldhdSubjectNavKey,
#ftdivider + #hdcontentNav+#hdcontentNav,
td[bgcolor="#e3ecff"],
marquee#scrolltext,
td.listhead script + a,
td.listhead span.highlight,
td.listhead a[onclick] {
 display: none;
 }

}
