css:
@-moz-document domain("tabletopgamingnews.com"){
/* Hide Stuff */

.tm_topads_468,
.tm_sidebarwidget_left ul >:nth-child(3),
.tm_right ul >:nth-child(1),
hr+center,
hr,
.right_sidebar_ad,
.right_sidebar_ad2,
.right_sidebar_ad3,
.left_sidebar_ad,
.left_sidebar_ad2,
.left_sidebar_ad3
{
    display: none !important;
}
/* Header */

.tm_header_file,
.tm_header_logo_ads
{
    width: 100% !important;
    padding: 0px !important;
    height: 90px !important;
    text-align: center !important;
}

.tm_logo
{
    float: none !important;
    padding-left: 20px !important;
}
/* Floating Menu */

.navigasi_menu
{
    width: 100% !important;
}

.menu-topmenu-container
{
    text-align: center !important;
}

.navigasi_menu ul
{
    display: inline-block;
}

.navigasi_menu li
{
    display: inline-block;
}
/* Page Width */

.tm_content,
.tm_page,
.tm_footer,
.tm_post
{
    display: table !important;
    width: 100% !important;
}

.tm_left
{
    display: table-cell !important;
    float: none !important;
    vertical-align: top !important;
    padding: 0px 5px 0px 60px !important;
}

.tm_center,
.tm_category_2,
.tm_middle_page_2,
.tm_middle_post_2
{
    display: table-cell !important;
    float: none !important;
    width: auto !important;
    vertical-align: top !important;
}

.tm_right
{
    display: table-cell !important;
    float: none !important;
    vertical-align: top !important;
    padding: 0px 60px 0px 5px !important;
}

@media only screen and (max-width: 1028px)
{
    .tm_left
{
        display: none !important;
    }
    .tm_center,
    .tm_category_2,
    .tm_middle_page_2,
    .tm_middle_post_2
{
        padding: 0px 15px 0px 15px !important;
    }
    .tm_right
{
        display: none !important;
    }
    .navigasi_list a
{
        font-size: 50%;
        padding: 0px 10px;
    }
    .tm_menu_desktop
{
        overflow: hidden !important;
    }
}
/* Center Images */

.tm_cat_image,
.wp-post-image
{
    display: block !important;
    margin: auto !important;
}

.tm_cat_image img,
.wp-post-image img
{
    width: inherit !important;
    max-height: 400px !important;
}
/* Mobile Tweaks */

.loop-link
{
    min-height: 110px !important;
}

.circles .loop-link img.post-thumbnail
{
    -webkit-border-radius: 20px;
    border-radius: 20px;
}

.loop-link img.post-thumbnail
{
    width: 100px !important;
    height: 100px !important;
    float: none !important;
    position: absolute !important;
    margin-top: -52px !important;
    left: 5px !important;
    top: 50% !important;
    padding: 1px;
    border: 1px solid #558040;
    background-color: #ffffff;
}

.loop-link .comments
{
    top: 0px !important;
    left: 90px !important;
    -webkit-filter: drop-shadow(2px 1px 0 white) drop-shadow(-2px 1px 0 white);
    filter: drop-shadow(2px 1px 0 white) drop-shadow(-2px 1px 0 white);
}

.loop-link .post-date-author
{
    margin-left: 115px !important;
    padding-top: 0px !important;
}

#content .loop-link h2
{
    width: 60% !important;
    margin-left: 115px !important;
    padding-bottom: 0px !important;
    font-size: 120% !important;
}

.loop-link i.arrow
{
    right: 0px !important;
}

.loop-link .bottom-border
{
    left: 0px !important;
}
}
