@-moz-document url-prefix("http://sp.pf.mbga.jp/12007219"),  url-prefix("http://us-gijin-app.amz-aws.jp") {
  
  ul#tab_android {display: flex;}
  #tab_android.clumn2.li {width: 48% !important;}
  #tab_android.clumn3.li {width: 32% !important;}
  
  /*[[style]]*/
  
  .button_select_event_tosikosi, .selbtn_event1, .button_select_event_plum {background-image: -moz-linear-gradient(top, #333333 50%, #000000 50%); border-radius: 10px;}
  .button_event_police, .button_event_chess {background-image: -moz-linear-gradient(top, #7C6C64 50%, #4B2C29 50%); border-radius: 10px;}
  .tab_button1_event_police, .bg_pink_event_police, .tab_button1_event_chess, .bg_pink_event_chess {background-image: -moz-linear-gradient(top, #ffe3f1 50%, #f5d9e7 50%);}
  .tab_button2_event_police, .tab_button2_event_chess {background-image: -moz-linear-gradient(top, #DDDDDD 50%, #CCCCCC 50%);}
  
  .area_line_event_police, .area_line_event_chess {background-image: -moz-radial-gradient(center, circle cover, #BF9286 20%, #35221C 100%);}
  .event_kun_line_event_chess {background-image: -moz-radial-gradient(center, circle cover, #BF9286 20%, #35221C 100%);}
  .frame_event_escape_index {background-image: -moz-linear-gradient(top, #AFAFAF 0%, #FFF 100%);}
  .area_line_event_escape {background-image: -moz-radial-gradient(center, circle cover, #5858FA 20%, #00008B 100%);}
  
  /*Mobage nav bar attempt*/
  #mbga-pf-footer {display: flex; flex-wrap: wrap;}
  .mbga-pf-footer-arrow {background-size: 6px 9px; transition: all 0.5s ease-in-out;}
  .mbga-pf-footer-arrow-out {background-size: 8px 14px; transition: all 0.5s ease-in-out;}
  #mbga-pf-footer-navbar, #mbga-pf-footer-navbar-mini, .mbga-pf-footer-li {display: flex; flex-wrap: nowrap; background: -moz-linear-gradient(top, #002a5b 0, #003877 1%, #00438f 2%, #002e62 50%, #002d61 98%, #003872 99%, #004486 100%);}
  ul.mbga-pfbox-h {display: flex; justify-content: center;}
  li.mbga-pf-footer-li {flex: 1;}
  
}

