@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain(opensubtitles.org){

  /* GLOBAL */
  h1 , h2 , .a_1,.a_2, .star, .hint, .msgarrow , div#wibiyaToolbar , [id="network"],[href*="iwatchfilm"], [href*="torrentbar"], [href*="zoozle"], [href*="http://www.amazon.com/"], [rel="nofollow"], [href*="http://ads"] , [id="ad1"], [id="ad2"], [id="ad2"] , [id="discussion"] , .bar, #spot-im-root , #multiple_slides_visible , div.footer , div.top_info_right , .content > fieldset  { display: none !important; }
  
  
  /*Black Background + Disable Text Truncate*/  body { background-color: black ; overflow: auto  !important; text-overflow: initial  !important;}
  /*Remove BackGround from searchfield*/ .search_field { background: none; }
  /*Remove Ad reserved zone in search result list*/ tr { height: 1px !important ; background-color: black}
  /*Remove botoom bar in result list*/  #submultiedit > fieldset  {display:none;} 
  
  /*Remove some columns in tables*/
  th:nth-child(6) , td:nth-child(6){display:none;}
  th:nth-child(7) , td:nth-child(7){display:none;}
  th:nth-child(8) , td:nth-child(8){display:none;}
  th:nth-child(9) , td:nth-child(9){display:none;}
  
  /*Window Size*/
  .content , table#search_results { width: 100%}
  .content > div  {right:1%}
  div.header , div.logo {padding-bottom:20px;}


}
