css:
@-moz-document domain("zozo.jp") {
html,
body,
#gblFooter,
#contentHeader,
#searchResultNum > div,
#searchSidebarSelecter,
#swConditionTab,
#noticeData,
#contentHeader > h1
{
background: none #141414 !important;
color: #DCDCDC !important;
}

#categoryList > ul *,
#myFavorite > ul *
{
color: #CCC !important;
border-color: #666 !important;
background: #141414 !important;
}

#playSlot,
#sideMenuMale > div.maleList.lineside,
#sideOmikuji,
#sec10thBanner,
#fCorporate,
#sideMenuMale > dl,
#menuUsedBanner,
#about
{
display: none !important;
}


img,
#btnNext > span,
#btnPrev > span,
div > div.bkmk > form
{
opacity: .90 !important;
}


* {
color: #CCC !important; 
border-color: #444 !important;
outline-color: #444 !important;
}

html *:after,
html *:before
{
color: #CCC !important;
border-color: #666 !important;
background: none transparent !important;
}


a:link,
a:link *:not(img)
{
color: #B1CBF7 !important;
background-color: transparent !important;
border-color: #666 !important;}

a:visited, 
a:visited *
{
color: #B1CBF7 !important;
}

#hFavoriteLink > div > div,
#hNotice > div > div > div,
#hCart > div > div > div,
#hServiceMap > div > div,
#hUserName > div > div > ul,
a:hover,
a:hover *:not(img)
{
color: #FFE900 !important; 
background-color: #363037 !important;
border-color: #999 !important;
}
}
