css:
@-moz-document url(http://mobileforum.gameloft.com/forum_revamp/) {
  
  dd.lastpost > span > a:nth-child(3) > img {
    height: 13px;
    width: auto;
  }

  #page-body > div:nth-child(2), #page-body > p:nth-child(3) {
      display: none !important;
  }

  .header {
      height: auto !important;
  }

  .forum-image img {
      height: 50px;
      width: auto;
  }

  ul.topiclist li {
      height: 60px;
      overflow: hidden;
  }

  .buttons div.post-icon, .buttons div.reply-icon {
      height: 30px;
  }

  .bg1, .bg2 {
      border: 1px solid silver;
  }

  .profile-icons {
      margin-left: 10px;
  }

  ul.profile-icons li.edit-icon,ul.profile-icons li.quote-icon,ul.profile-icons li.pm-icon {
      width: 100px;
  }

  .postprofile img {
      height: 100px;
      width: auto;
  }
}
