css:
         /* Pocket - Perfect reading (Идеальное чтение) */



  /* Главная страница */ 



  /* Другое */
 @-moz-document domain("getpocket.com") {
  
    /* Уведомление о синхронизации */
  .notifications-sync .content {
    padding-top: 9px;
    padding-bottom: 9px;
  } 
  
}



  /* Вид плитки */ 
  @-moz-document domain("getpocket.com") {

	/* Верхний бар */ 
	#page .toolbar {
		margin-left: 20px;
		margin-right: 10px;
		max-width: 100%;
	}
    
    .pkt-nav .pagenav_sectiontoggle .queue_togglesection_optionselected {
      padding-top: 2%;
      height: 90%;
     
    }
	
	#page .pkt-nav {
		top: -5px;
		height: 45px;
		box-shadow: 0 2px 5px rgba(0, 0, 0, 0.5);
		border-bottom-width: 0px;
	}
	
	.flexbox .pagenav_extrasdivider {
		height: 40px;
	}
	
	.flexbox .icons .queue_title {
		margin-left: 120px;
	}
	
	.container-bulkedit-active {
		height: 45px;
	}
	
	
	/* Выпадающая панель с тегами */
	#pagenav_tagfilter .popover-active,
  #pagenav_tagfilter .tagfilter_loading {
		height: 350px;
		box-shadow: 0 1px 5px rgba(0, 0, 0, 0.5);
		border-width: 0px;
		border-radius: 10px;
	}	
	
	#pagenav_tagfilter ul {
		height: 340px;
	}
	
  /* Меню удаления тега */
    .overlay_screen .overlay_detail_confirm {
      box-shadow: 0 1px 8px rgba(0, 0, 0, 0.5);
		border-width: 0px;
		border-radius: 10px;
    }
	
	/* Левый бар*/	
	div.side-nav {
		padding-left: 20px;
		width: 125px !important;
	}
	
	.nav-content-header {
		height: 45px;
		padding: 0;
	}
	
	
	/* Весь список */
	#content .wrapper.active {
		padding-top: 60px;
	}
	
	.queue_secondarynav {
		margin-top: 0px;
		margin-left: 10px; margin-right: 20px;
	}
		
	body #container {
		max-width: 100%;
}

	body .wrapper_full {
  	max-width: 100%;
}

	body .page_queue_grid .queue_list {
		padding: 0;
}

	
	/* Оформление плитки */
	body .page_queue_grid .item {
		max-width: 300px;
}
	
	.page_queue_grid .item_content{
		box-shadow: 0 1px 4px rgba(0, 0, 0, 0.5);
		border-width: 0px;
		border-radius: 5px;
	}
	
	.page_queue_grid .item .title{
		line-height: 1.1;

		padding: 5px;
		padding-bottom: 5px; padding-top: 5px;
		padding-right: 5px;
	}

	.page_queue_grid .item .favicon {
		left: 5px;
		bottom: 5px;
	}
	
	.page_queue_grid .item .sub {
		padding-bottom: 5px;
		padding-top: 0px;
		padding-left: 25px;
		padding-right: 5px;
	}
	
	.page_queue_grid .item .buttons {
		bottom: 5px;
		right: 5px;
	}
	
	
	/* Меню добавления сылки */
	#addMenu .container {


		border-radius: 10px;			
	}
	
	#addMenu {
		box-shadow: 0 1px 5px rgba(0, 0, 0, 0.5);
		border-width: 0px;
		border-radius: 10px;	
		width: 300px;
	}
	
	#addMenu.popover-new.addmenu-hideconnect.popover-new-bottomleft.popover-active.shown {
		top: 45px !important;
	}	
	
	#addMenu .container h5 {
		padding: 0px;
		margin-top: 5px; margin-bottom: 5px;
	}

	#addMenu .container input {
		width: 180px;
		margin-bottom: 8px; 
		margin-left: 5px; 
		margin-right: 5px; 
		padding-left: 5px; 
		padding-right: 5px;
	}
	
	#addMenu .container .button {
		width: 76px;
		margin-left: 5px; 
		padding: 5px; 
		margin-bottom: 5px; 
		margin-right: 5px;
	}
	
  /* Меню профиля */ 
    #pagenav_options_container .popover-new-bottomleft {
      top: 27px !important;
    }
	
	/* Почта */ 
	.popover-active {
		box-shadow: 0 1px 5px rgba(0, 0, 0, 0.5);
		border-width: 0px;
		border-radius: 10px;
	}
    
  /* Уведомление - добавлено в архив */
  .pkt_toast {
    border-radius: 5px;

		border-width: 0px;

  }
    
  .pkt_toast_wrapper {
    top: 30px;
    margin-top: 0px !important;
  }
	
    
  /* Редактировать теги */
  .overlay_screen .overlay_detail_confirmtag {
    box-shadow: 0 1px 5px rgba(0, 0, 0, 0.5);
		border-width: 0px;
		border-radius: 10px;
  }

  .page-app-ru-ru .overlay_screen .overlay_detail_confirmtag {
    padding: 5px 5px 20px;
    width: 345px;
  }
  
  .overlay_screen .overlay_detail_confirm h3  {
    margin-top: 5px; margin-bottom: 15px;
  }
    
  .overlay_detail_confirmtag .token-input-list, .token-input-dropdown-fortag {
    width: 335px;
    margin-bottom: 10px;
    float: inherit !important;
  }
	
    .overlay_screen .overlay_detail_confirmtag .button {
      float: inherit !important;
    }
    
    .token-input-dropdown-fortag {
		border-radius: 5px;
      width: 335px !important;
    }
}




/* Вид список */
  @-moz-document domain("getpocket.com") {
  
    /* My list (Мой список) */
   .page_queue_list .item .buttons {
     right: 0em;
     top: 4.35em;
  }
  
  .page_queue_list .item .title {
    white-space: pre-line;
    line-height: 1;
    padding-left: 110px;
    padding-top: 10px;
  }
    
  .page_queue_list .item .sub {
    padding-left: 110px;
     margin-left: 0;
  }

}




  /* Оформление текста при чтении */ 
  @-moz-document domain("getpocket.com") {
 
  .fontsize-0 .reader_content{
 max-width: 600px;
  }
     
  
   /* Padding on the page (Отступы на странице) */ 
  #page_reader.articleview {
    padding-top: 30px;
    padding-bottom: 20px;
  }
  
  
  /* Article title (Название статьи) */ 
  .reader_head h1 {
    font-size: 30px;
    text-align: center;
  }

  
  /* Indent after the title of the article (Отступ после названия статьи) */
  .reader_head .sub {
    margin: 5px auto;
  }
  
  .reader_head {
    padding-bottom: 0px;
    margin-bottom: 5px;
  }
    
  
  /* Headlines articles (Заголовки статьи) */ 
  .text_body h1,
  .text_body h2,
  .text_body h3, 
  .text_body h4, 
  .text_body h5, 
  .text_body h6, 
  .text_body h7 {
    text-indent: 30px; 
    text-align: justify;
    max-width: 600px;
    margin-top: 15px;
    margin-bottom: 0px;
  }
  

  /* Text paragraph (Текстовый абзац) */  
  .text_body p {
    line-height: 1.3;
    margin:0 0 0.5em;
    width: 600px;
    margin: 5px auto;
    text-indent: 30px;
    text-align: justify;   
  }
     
  .text_body div  {
    line-height: 1.3;
    margin:0 0 0.5em;
    width: 600px;
    margin: 5px auto;
    text-indent: 30px;
    text-align: justify;        
  }
   
  .text_body {
    line-height: 1.3;
    margin:0 0 0.5em;
    width: 600px;
    margin: 5px auto;
    text-align: justify; 
  }
  
  .text_body br {
    display: flex;
  }
  
  .reader_head .sub .hasTags {
    margin-bottom: 10px;
    margin-top: 0px;
  }
    
  
  /* Numbered list (Нумерованый список) */
  .text_body ol {
    line-height: 1.3;
    margin:0 0 0.5em;
    width: 600px;
    margin: 5px auto;
    text-indent: 30px;
    text-align: justify;
    margin-top: 5px !important;
  }
  
  
  /* Bulleted list (Маркированый список) */
  .text_body ul {
    line-height: 1.3;
    margin:0 0 0.5em;

    margin: 5px auto;
    text-indent: 30px;
    text-align: justify;
  }
  
  .text_body li {
    line-height: 1.3;
    margin:0 0 0.5em;
    width: 570px !important;
    margin: 5px auto;
    margin-left: 30px !important;
    text-align: justify;
    text-indent: 0px !important;
    margin-left: -10px;
  }
  
  
  /* Quote (Цитата) */
  .text_body blockquote {
    background-color: transparent;
    background-image: url("data:image/svg+xml;base64,%3D"), url("data:image/svg+xml;base64,%3D");
    background-repeat: repeat-x, repeat-x;
    background-attachment: scroll, scroll;
    background-position: 0px 0px, -13px bottom;
    background-clip: border-box, border-box;
    background-origin: padding-box, padding-box;
    background-size: 27.3px auto, 27.3px auto;

    border-left-width: 0px; 
    padding: 0em; 
    
    padding-bottom: 20px; padding-top: 20px;
  
    line-height: 1.3;
    margin:12px 0 12px;
    max-width: 600px;
    text-indent: 30px;
    text-align: justify;

    white-space: pre-line; 
  }
  

    
  
  /* Images (Изображения) */  
  .RIL_IMG img,
  .RIL_IMG,
  .RIL_IMG.loaded,
  .text_body div img {
    height: auto;

    max-width: 600px;
    text-align: center !important;
    margin-bottom: 2px;
    margin-top: 2px; 
    
    text-indent: 0px !important;
    border-radius: 5px;
  } 
     
  
  /* Description image (Описание изображения) */ 
  .RIL_IMG.hasCaption caption,
  .text_body div.img-alt {
    text-align: center !important;
    font-style: italic;
    padding: 5px 0px 1px;
    font-size: 0.8em;
    color: #B1B2B2 !important;
  }
     

  /* Video (Видео) */
  .RIL_VIDEO {
    margin-bottom: 2px;
    text-indent: 0px !important;
    text-align: center !important;
  }
    
    .RIL_VIDEO  embed {
      width: 600px !important;
      height: 365px !important;

       max-height: 550px !important;
       max-width: 600px !important;
    }
}
  
  /* Дизайн страницы чтения */
  @-moz-document domain("getpocket.com") {
 
  /* Верхний бар */ 
  .page-reader #page .pkt-nav {
    top: -0.5em;
    box-shadow: none;
    background: #F4F4F6;
    height: 45px !important;
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0.5);
    border-width: 0px;
  }
  
  .flexbox .toolbar_reader_title {
    max-width: 650px;
  }
  
  #page .pkt-nav .wrapper.active {
    margin-left: 20px; margin-right: 20px;
  }
    
  #styleMenu {
    box-shadow: 0 1px 5px rgba(0, 0, 0, 0.5);
		border-width: 0px;
		border-radius: 10px;
    border-bottom-width: 0px;
    left: 1082px !important;
    top: 39px !important;
  }
    
    #shareMenu {
    box-shadow: 0 1px 5px rgba(0, 0, 0, 0.5);
		border-width: 0px;
		border-radius: 10px;
    border-bottom-width: 0px;
    }

    .page-reader .pkt-nav {
      border-width: 0px !important;
    }
  
  body.page-reader {
    background: url(data:image/jpeg;base64,) center;
}

  .page-reader #page_reader.articleview {
    padding-top: 75px;
  }

  .page-reader .reader_content {
    background: #fefefe;
    padding: 2.1em;
    border: 1px rgba(153,153,153,.85) solid;
    border-radius: 10px;
    box-shadow: 0 1px 5px rgba(0, 0, 0, 0.5);
    position: relative;
  }

  .page-reader .original {
    color: #fefefe;
    background-color: #62af43;
    padding: .3em .5em !important;
    border-radius: 2px;
    box-shadow: 0 1px 0 #4c8533;
    position: absolute;
    left: -.5em;
    top: 1em;
  }
 
  .page-reader .original a {
    color: #fefefe !important;
    font-size: 14px !important;
  }

  .page-reader .original::before {
    content: '';
    display: block;
    width: 0px;
    height: 0px;
    border-style: solid;
    border-width: 0 7px 7px 0;
    border-color: transparent #4c8533 transparent transparent;
    position: absolute;
    left: 0;
    top: 1.53em;
  }

  .page-reader .authorsdomain {
    display: none;
  }
      
  .reading_progress_bar {
    transition-timing-function: ease-out;
    transition-duration: 0.1s;
  }
  
  
  /* Уведомление о ошибке */
  .csstransforms3d .notifications-error {
    margin-top: -13px;
    box-shadow: 0 2px 5px rgba(0, 0, 0, 0.5);
    transition-delay: 8s, 7s;
    transition-duration: 1s, 1s;
  }
  
  .notifications-error .content {
    padding-top: 7px;
    padding-bottom: 5px;
  }
  
  
  /* Уведомление о синхронизации */
  .notifications-sync .content {
    display: none;
  } 
 
    
}
