css:
@-moz-document domain("reddit.com") {
/* GIVE EVERY COMMENT THE RANDOM FILLY! */
.commentarea .usertext{padding-left: 70px; min-height: 140px; margin-top: -70px}

.commentarea .usertext-body:before{
 display:block;
 width: 70px;
 height: 70px;
 content: " ";
 position: relative;
 top: 75px;
 right: 70px;
 background:url(http://a.thumbs.redditmedia.com/tC7-NUJ-_PdpOrTvJfFxsOIY-bxgJtv_EbcgIzsf0G0.png);
 background-position: 0 -1200%
}

.commentarea .usertext-edit:not(.viewSource){padding-top: 70px; margin-left: -70px}
.res-commentBoxes .commentarea .comment{overflow:visible!important}
.usertext-edit {min-width:505px}

.usertext input[value$="a"] + .usertext-body:before{background-position: 0 0}
.usertext input[value$="b"] + .usertext-body:before{background-position:0 -100%}
.usertext input[value$="c"] + .usertext-body:before{background-position:0 -200%}
.usertext input[value$="d"] + .usertext-body:before{background-position:0 -300%}
.usertext input[value$="e"] + .usertext-body:before{background-position:0 -400%}
.usertext input[value$="f"] + .usertext-body:before{background-position:0 -500%}
.usertext input[value$="g"] + .usertext-body:before{background-position:0 -600%}
.usertext input[value$="h"] + .usertext-body:before{background-position:0 -700%}
.usertext input[value$="i"] + .usertext-body:before{background-position:0 -800%}
.usertext input[value$="j"] + .usertext-body:before{background-position:0 -900%}
.usertext input[value$="k"] + .usertext-body:before{background-position:0 -1000%}
.usertext input[value$="l"] + .usertext-body:before{background-position:0 -1100%}
.usertext input[value$="m"] + .usertext-body:before{background-position:-100% 0}
.usertext input[value$="n"] + .usertext-body:before{background-position:-100% -100%}
.usertext input[value$="o"] + .usertext-body:before{background-position:-100% -200%}
.usertext input[value$="p"] + .usertext-body:before{background-position:-100% -300%}
.usertext input[value$="q"] + .usertext-body:before{background-position:-100% -400%}
.usertext input[value$="r"] + .usertext-body:before{background-position:-100% -500%}
.usertext input[value$="s"] + .usertext-body:before{background-position:-100% -600%}
.usertext input[value$="t"] + .usertext-body:before{background-position:-100% -700%}
.usertext input[value$="u"] + .usertext-body:before{background-position:-100% -800%}
.usertext input[value$="v"] + .usertext-body:before{background-position:-100% -900%}
.usertext input[value$="w"] + .usertext-body:before{background-position:-100% -1000%}
.usertext input[value$="x"] + .usertext-body:before{background-position:-100% -1100%}
.usertext input[value$="y"] + .usertext-body:before{background-position:-200% 0}
.usertext input[value$="z"] + .usertext-body:before{background-position:-200% -100%}
.usertext input[value$="1"] + .usertext-body:before{background-position:-200% -200%}
.usertext input[value$="2"] + .usertext-body:before{background-position:-200% -300%}
.usertext input[value$="3"] + .usertext-body:before{background-position:-200% -400%}
.usertext input[value$="4"] + .usertext-body:before{background-position:-200% -500%}
.usertext input[value$="5"] + .usertext-body:before{background-position:-200% -600%}
.usertext input[value$="6"] + .usertext-body:before{background-position:-200% -700%}
.usertext input[value$="7"] + .usertext-body:before{background-position:-200% -800%}
.usertext input[value$="8"] + .usertext-body:before{background-position:-200% -900%}
.usertext input[value$="9"] + .usertext-body:before{background-position:-200% -1000%}
.usertext input[value$="0"] + .usertext-body:before{background-position:-200% -1100%}

/*Hiddenbee goes here*/

.usertext input[value$="5r"] + .usertext-body:before{background:url(http://b.thumbs.redditmedia.com/BFmq7f2F-_ORi_XLpmRG4Vao7IQRcsfCjSBNTW4hprE.png); background-position: -100% -500%}
.usertext input[value$="5s"] + .usertext-body:before{background:url(http://b.thumbs.redditmedia.com/BFmq7f2F-_ORi_XLpmRG4Vao7IQRcsfCjSBNTW4hprE.png); background-position: -100% -600%}
.usertext input[value$="555"] + .usertext-body:before{background:url(http://b.thumbs.redditmedia.com/BFmq7f2F-_ORi_XLpmRG4Vao7IQRcsfCjSBNTW4hprE.png); background-position: -100% -700%}

/*Dear /u/DoomedCivilian, thanks for the Reddit Gold. It temporarily solved my problem of reddit-wide comment fillies. This works better though, no offense!*/
}
