css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("youtube.com") {

       /* бегунок */
   .html5-scrubber-button, .ytp-scrubber-button {background: #333 !important;border-color: rgba(38, 201, 254, 0.7) !important;box-shadow: 0 0 4px rgba(255, 255, 255,  1) !important;transition:all 350ms ease-in-out !important}
       /* бегунок  при наведени*/ 
   .html5-scrubber-button:hover, .ytp-scrubber-button:hover {background: #fff !important;border-color: rgba(54, 201, 238,   0.5) !important;box-shadow: 0 0 9px rgba(255, 255, 255,   0.9) !important;transition:all 10ms ease-in-out !important}       
       /* нижний бар */
   .html5-player-chrome {background-color: #000 !important;background:  rgba(0, 0, 0,  0.65)!important;}
       /* загрузка < */
   .html5-play-progress, .ytp-play-progress {background: rgba(54, 201, 238, 0.99) !important; box-shadow: 0px 0px 3px rgba(54, 201, 238,  .9 ) !important}        
       /* загрузка > */
   .html5-play-progress,.ytp-load-progress {background: rgba(54, 201, 238, 0.99) !important; box-shadow: 0px 0px 9px rgba(0, 0, 0,  .9 )inset !important}        
       /* загрузка */
   .ytp-unloaded-progress {background: rgba(0, 0, 0,  0.1)!important}   
       /* 10 сек */
   .ytp-ad-progress {background: #000 !important; background: linear-gradient(to bottom, #000 0%, #333 100%, #000 190%) !important;background: -webkit-linear-gradient(to bottom, #000 0%, #333 100%, #000 190%) !important}            
       /* бегунок звука */
   .ytp-volume-slider-foreground {background: rgba(54, 201, 238,  0.9)!important; box-shadow: 0px 0px 3px rgba(54, 201, 238,  .9 ) !important}    
       /* звук > */ 
   .ytp-volume-slider-foreground:after {background: rgba(54, 201, 238,  0.9)!important; box-shadow: 0px 0px 5px rgba(0, 0, 0,  .9 )inset !important}  
       /* звук < */
   .ytp-volume-slider-foreground:before {background: rgba(54, 201, 238,  0.9)!important; box-shadow: 0px 0px 1px rgba(0, 0, 0,  .9 )inset !important}  
       /*цвет текста*/
   .ytp-time-display, .ytp-time-current, .ytp-time-separator, .ytp-time-duration,    .ytp-tooltip.ytp-bottom,.ytp-tooltip {color: #3ce!important}
    
     /* окна */
  .ytp-subtitles-settings-dialog-top-level {border:none!important}
  .ytp-dialog, .ytp-menu, .ytp-panelpopup {background: rgba( 0, 0, 0, 0.6)!important; box-shadow: 0px 0px 5px rgba(0, 0, 0,  .9 )inset !important;border-radius:7px!important}
  .ytp-subtitles-settings-dialog-top-level .ytp-subtitles-settings-dialog-list-selected, .html5-info-bar {background: rgba( 54, 201, 238, 0.3)!important; box-shadow: 0px 0px 2px rgba(0, 0, 0,  .9 )inset !important}
  .ytp-dialog-buttons {background:transparent!important}
        
       /* иконки */  
  .ytp-button-watch-later {background: url(http://savepic.org/7223747.png) no-repeat !important;background-size: contain !important;transform:scale(0.7,0.7) !important}  
  .ytp-subtitles-button, .cc-international .ytp-subtitles-button-active:hover, .cc-international .ytp-subtitles-button-active, .ytp-subtitles-button-active:hover {background: url(http://savepic.org/7221699.png) no-repeat !important;background-size: contain !important;transform:scale(0.7,0.7) !important}  
  .ytp-settings-button, .ytp-settings-button-active {background: url(http://savepic.org/7224771.png) no-repeat !important;background-size: contain !important;transform:scale(0.7,0.7) !important}  
  .ytp-size-toggle-large {background: url(http://savepic.org/7222723.png) no-repeat !important;background-size: contain !important;transform:scale(0.7,0.7) !important}
  .ytp-size-toggle-small {background: url(http://savepic.org/7216579.png) no-repeat !important;background-size: contain !important;transform:scale(0.7,0.7) !important}  
  .ytp-button-fullscreen-enter {background: url(http://savepic.org/7212483.png) no-repeat !important;background-size: contain !important;transform:scale(0.7,0.7) !important}
  .ytp-button-fullscreen-exit  {background: url(http://savepic.org/7266754.png) no-repeat !important;background-size: contain !important;transform:scale(0.7,0.7) !important}  
  .ytp-settings-hd-quality-badge {background: url(http://savepic.org/7244265.png) no-repeat !important;background-size: contain !important;transform:scale(2.1,2.1) !important}  
  .ytp-button-playlist {background: url(http://savepic.org/7251434.png) no-repeat !important;background-size: contain !important;transform:scale(0.9,0.9) !important; margin-top: -3px !important} 
  .ytp-button-play {background: url(http://savepic.org/7270850.png) no-repeat !important;background-size: contain !important;transform:scale(0.7,0.7) !important;background-position: center center !important}
  .ytp-button-pause, .ytp-button-pause:hover:not(.ytp-disabled)  {background: url(http://savepic.org/7261634.png) no-repeat !important;background-size: contain !important;transform:scale(0.7,0.7) !important;background-position: center center !important}
  .ytp-button-stop {background: url(http://savepic.org/7217623.png) no-repeat !important;background-size: contain !important;transform:scale(0.7,0.7) !important}
  .ytp-button-replay {background: url(http://savepic.org/7223767.png) no-repeat !important;background-size: contain !important;transform:scale(0.7,0.7) !important}  
  .ytp-button-next {background: url(http://savepic.org/7246274.png) no-repeat !important;background-size: contain !important;transform:scale(0.7,0.7) !important}
  .ytp-button-prev {background: url(http://savepic.org/7219671.png) no-repeat !important;background-size: contain !important;transform:scale(0.7,0.7) !important}  
 /*100%*/ .ytp-button-volume[data-value="max"], .ytp-button-volume[data-value="loud"] {background: url(http://savepic.org/7265730.png) no-repeat !important;background-size: contain !important;transform:scale(0.7,0.7) !important}
 /*50%*/.ytp-button-volume[data-value="normal"], .ytp-button-volume[data-value="quiet"] {background: url(http://savepic.org/7263682.png) no-repeat !important;background-size: contain !important;transform:scale(0.7,0.7) !important}
  /*0%*/.ytp-button-volume[data-value="min"] {background: url(http://savepic.org/7253442.png) no-repeat !important;background-size: contain !important;transform:scale(0.7,0.7) !important}
 /*off*/ .ytp-button-volume[data-value="off"] {background: url(http://savepic.org/7256514.png) no-repeat !important;background-size: contain !important;transform:scale(0.7,0.7) !important} }
