@-moz-document domain("virtualpopstar.com") {

  .top_container {
    position:fixed; width:100%; top:0;
    margin: 0px auto;
    border-bottom: 16px;
    border-top:0px;
    border-right:0px;
    border-left:0px;
    border-color: #fff;
    border-style: solid;
    border-image: url(http://www.virtualpopstar.com/layout/nl/images/bar_top.png?v=37929) 41% round;
}
  
  .header_container {
    z-index: -22;
    background-image: url(http://www.seepix.com.br/wp-content/uploads/2013/06/header-green.jpg);
    background-position: left;
    background-color: #01aef0;
    background-repeat:no-repeat;
}
  .bar_top{
    background-color:#f2f2f2;
    background-image:url(http://i.imgur.com/2OFkmM5.jpg);
    background-repeat:repeat;
    height: 1000px;
    position:fixed; width:100%; top:0;
    z-index: -2;

  }
  .content_container {
    width: 960px;
    overflow:auto;
    position:absolute;
    top: 0px;
    z-index: -1;
    margin-top: 190px;
    left:0;
    right:0;
    margin-left:auto;
    margin-right:auto;
   
}
 
  .block_menu_single {
    height: 31px;
    background-image: url("http://www.transparenttextures.com/patterns/asfalt-light.png");
    line-height: 31px;
    padding-left: 36px;
    color: #FFF;
    margin-bottom: 3px;
}
  
  .header_bg {
    background-image: url("http://i.imgur.com/dsMFGCa.png");
}
  
  .top_menu_container .left-menu li {
    padding-left: 9px;
    padding-right: 9px;
    border-right: 0px solid #808080;
    float: left;
}
  
  .top_menu_container .right-menu li {
    padding-left: 9px;
    padding-right: 9px;
    border-left: 0px solid #808080;
    float: right;
}
  
  .top_menu_container .menu li, .top_menu_container .menu li a {
    margin: 0px;
    font-size: 11px;
    text-decoration: none;
    color: #FFF !important;
}
  
  .content_left_area {
    width: 225px;
    position: fixed;
    margin: 0px auto;
    margin-left:735px;
}
  
  .content_right_area {
    float: left;
}

