css:
@namespace url(http://www.w3.org/1999/xhtml);
@-moz-document domain("omegle.com") {
/* General */
* {
	color:       rgba(0,0,0, 0.925) !important;
	font-size:   14px !important;
	font-weight: 300 !important;
	font-family: 'Helvetica Neue', 'Helvetica', sans-serif !important;
	background:  rgb(250,250,250) !important;
	box-sizing:  border-box !important;
}

/* Form inputs */
button,
input:not([type="checkbox"]),
textarea {
	padding:       0.5em 0.5em !important;
	border:        1px solid rgba(0,0,0, 0.125) !important;
	border-radius: 2.5px !important;
}
button,
input[type="submit"],
input[type="button"] {
	background: rgba(0,0,0, 0.0275) !important;
	cursor:     pointer !important;
}

/* Hide stupid shit */
#fb-root,
#header,
#adwrapper,
#appstore,
.newchatbtnwrapper,
.btnkbshortcut {
	display: none !important;
}

/* Introduction page */
#intro,
#footer {
	left:          0 !important;
	width:         auto !important;
	max-width:     60em !important;
	padding:       1em 1em !important;
	border:        none !important;
	border-radius: 0 !important;
	margin:        0 auto !important;
	box-shadow:    none !important;
}

/* Fix top margins */
#introtext > :first-child,
#introtext > p:first-of-type {
	padding-top: 0 !important;
	margin-top:  0 !important;
}

#topterms {
	text-align:     left !important;
	padding-bottom: 2.5em !important;
}

/* Hide 'Omegle is great on mobile' message */
#mobilesitenote {
	display: none !important;
}

/* Hide video monitoring notice */
#monitoringnotice {
	display: none !important;
}

/* Left-align topic/button headers */
h2#intoheader,
h2#startachat {
	font-weight: 400 !important;
	text-align:  left !important;
}

/* Interests editor box */
.topictageditor {
	border:        1px solid rgba(0,0,0, 0.125) !important;
	border-radius: 2.5px !important;
}
.topictageditor .topictag,
.topictageditor .topictag * {
	background: rgb(235,235,235) !important;
}
.topictageditor .topictag {
	border: 0.5px solid rgba(0,0,0, 0.125) !important;
	margin: 0 !important;
}
.topictageditor .topictagdelete {
	font-family: sans-serif !important;
	cursor:      pointer !important;
}
.topictageditor input {
	padding: 0 !important;
	border:  none !important;
}

/* Hide the box to use Facebook likes as interests */
.topictageditor + div {
	display: none !important;
}
/* Hide the box to use your university e-mail */
.topictageditor + div + div + div {
	display: none !important;
}

#chattypetextcell {
	vertical-align: top !important;
}

/* Hide the video chat button */
.chattypeorcell,
#chattypevideocell {
	display: none !important;
}
/* Hide the extra options */
#textbtnstatus,
#videobtnstatus {
	display: none !important;
}

/* Fix feedback section */
#feedback {
	margin-left: 0 !important;
}
#feedback > h2 {
	font-weight:  400 !important;
	padding-left: 0 !important;
	cursor:       pointer !important;
}
#feedback > h2::before {
	content: '\25bc';
	display: inline-block;
	width:   1.25em;
	color:   rgba(0,0,0, 0.725);
}
#feedback.collapsed > h2::before {
	content: '\25b6';
}
#feedback form {
	margin-left: 2em !important;
}

/* Hide legal text */
#footer #feedback > :first-child {
	display: none !important;
}

#footer {
	margin-bottom: 3em !important;
}

/* Chat interface */
.logwrapper,
.logbox {
	top:          0 !important;
	right:        0 !important;
	left:         0 !important;
	border:       none !important;
	margin-right: 0 !important;
}
.controlwrapper {
	right:  0 !important;
	left:   0 !important;
	bottom: 1.125em !important;
	border: none !important;
	margin: 0 !important;
}
.logbox,
.controlwrapper {
	padding: 0.5em 0.5em !important;
}

/* Message sender name */
.msgsource {
	padding-right: 0.125em !important;
	font-weight:   500 !important;
}
/* Your name */
.youmsg .msgsource {
	color: rgb(55,116,214) !important;
}
/* Stranger's name */
.strangermsg .msgsource {
	color: rgb(238,39,77) !important;
}

/* 'It may take a while to find someone with common interests...' message */
.commonlikescancel {
	display: none !important;
}

/* Disconnect messages, typing notifications, 'Find strangers with my interests', &c. */
.logitem > p.statuslog,
.logtopicsettings, .logtopicsettings *,
.logsavelinks, .logsavelinks * {
	color: rgba(0,0,0, 0.725) !important;
}

/* 'Find strangers with my interests' tick box */
.shoulduselikescheckbox {
	margin: 0px !important;
}

/* New Chat button is hidden, so make the padding consistent */
.logsavelinks {
	margin-top: 0.5em !important;
}

/* Fix margin on 'Great conversation?' */
.conversationgreat {
	padding-left: 0px !important;
}

/* Hide links for reddit and Facebook */
.logsavelinks > span:last-child {
	color:     rgba(0,0,0, 0) !important;
	font-size: 0.25px !important;
}
.logsavelinks > span:last-child * {
	display: none !important;
}
.logsavelinks > span:last-child:before {
	color:     rgba(0,0,0, 0.725) !important;
	font-size: 15px !important;
	content:   '(n/a)' !important;
}
/* Hide message about the Omegle sub-reddit */
.logitem:last-child[style="height: 23px;"],
.logitem:last-child[style="height: 50px;"] {
	display: none !important;
}

/* Fix in-chat tag editor margin */
.logitem .topictageditor {
	margin-right: 0 !important;
}

/* Chat buttons */
.controlwrapper button {
	color:  rgb(255,255,255) !important;
	border: none !important;
}
.disconnectbtnwrapper,
.sendbtnwrapper,
.chatmsgcell {
	border:        1px solid rgba(0,0,0, 0.275) !important;
	border-radius: 0px !important;
}
/* Stop */
.disconnectbtnwrapper {
	background: rgb(174,174,174) !important;
	border:     1px solid rgb(174,174,174) !important;
}
/* Really? */
.disconnectbtnwrapper.reallybtn {
	background: rgb(230,70,89) !important;
	border:     1px solid rgb(230,70,89) !important;
}
/* New */
.disconnectbtnwrapper.newbtn {
	background: rgb(91,181,109) !important;
	border:     1px solid rgb(91,181,109) !important;
}
/* Send */
.sendbtnwrapper {
	background: rgb(92,144,240) !important;
	border:     1px solid rgb(92,144,240) !important;
}

/* Message textarea */
.chatmsgwrapper,
.chatmsgwrapper textarea {
	background: rgba(255,255,255, 0.0) !important;
	padding:    0 !important;
	border:     none !important;
	box-shadow: none !important;
	resize:     none !important;
}
.chatmsgwrapper textarea {
	padding: 0.35em 0.5em !important;
}

/* Disconnected state */
.controlwrapper[style="margin-right: 168px;"] .sendbtnwrapper,
.controlwrapper[style="margin-right: 168px;"] .chatmsgcell {
	background: transparent !important;
	border:     1px solid rgba(0,0,0, 0.125) !important;
}
.controlwrapper[style="margin-right: 168px;"] .chatmsgwrapper textarea {
	color: rgba(0,0,0, 0.125) !important;
}
.controlwrapper[style="margin-right: 168px;"] .sendbtnwrapper button {
	color:      rgba(0,0,0, 0.125) !important;
	background: transparent !important;
	cursor:     default !important;
}
} /* eof */
