css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("newsmachete.com"), domain("www.newsmachete.com") {
  
  @font-face {
  font-family: 'Work Sans';
  font-style: normal;
  font-weight: 300;
  src: local('Work Sans Light'), local('WorkSans-Light'), url(http://fonts.gstatic.com/s/worksans/v1/FD_Udbezj8EHXbdsqLUplxampu5_7CjHW5spxoeN3Vs.woff2) format('woff2'), url(http://fonts.gstatic.com/s/worksans/v1/FD_Udbezj8EHXbdsqLUpl6RDOzjiPcYnFooOUGCOsRk.woff) format('woff');
}


  #share-buttons, body > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(2) > div:nth-child(1),
    body > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(3){
    display: none !important;
  }
  
  body table tr td {
    border: none  !important;
    padding: 10px 0px;
    position: relative;
    font-family: 'Work Sans' !important;
    font-weight: 300 !important;
    font-size: 24px !important;
    line-height: 1.3 !important;
  }
  body table tr td hr {
    border-width: 10px !important;
  border-top-color: #FF7764 !important;
  border-right-color: #FFF !important;
  border-bottom-color: #FF7764 !important;
  border-left-color: #FFF !important;
    position: static !important;
  }
  
 body > table:nth-child(5) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(3) {
   font-size: 20px !important;
 }
  
/* Fade in background */


 a {
   color: #386CBF !important;
	border-bottom: 1px solid rgba(56,108,191,0.4);
	font-weight: 500;
	-webkit-transition: background-color 0.3s, border-color 0.3s;
	transition: background-color 0.3s, border-color 0.3s;
   text-decoration: none !important;
}

 a:hover,
a:focus {
	border-color: rgba(74,144,255,0);
	background-color: rgba(74,144,255,0.1);
}
  
  body > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > a:nth-child(1) {
    border-bottom: none !important;
  }
  body > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > a:nth-child(1):hover,
      body > table:nth-child(3) > tbody:nth-child(1) > tr:nth-child(1) > td:nth-child(1) > a:nth-child(1):focus{
        background-color: #fff !important;
        border-color: none  !important;
  }
}
