css:
@-moz-document domain("geektv.me") {
body{
    background: #1d1d1d;
}    
/*SETTINGS FOR THE HEADER */
#header .navbar-inner {
    background-color: #235070;
    border: 0px;
}    
.navbar-link{
    text-shadow: 0 0 0 !important;
    color: white !important;
    font-weight: 500;
}
.btn-primary{
    background-color: #36BBE3 !important;
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#36BBE3), to(#35B5DB)) !important; 
}
.btn-primary{
    background-color: #2EA6C9 !important;
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#2EA6C9), to(#2EA6C9)) !important;
}    

/*SETTINGS FOR THE MAIN CONTENT */

/*container for the main content/body */
.container {
    display: flex;
    flex-flow: row wrap;
    justify-content: center;
      
}
/*sidebar */
.span2{
    min-width: 140px;
} 
.table{
    margin: 10px 0 0 0;
}
.table td{
    height: 25px;
    border: white;
}
.table img{
    margin: 5px 10px 0 0;
}
.table a{
    margin: 10px 0 0 0px;
    color: #8F8F8F;
}
.table a:hover{
    color: #36BBE3;
}
.btn-info{
    background-color: #36BBE3 !important;
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#36BBE3), to(#35B5DB)) !important; 
}
.btn-info:hover{
    background-color: #2EA6C9 !important;
    background-image: -webkit-gradient(linear, 0 0, 0 100%, from(#2EA6C9), to(#2EA6C9)) !important;
}
/*top div */
.breadcrumb{
    background-color: #525252;
    border: 1px solid #3B3B3B;
    width: calc(100% - 12px);
    
}
.breadcrumb a:hover{
    color: #36BBE3;
}    
.breadcrumb  li{
    color: #8F8F8F;
    text-shadow:  0 0 0 #8F8F8F;
}
/*video container settings */
.span12{
    border: #525252;
    margin-left: 20px;;
}    

.bs-docs-example{
    background-color: #525252;
    border: #525252;
    margin-left: 20px !important;
}
.img-polaroid{
    width: 94%;
    height: 100%;
}

.span10 table table{
    display: flex;
    justify-content: center;
}    
h3{
    font-size: 30px;
    font-weight: 100;
    margin: 20px 0 0 0;
}   
h3 a:hover{
    color: #36BBE3;
}    

/*SETTINGS FOR THE FOOTER*/
.footer{
    background-color: #1F4561;
    border: 0;
}    
@media screen and (max-width: 1200px) {
    .breadcrumb{                                    
    	min-width: calc(100% - 12px);
	}
}    
}
