css:
@-moz-document domain("chickensmoothie.com") {
#page-header
{
    background:url(http://orig00.deviantart.net/e818/f/2015/207/7/e/rgbtrbryhsrstg_by_silverhorseshoos-d92xe3x.jpg) !important;
    margin: auto;
    background-repeat: no-repeat;
    max-width: 1024px;
    border-radius: 6px;
}

#body_inner
{
    background: url(http://orig12.deviantart.net/41c0/f/2015/207/2/4/fgtgztshrtsj_by_silverhorseshoos-d92xfma.jpg) ;
}

.panel, .navbar, #page-header, #whoisonline, .forumbg, .rules, .liner, .unreadpost, .forabg
{
    box-shadow: 1px 1px 3px;
}

#page-body .linklist a, h2 a, .pagination > a, .topic-actions .page-dots, .left, #jumpbox+ h3, #jumpbox~ p, #jumpbox~ p a, label, .pagination, h2, p+ h3, #page-body > h2
{
    color:white  !important;
}

.author, h2, label span, #page-body a
{
    color: black  !important;
}
}

@-moz-document url("http://www.chickensmoothie.com/trades/"), url("http://www.chickensmoothie.com/trades/tradingcenter.php"), url("http://www.chickensmoothie.com/trades/tradingsettings.php"), url("http://www.chickensmoothie.com/trades/findpartner.php"), url-prefix("http://www.chickensmoothie.com/adoptables/"), url-prefix("http://www.chickensmoothie.com/accounts/"), url-prefix("http://www.chickensmoothie.com/pound"), url-prefix("http://www.chickensmoothie.com/archive"), url-prefix("http://www.chickensmoothie.com/help/"), url-prefix("http://www.chickensmoothie.com/payments/balance"), url-prefix("http://www.chickensmoothie.com/store/"), url-prefix("http://www.chickensmoothie.com/trades/edittrade.php?partner="), url-prefix("http://www.chickensmoothie.com/trades/sharetrade.php?id=") {
#csbody
{
    box-shadow: 1px 1px 3px;
}
}

@-moz-document url-prefix("http://www.chickensmoothie.com/candy/") {
#csbody
{
    box-shadow: 1px 1px 3px;
}
}

@-moz-document url-prefix("http://www.chickensmoothie.com/Forum/memberlist.php?mode=viewprofile&u="), url-prefix("http://www.chickensmoothie.com/trades/viewtrade.php?id=") {
h2
{
    color:white  !important;
}
}

@-moz-document url("http://www.chickensmoothie.com/trades/findpartner.php"), url("http://www.chickensmoothie.com/trades/tradingsettings.php"), url-prefix("http://www.chickensmoothie.com/Forum/ucp.php?"), url-prefix("http://www.chickensmoothie.com/Forum/oekaki/gallery.php?user="), url-prefix("http://www.chickensmoothie.com/Forum/index") {
label, p+ h3
{
    color: black  !important;
}
}

@-moz-document url("http://www.chickensmoothie.com/") {
#welcome+ div img
{
    border-radius: 12px;
}
}

@-moz-document url-prefix("http://www.chickensmoothie.com/candy/") {
#medimenu, #medicont img
{
    border-radius: 12px;
}
}
