css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document regexp("^(http|https)\\:\\/\\/(?!www).*\\.visualstudio\\.com\\/.*$") {
  
  .agile-board .horizontal .horizontal-table > .row.content {
    background-color: #FAFAFA;
  }
  
  .board-tile.ui-draggable-dragging {
    transform: rotate(5deg);
  }
  .proposed .board-tile .container {
    display: none;
  }
  
  .proposed .board-tile-content {
    max-height: 1.8em;
    overflow: hidden;
  }
  
  .proposed .board-tile-content .id-title-container {
    margin: 0;
    padding: 0;
  }
  
  .complete .board-tile .container {
    display: none;
  }
  
  .complete .board-tile-content {
    max-height: 1.8em;
    overflow: hidden;
    color: #999;
  }
  
  .complete .board-tile-content .id-title-container {
    margin: 0;
    padding: 0;
  }
}
