css:
@-moz-document url-prefix(http://vse10.ru/main/) {
body {
  /*скрывает полосу прокрутки на странице*/
  overflow:  hidden;
  /*голубой цвет, чтобы footer не был белым */
  background: #4C6B8F;
  font-family: Ubuntu-mono,Tahoma,Verdana,sans-serif;
}
.keyboard {
  /*убираю клавиатуру, что бы не отвлекала*/
  display: none !important;
}
/*Селектор элементов*/
html > body > table > tbody > tr > td > img {
  /*убираю центральный логотип вверху страницы*/
  display: none;
}
/*Селектор классов*/
.container, .top_left, .top_right, .sf, .footer {
  /*убираю остальные элементы вверху страницы*/
  display: none;
}
/*Селектор атрибутов*/
[style] {
  /*убираю оставшуюся пустую полосу вверху станицы*/
  padding: 0 !important;
}
/*Расширяю поле ввода */
.keyField {
  width: 98% !important;
  line-height: 0px !important;
  margin-top: 0px !important;
  height: 33px !important;
}
.fields {
  top: 17px !important;
}
.controlNav Div{
  overflow: visible !important;
  padding-top: 5px !important;
}
html > body > table > tbody > tr > td {
  background-color: #4C6B8F !important;
}
 
  /*Диалоговое окно. После прохождения упражнения */
.dialogInfo {
	background-color: #4C6B8F !important;
 padding: 0px 0px 0px 0px !important;
	position: fixed;
}
.dialogButtons {
	background: transperant !important;
	background-color: #4C6B8F !important;
	padding: 17px 0 20px 48px !important;
	position: static;
}
.dialogNumbers {
 margin-bottom: 0px !important;
}
.f13, .f30 {
 display: none !important;
}
.dialogStat a {
 background: #4C6B8F !important;
 background-color: #4C6B8F !important;
 padding-left: 0px !important;
 position: static;
}
.ui-corner-all {
 position: fixed !important;
 top: 5px !important;
}

/*Диалоговое окно. Псле прохождения урока*/
.dialogComlete {
	background: #4C6B8F !important;
 padding-top: 0px!important;
	margin: 0px 0px 0px 20px;
}
.dialogComlete .f14 {
	padding: 10px 0 20px 0;
}
.dialogInfo .f24 {
 color: #FFFFFF;
}
}
