css:
@-moz-document url("about:newtab") {
  /*BACKGROUND*/
  
   #newtab-vertical-margin { /* #newtab-window{ */
      background: url('http://i.imgur.com/eho7A4S.jpg') !important;
      background-size: 100% 100% !important;
   }
  /*BALANCING CSS CODE - DO NOT EDIT*/
  
   #newtab-horizontal-margin { /* #newtab-scrollbox{ */
      margin-top: 25px !important;
      background-color: transparent !important;
   }
  /*MAKING STUFF DISAPPEAR*/
  
   #newtab-search-container, #newtab-search-logo, #newtab-margin-top, #newtab-margin-bottom, #newtab-customize-button, #newtab-intro-what {
     display: none !important;
   }  
  /* OPACITY */
  
  .newtab-thumbnail {
    opacity: 0.7 !important;
  }
  /* CELL DIMENSION CONTROL */
  
   .newtab-cell {
     width: 350px !important; height: 140px !important;
   }
}
