css:
@-moz-document domain("time.precisiondev.net") {
html, body {
    height: 100%;
}

body {
    background: #333 url('http://precisiondev.net/wp-content/uploads/2015/03/pd-logo-white-252x65.png') center 24px no-repeat;
    font-family: 'Trebuchet MS', Tahoma;
}

input, select {
    font-family: 'Trebuchet MS', Tahoma;
    border-radius: 4px;
    border: solid 1px rgba(0,0,0,0.45);
}
input[type="submit"] {
    margin: 5px;
}

.TableHeader,
.TableFooter,
.TableTitle {
    background-color: rgba(255,255,255,0.25);
    font-family: 'Trebuchet MS', Tahoma;
}

.TableBody {
    background-color: rgba(255,255,255,0.65);
    font-family: 'Trebuchet MS', Tahoma;
}
.TableBody.rgRow {
    background-color: rgba(255,255,255,0.85);
}
.TableBodyAlternate {
    font-family: 'Trebuchet MS', Tahoma;
}

table[bgcolor="#ffffff"],
form > div > br,
td.TableTitle:not([align="center"]) {
    display: none;
}

table {
    margin: 0.5em 0;
}
table, tr, td, th, thead, tfoot, tbody {
    border: none !important;
    border-color: rgba(0,0,0,0) !important;
}
table tr {
    border-bottom: solid 1px rgba(255,255,255,0.10) !important;
}
table table {
    margin: 0;
}
table:nth-child(3) {
    position: fixed;
    top: 0.4em;
    right: 0.8em;
}
table:nth-child(3) td:first-of-type {
    padding-left: 1.0em;
}
table:nth-child(3) table {
    box-shadow: 0 0 30px #666;
    box-shadow: 0 0 10px #333;
}

pre {
    margin: 0;
}
}
