css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("www.anncoulter.com"), domain("anncoulter.com") {

@font-face {
font-family: 'Exo';
font-style: normal;
font-weight: 500;
src: local('Exo Medium'), local('Exo-Medium'), url(http://fonts.gstatic.com/s/exo/v4/hyACI556ZrB6j7QvrcI0OOvvDin1pK8aKteLpeZ5c0A.woff2) format('woff2'), url(http://fonts.gstatic.com/s/exo/v4/uS3pLXRHi0kuC6qZqTorF-vvDin1pK8aKteLpeZ5c0A.woff) format('woff');
}
@font-face {
font-family: 'Exo';
font-style: italic;
font-weight: 500;
src: local('Exo Medium Italic'), local('Exo-MediumItalic'), url(http://fonts.gstatic.com/s/exo/v4/gt2C348WKP6Tg5YiMi0znHYhjbSpvc47ee6xR_80Hnw.woff2) format('woff2'), url(http://fonts.gstatic.com/s/exo/v4/1LnzhM6dYPICwfoM9MfuCnYhjbSpvc47ee6xR_80Hnw.woff) format('woff');
}
@font-face {
font-family: 'Open Sans';
font-style: normal;
font-weight: 300;
src: local('Open Sans Light'), local('OpenSans-Light'), url(http://fonts.gstatic.com/s/opensans/v13/DXI1ORHCpsQm3Vp6mXoaTRampu5_7CjHW5spxoeN3Vs.woff2) format('woff2'), url(http://fonts.gstatic.com/s/opensans/v13/DXI1ORHCpsQm3Vp6mXoaTaRDOzjiPcYnFooOUGCOsRk.woff) format('woff');
}
@font-face {
font-family: 'Open Sans';
font-style: italic;
font-weight: 300;
src: local('Open Sans Light Italic'), local('OpenSansLight-Italic'), url(http://fonts.gstatic.com/s/opensans/v13/PRmiXeptR36kaC0GEAetxtTIkQYohD4BpHvJ3NvbHoA.woff2) format('woff2'), url(http://fonts.gstatic.com/s/opensans/v13/PRmiXeptR36kaC0GEAetxvR_54zmj3SbGZQh3vCOwvY.woff) format('woff');
}


section.main article h3, section.detail article h3 {
font-family: 'Exo';
font-style: normal;
font-weight: 500;
font-size: 32px !important;
}

h3 i {
font-family: 'Exo';
font-style: italic;
font-weight: 500;
}
i {
font-family: 'Open Sans';
font-style: italic;
font-weight: 300;
}
.main > table:nth-child(1) {
display: none !important;
}
section.sidebar {
display: none !important;
}
section.main, section.detail {
width: 690px !important;
color: #505050 !important;
}
body, html {
background-color: #fff !important;
}
body {
margin-top: -25px !important;
}
nav {
background-color: #293144 !important;
}
nav a {
color: #fff !important;
}
nav a:hover {
color: #000 !important;
letter-spacing: 0 !important;
font-style: normal !important;
background-color: #A9A9A9 !important;
border: none !important;
}
section.main article, section.detail {
font-size: 24px  !important;
font-family: 'Open Sans';
font-style: normal;
font-weight: 300;
}

section.main article h3 a {
color: #000  !important;
}

.archive_directory a {
font-size: 18px !important;
display: block !important;
border-bottom: 1px solid transparent  !important;
}
.archive_directory a:hover {
border-bottom: 1px dotted #0142cc !important;
}
.archive_directory a:hover, section.main article h3 a:hover {
letter-spacing: 0  !important;
}

a {
color: #FF2112 !important;
text-decoration: none !important;
transition-property: color, border-color !important;
transition-duration: .75s, 1.0s !important;
transition-timing-function: ease-out !important;
}
a:hover {
color: #0142CC !important;
font-style: italic !important;
letter-spacing:  0.75px  !important;
text-decoration: none !important;
border-bottom: 2px dotted #0142CC  !important;
}

.images-on header h1 a:hover {
border-right: transparent !important;
background: transparent url("http://www.anncoulter.com/ann_coulter.png") no-repeat scroll 10px bottom !important;
border-bottom: none !important;
}


}
