css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tweetdeck.twitter.com") {
  
  /*Light TweetDeck 3 Coco is a theme made by @CamaradeBerter for Tweetdeck's light theme.*/
  
  /*Text*/
  a
  {
    color: #c00;
    text-decoration: none;
  }
  a:hover, a:focus, .link-complex:hover .link-complex-target, .link-complex:focus .link-complex-target, .link-complex:hover .link-complex-target
  {
    color: #ffd700;
    text-decoration: none;
  }
  .release-notes-header-subtitle
  {
    color: #fff;
  }
  
  /*Buttons*/
  .btn
  {
    background: transparent;
    border: none;
    box-shadow: none;
    color: #c00;
  }
  .btn:hover, .btn:focus, .btn:active, .btn-positive:hover, .btn-on-blue:hover, .btn.is-selected
  {
    background: transparent;
    box-shadow: none;
    color: #ffd700;
  }
  .btn[disabled], .btn[disabled]:hover, .btn[disabled]:focus, .btn[disabled]:active, .btn-positive.is-disabled, .btn-positive.is-disabled:hover, .btn-positive.is-disabled:focus, .btn-positive[disabled], .btn-positive[disabled]:hover, .btn-positive[disabled]:active, .btn-on-blue.is-disabled, .btn-on-blue.is-disabled:hover, .btn-on-blue.is-disabled:focus, .btn-negative.is-disabled, .btn-negative.is-disabled:hover
  {
    background: transparent;
    color: transparent;
  }
  .btn-negative:hover, .btn-neutral-negative:hover, .btn-neutral-negative:hover .label, .btn-fav.s-favorited:hover
  {
    color: red;
    text-decoration: none;
    background: transparent;
  }
  .more-tweets-btn
  {
    background: #c00;
    color: #fff;
    box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.2);
  }
  .more-tweets-btn:hover
  {
    background: #c00;
    color: #ffd700;
    box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.2);
  }
  
  /*Inputs*/
  input, input:focus, textarea
  {
    background: transparent;
    border: none;
    box-shadow: none;
  }
  select
  {
    background: transparent;
    border: none;
    box-shadow: none;
  }
  
  /*Scrollbars*/
  .scroll-styled-v::-webkit-scrollbar-thumb, .scroll-styled-h::-webkit-scrollbar-thumb
  {
    background: rgba(0, 0, 0, 0.1);
    border-radius: 0px;
  }
  .scroll-styled-v::-webkit-scrollbar-thumb:hover, .scroll-styled-h::-webkit-scrollbar-thumb:hover
  {
    background: rgba(0, 0, 0, 0.2);
  }
  .scroll-styled-v::-webkit-scrollbar-track, .scroll-styled-h::-webkit-scrollbar-track
  {
    border: none;
  }
  
  
  /*App header*/
  .app-header
  {
    background: #c00;
    box-shadow: 3px 0px 3px rgba(0, 0, 0, 0.2);
  }
  .tweet-button /*'New Tweet' button*/
  {
    background: transparent;
    color: #fff;
    box-shadow: none;
  }
  .tweet-button:hover
  {
    background: transparent;
    color: #ffd700;
  }
  .attach-compose-buttons .tweet-button /*When the composer is open*/
  {
    background: transparent;
  }
  .app-search-input /*Search box*/
  {
    background: #c00;
    box-shadow: none;
    border-bottom: 2px solid #fff;
  }
  .app-search-input:focus, .app-search-input.is-focused
  {
    background: #c00;
    border-top: none;
    border-left: none;
    border-right: none;
    border-bottom: 2px solid #fff;
    border-radius: 6px 6px 0px 0px;
    color: #fff;
  }
  .search-input-perform-search /*Search logo*/
  {
    color: #e57f7f
  }
  .search-input-perform-search:hover
  {
    color: #ffd700;
  }
  .app-search-fake /*Search button*/
  {
    background: transparent;
    box-shadow: none;
    color: #fff;
  }
  .app-search-fake:hover
  {
    color: #ffd700;
  }
  .column-nav-item /*Columns'icons*/
  {
    background: #c00;
  }
  .column-nav-link
  {
    color: #fff;
  }
  .column-nav-link:hover
  {
    color: #ffd700;
  }
  .column-nav-link .attribution, .column-nav-link:after
  {
    color: #e57f7f;
  }
  .numbered-badge-onnav /*New messages abdge*/
  {
    border: 2px solid #c00;
  }
  .numbered-badge-onheader
  {
    border: 2px solid #c00;
  }
  .numbered-badge
  {
    background: #fff;
    color: #c00;
    border-radius: 6px;
  }
  .app-nav-link /*'Add column' button*/
  {
    color: #fff;
  }
  .app-nav-link:hover
  {
    color: #ffd700;
  }
  .with-nav-border-t:before
  {
    border: none;
  }
  .app-navigator /*The 3 buttons at the bottom of the header*/
  {
    background: transparent;
  }
  .app-nav-tab
  {
    color: #fff;
  }
  .app-nav-tab:hover
  {
    color: #ffd700;
  }
  .app-nav-tab.is-selected
  {
    background: transparent;
    color: #fff
  }
  .app-nav-tab.is-selected:hover
  {
    color: #ffd700;
  }
  .app-title /*TweetDeck logo*/
  {
    background: transparent;
  }
  
  /*Compose panel*/
  .is-open .drawer:after /*Shadow*/
  {
    box-shadow: none;
  }
  .compose
  {
    background: #fff;
    color: #c00;
  }
  .compose-header
  {
    border: none;
    background: #c00;
    color: #fff;
    height: 50px;
    line-height: 34px;
    margin: 0px -15px;
    padding: 0px 15px;
    box-shadow: 0px 3px 3px rgba(0, 0, 0, 0.2);
  }
  .compose-text-title /*'From'*/
  {
    color: #c00;
  }
  .txt-r-white /*To choose how the accounts list appears*/
  {
    color: #c00;
  }
  .compose-account /*Accounts'icons*/
  {
    background: transparent;
  }
  .compose-account-img
  {
    border-radius: 20%;
  }
  .compose-account-img:focus
  {
    box-shadow: none;
  }
  .is-selected.compose-account .compose-account-img
  {
    box-shadow: none;
  }
  .compose-account-selected /*Tick*/
  {
    border-radius: 50%;
    box-shadow: none;
  }
  .compose-account b, .compose-account p
  {
    color: #c00;
  }
  .compose-message-recipient-input-container, .compose-message-recipient-input-container.is-focused /*DM recipient*/
  {
    box-shadow: none;
    border-bottom: 2px solid #c00;
    color: #c00;
  }
  .dropdown-menu
  {
    border: none;
  }
  .compose-reply-tweet
  {
    background: transparent;
  }
  .remove-recipient, .compose-message-recipient-name
  {
    border: none;
  }
  .lst-modal /*Usernames list*/
  {
    background: #c00;
    border: none;
    border-radius: 6px;
    box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.2);
  }
  .lst li
  {
    border: none;
    color: #fff;
  }
  .lst .s-selected
  {
    background: transparent;
    color: #ffd700;
  }
  .compose-text
  {
    border-bottom: 2px solid #c00;
    border-radius: 0px;
  }
  .character-count-compose:disabled /*Count remaining characters*/
  {
    color: #c00;
  }
  .invalid-char-count:disabled
  {
    color: red;
  }
  .compose .popover /*Scheduled Tweet box*/
  {
    background: #c00;
    border-radius: 6px;
    box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.2);
  }
  .caret /*The little triangle*/
  {
    display: none;
  }
  .compose .popover input, .popover .cal
  {
    color: #fff;
  }
  .compose .popover .btn
  {
    color: #fff;
  }
  .compose .popover .btn:hover
  {
    color: #ffd700;
  }
  #calbody /*Calender*/
  {
    background: transparent;
  }
  #caldays
  {
    border: none;
  }
  #caldays span
  {
    color: #fff;
  }
  #calweeks
  {
    background: transparent;
  }
  .calweek a
  {
    border-radius: 6px;
    background: transparent;
    color: #ffd700;
  }
  .calweek a:hover
  {
    background: #e57f7f;
  }
  #calcurrent
  {
    background: #fff;
    color: #ffd700;
  }
  #caltoday
  {
    background: transparent;
    color: #fff;
  }
  a.caldisabled
  {
    background: transparent !important;
    color: transparent !important;
  }
  a.caloff
  {
    color: #e57f7f;
    background: transparent;
  }
  a.caloff:hover
  {
    background: #e57f7f;
    color: #c00;
  }
  .compose-remember-state /*'Stay open'*/
  {
    color: #c00;
  }
  
  /*Search panel*/
  .app-search .popover
  {
    border-radius: 6px;
    box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.2);
  }
  .app-search .popover input
  {
    color: #fff;
    border-bottom: 2px solid #fff;
    border-radius: 6px 6px 0px 0px;
  }
  .recent-searches-fixed-list /*Search history*/
  {
    background: #fff;
  }
  .list-item, .list-item .list-icon
  {
    color: #c00;
  }
  .list-item.is-selected, .list-item.is-selected .list-icon
  {
    background: transparent;
    color: #ffd700;
  }
  .list-item:hover txt-mute, .list-item.is-selected .txt-mute
  {
    color: #ffd700;
  }
  .list-item-button /*'Clear history' button*/
  {
    background: #c00;
    color: #fff;
    border-radius: 0px 0px 6px 6px;
    box-shadow: 0px -3px 3px rgba(0, 0, 0, 0.2);
  }
  .app-search .popover .scroll-none
  {
    background: #c00;
    border-radius: 6px 6px 0px 0px;
    box-shadow: 0px 3px 3px rgba(0, 0, 0, 0.2);
  }
  .app-search .popover ul
  {
    background: #fff;
    border-radius: 0px;
    box-shadow: none;
  }
  .accordion-divider-t /*Search controls separations*/
  {
    border: none;
  }
  .facet-type 
  {
    border: none;
  }
  .with-column-divider-bottom
  {
    border: none;
  }
  .app-search .popover .btn
  {
    color: #fff;
  }
  .app-search .popover .btn:hover
  {
    color: #ffd700;
  }
  .app-search .popover .btn:active, .app-search .popover .btn.is-selected
  {
    color: #fff;
    border-bottom: 2px solid #fff;
  }
  .facet-type.is-expanded /*Search controls*/
  {
    background: transparent;
  }
  .accordion-header, .accordion .is-active .accordion-header, .accordion .is-active .accordion-header:hover, .is-expanded .accordion-panel .txt-mute
  {
    color: #fff;
  }
  .accordion-header:hover
  {
    color: #ffd700;
  }
  .app-search .popover .facet-subtitle
  {
    color: #e57f7f;
  }
  .app-search .column-header-temp
  {
    display: none;
  }
  .app-search .popover .column-temp .column-header, .app-search .popover .column-header-temp
  {
    background: #c00;
    border: none;
  }
  .app-search .popover .column-temp .column-header a, .app-search .popover .column-header-temp a
  {
    color: #fff;
  }
  .list-divider /*Footer*/
  {
    border: none;
    background: #c00;
    border-radius: 0px 0px 6px 6px;
    box-shadow: 0px -3px 3px rgba(0, 0, 0, 0.2);
  }
  
  /*Accounts Panel*/
  .bg-r-light-gray /*Background*/ 
  {
    background: #fff;
  }
  .drawer-header /*Header*/
  {
    border: none;
    height: 50px;
    box-shadow: 0px 3px 3px rgba(0, 0, 0, 0.2);
    z-index: 2;
    background: #c00;
    color: #fff;
  }
  .drawer-header-title
  {
    color: #fff;
    padding-top: 12px;
  }
  .obj-right .txt-r-dark-gray, .drawer-header .txt-r-dark-gray
  {
    color: #e57f7f;
  }
  .drawer-header:after
  {
    background: transparent;
  }
  .drawer-content
  {
    top: 50px;
    z-index: 1;
  }
  .txt-r-dark-gray /*ID*/
  {
    color: #949799;
  }
  .txt-r-text-link /*Tweets, following and followers numbers*/
  {
    color: #292F33;
  }
  .accordion .is-active /*'Default account*/
  {
    color: #ffd700;
  }
  .account-row-separator-b:after /*Separator*/
  {
    background: transparent;
  }
  .account-settings-row.is-highlighted, .account-settings-row.is-highlighted:last-child /*Contributions*/
  {
    background: #e57f7f;
    border: none;
  }
  .bg-r-faded-gray, .contributor-row, .contributor-row[data-state="settings"], .contributor-row[data-state="confirmAdd"], .contributor-row[data-state="closed"]::after, .contributor-row[data-state="confirmRemove"]
  {
    background: transparent;
  }
  .contributor-settings-role
  {
    border: none;
  }
  .contributor-adder-block.position-sticky-stuck 
  {
    border: none;
    box-shadow: 0px 3px 3px rgba(0, 0, 0, 0.2);
    background: linear-gradient( to bottom, #e57f7f, rgba(229, 127, 127, 0.9));
    z-index: 2;
  }
  
  /*Columns*/
  .app-content, .app-columns-container, .app-columns, .is-narrow-columns .app-columns, .is-wide-columns .app-columns /*Background*/
  {
    background: #fff;
    padding: 0px;
  }
  .needs-scroll-bottom-offset.scroll-styled-h
  {
    bottom: 0px;
  }
  .column, .is-narrow-columns .column, .is-wide-columns .column
  {
    border-radius: 0px;
    margin: 0px;
    background: #fff;
    box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.2);
  }
  .column-header, .column-header-temp
  {
    background: #c00;
    border: none;
    color: #fff;
    box-shadow: 0px 3px 3px rgba(0, 0, 0, 0.2);
    z-index: 2;
  }
  .column-header input
  {
    border-bottom: 2px solid #fff;
    border-radius: 0px;
  }
  .column-header-link
  {
    background: transparent;
    border: none;
    color: #fff;
  }
  .column-number
  {
    display: none;
    color: #e57f7f;
  }
  .column-drag-handle
  {
    display: none;
  }
  .column-header .txt-mute /*ID*/
  {
    color: #fff;
  }
  .is-options-open .column-settings-link /*Options logo*/
  {
    color: #ffd700;
    background: #e57f7f;
    border-radius: 6px 6px 0px 0px;
  }
  .facet-subtitle, .toggle-item
  {
    color: #c00;
  }
  .facet-subtitle:hover, .toggle-item:hover
  {
    color: #ffd700;
  }
  .column-options
  {
    background: #e57f7f;
    z-index: 1;
  }
  .column-options .button-tray /*Footer's column options*/
  {
    background: #e57f7f;
  }
  .btn-options-tray:hover .label, .btn-neutral-positive:hover .label
  {
    text-decoration: none;
  }
  .column-message
  {
    background: #e57f7f;
    z-index: 1;
  }
  .stream-item:not(.conversation-event) /*Tweets*/
  {
    border-bottom: none;
  }
  .txt-mute a:hover, .txt-mute a:focus /*Time*/
  {
    color: #c00;
  }
  footer a:hover, footer a:hover span /*'Details'*/
  {
    color: #c00;
  }
  .avatar
  {
    border-radius: 20%;
    z-index: 1;
    box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.2);
  }
  .media-size-small, .media-size-medium /*Pictures, videos...*/
  {
    border-radius: 6px;
    z-index: 1;
    box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.2);
  }
  .in-tweet-divider::before
  {
    display: none;
  }
  .card-holder
  {
    border: none;
  }
  .quoted-tweet /*Tweet's preview*/
  {
    border: none;
    background: #c00;
    color: #fff;
    border-radius: 6px;
    box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.2);
  }
  .quoted-tweet a, .quoted-tweet .txt-mute
  {
    color: #fff;
  }
  .quoted-tweet a:hover
  {
    color: #ffd700;
  }
  .gap-chirp /*'Show more tweets' button*/
  {
    background: #e57f7f;
    color: #fff;
  }
  .pos-l /*Menu*/
  {
    z-index: 2;
    background: #c00;
    border-radius: 6px;
    box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.2);
  }
  .dropdown-menu a:hover
  {
    color: #22a677;
  }
  .input-group-button /*Collection's footer*/
  {
    border: none;
  }
  .input-group-button .btn
  {
    color: #fff;
  }
  .input-group-button .btn:hover
  {
    color: #ffd700;
  }
  .input-group-input
  {
    color: #fff;
    border-bottom: 2px solid #fff;
  }
  .add-by-url
  {
    background: #c00;
    box-shadow: 0px -3px 3px rgba(0, 0, 0, 0.2);
  }
  .tweet-dogear /*Display colored bar on the side instead of orange and green Fav/RT corners, original by @AlmtyCwrd in Snowflake*/
  {
	  display: block;
	  width: 0.1px;
	  height: 100%;
    background-image: none;
  }
  .is-favorite .tweet-dogear 
  {
	  border-right: 4px solid #fab41e;
	  box-shadow: 0px 0px 0px #fab41e;
  }
  .is-retweet.is-favorite .tweet-dogear
  {
	  border-right: 4px solid #a7c152;
	  box-shadow: 0px 0px 0px #a7c152;
  }
  .is-retweet .tweet-dogear 
  {
	  border-right: 4px solid #53cf86;
	  box-shadow: 0px 0px 0px #53cf86;
  }
  .is-selected-tweet, .is-unread 
  {
    background: none;
    border-right: 4px solid #c00;
  }
  .trends-column.is-shrunk /*Trends column*/
  {
    width: 270px;
  }
  
  /*Tweet*/
  .column-title-back
  {
    color: #fff;
  }
  .column-title-back:hover .icon
  {
    color: #ffd700;
  }
  .scroll-conversation
  {
    background: transparent;
  }
  .tweet-detail-wrapper
  {
    background: transparent;
    border: none;
  }
  .column-detail .is-selected-tweet
  {
    background: transparent;
    border-right: 4px solid #c00;
  }
  .tweet-stats
  {
    border: none;
  }
  .tweet-detail-actions /*Reply, RT, fav, more*/
  {
    border: none;
  }
  .detail-view-inline, .detail-view-inline-text /*'Reply to @...'*/
  {
    border: none;
  }
  .is-selected .reply-triangle
  {
    visibility: hidden;
  }
  .inline-reply
  {
    background: transparent;
  }
  .inline-reply .btn-neutral
  {
    color: #c00;
  }
  .social-proof-for-tweet-title
  {
    background: #e57f7f;
    color: #ffd700;
    border: none;
  }
  
  /*Messages*/
  .rpl textarea
  {
    border: none;
    box-shadow: none;
  }
  .account-link .media-grid-container /*Conversations avatars*/
  {
    border-radius: 20%;
    box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.2);
  }
  .conversation-event, .conversation-event + .stream-item:not(.conversation-event), .conversation-event:first-child
  {
    background: transparent;
    border: none;
  }
  
  /*Windows*/
  .mdl
  {
    border-radius: 6px;
    background: #fff;
    box-shadow: 3px 3px 3px rgba(0, 0, 0, 0.2);
  }
  .overlay, .ovl
  {
    background: rgba(255, 255, 255, 0.8);
  }
  .mdl-header
  {
    background: #c00;
    color: #fff;
    height: 65px;
    box-shadow: 0px 3px 3px rgba(0, 0, 0, 0.2);
    border-radius: 6px 6px 0px 0px;
  }
  .mdl-header a, .frm a
  {
    color: #fff;
  }
  .mdl-header a:hover, .frm a:hover
  {
    color: #ffd700;
  }
  .mdl .btn
  {
    color: #fff;
  }
  .mdl .btn:hover
  {
    color: #ffd700;
  }
  .mdl .btn[disabled], .mdl .btn[disabled]:hover
  {
    color: transparent;
  }
  .mdl-content, .mdl-column-med
  {
    background: transparent;
    border: none;
    z-index: 2;
  }
  .mdl-content a, .column-scroller .inline-reply .btn
  {
    color: #c00;
  }
  .mdl-content a:hover, .column-scroller .inline-reply .btn:hover
  {
    color: #ffd700;
  }
  .mdl-col-settings
  {
    background: transparent;
  }
  .mdl-column-med /*Left column*/
  {
    background: transparent;
    z-index: 1;
  }
  .mdl-column-med .pin-top
  {
    background: transparent;
    height: 50px;
    z-index: 2;
  }
  .mdl-column-med .pin-top form
  {
    color: #fff;
    border-bottom: 2px solid #fff;
  }
  .mdl-column-med .pin-top a
  {
    color: #fff;
  }
  .s-with-pinned, .l-column-scrollv
  {
    top: 50px;
    z-index: 1;
  }
  .mdl-column-med h3
  {
