@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

/*
Install Open Sans: http://www.fontsquirrel.com/fonts/open-sans
*/

/* makes tabs stretch whole tab space 
.tabbrowser-tabs:not([drag=detach]) > .tabbrowser-tab:not([pinned])[fadein] {
min-width: 10px !important;
max-width: 100% !important;
} */


/* fades buttons */
#home-button, #downloads-button, #new-tab-button, #ctraddon_bookmarks-button, #ctraddon_reload-button, bookmarks-button, reload-button, #preferences-button {
    opacity: 0.3 !important;
}

/* show or hide non pinned tabs favicons  */
.tabbrowser-tab:not([pinned]) .tab-icon-image {
display: none !important;
}

/*comment to show favicons 
#identity-box, #identity-icon-labels {
    display: none;
}*/

/* fix white bg when opening new tabs */
browser { 
    background-image: none !important; 
    background-color: #1d1e2a !important; 
}

#window-controls {
    display: none !important;
}

/* fonts */
#urlbar, #TabsToolbar .tabbrowser-tab, menu, .autocomplete-richlistbox, #menubar-items, statuspanel[type="overLink"] .statuspanel-label, toolbarbutton, #placeContent, #placesList, .editBMPanel_rowLabel, .textbox-input-box, .viewer-title.label.toolbar, .downloadTarget, .downloadDetails, #bookmarks-view-children, #sidebar-search-label, #sidebar-title, .menubar-text {
    font-family:  Open Sans;
    font-weight: 600;
    font-size: 13px !important;
}

/* tab colors */
#TabsToolbar .tabbrowser-tab, #ctraddon_addon-bar, .tabbrowser-strip.treestyletab-tabbar-placeholder, #toolbar-menubar, #menubar-items, menubar > menu, 
.tabbrowser-arrowscrollbox, #navigator-toolbox > _moz_generated_content_after, #TabsToolbar, .tab-background-middle {
    background: #121212 !important;
    /*background-image: url("http://puu.sh/iiYc2/4a7739ed32.png")!important;   */
    color: #6D7393 !important;
    box-shadow: none !important;
    border: none !important;
    text-shadow: none !important;
}

/* url colors */
#urlbar-wrapper, #urlbar, #nav-bar {
     background: #121212 !important;
     color: #464B6F !important;
}
#navigator-toolbox, toolbox, #urlbar-container, toolbaritem {
     background-color: #121212 !important;
     border: 0px !important;
    box-shadow: none !important;    
}

/* changes color of selected tab */
#TabsToolbar .tabbrowser-tab[selected] {
    /*opacity: 0.8 !important;*/
    color: #464B6F !important;
    background: #1d1e2a  !important;
    background-clip: padding-box !important;
    border-bottom: none !important;
}
/* changes color of tab on hover */
#TabsToolbar .tabbrowser-tab:hover {
    color: #161721 !important;
    background: #474c70  !important;
}

/* menu bar color */
#main-menubar, #toolbar-menubar, #menubar-items, menubar > menu {
    background: #161721 !important;
    /*color: #926d7b !important;*/
    text-shadow: none !important;
    height: 15px !important;
    border: none !important;
    margin-top: 0px !important;
}

/* borders/curves around tabs */
#main-window .tabbrowser-tab > .tab-stack, .tabbrowser-tab[pinned][titlechanged]:not([selected="true"]) > .tab-stack > .tab-content {
  border-width: 0px 1px 1px 1px !important;
  border-radius: 0px !important; 
  border-style: solid !important;
  border-top-color: #1d1e2a !important;
  border-bottom-color: #1d1e2a !important;
  border-left-color: #1d1e2a !important;
  border-right-color: #1d1e2a !important;
}    

.tab-icon-image {
    opacity: 0.5 !important;
    filter: grayscale(0.3);
    /*filter: brightness(50%);*/
	-webkit-filter: brightness(50%);
	-o-filter: brightness(50%);
	-ms-filter: brightness(50%);
}
.tab-content {
    padding-left: 4px !important;
    padding-right: 1px !important;
}

/*removes window borders */
#main-window:not([chromehidden*="toolbar"])[sizemode="normal"] {
 margin-top:-2px!important;
 margin-left:-1px!important;
 margin-right:-1px!important;
 margin-bottom: -1px !important;
}  
 /* use last line with caution, can~ crash flash items, removes ability to resize */  
/*-moz-appearance: none !important;*/

.autocomplete-richlistbox {
    background: #1d1e2a !important;
    color: #6f535e !important;
    box-shadow: none !important;
    border: none !important;
    width: 300px !important;
}

/* remove memory fox and reader icon */
#memfx-toolbar-button, #reader-mode-button, #readinglist-addremove-button {
    display: none !important;
}

#customizableui-special-spring3, .textbox-search-icon, .textbox-search-clear {
    display: none;
}

#ctraddon_addon-bar {
    border: none !important;
    background: #121212 !important;
}

statuspanel[type="overLink"] .statuspanel-label {
background: #1d1e2a !important;
border: 0px solid #3f3f3f !important;
color: #6f535e !important;
}

statuspanel .statuspanel-label {
background: #1d1e2a !important;
border: 0px solid #3f3f3f !important;
color: #6f535e !important;
font-family:  Open Sans;
font-weight: 600;    
font-size: 13px !important;  
/*margin-bottom: 16px !important;    */
}

statuspanel[type="status"] .statuspanel-label[value^="Looking"],
statuspanel[type="status"] .statuspanel-label[value^="Connect"],
statuspanel[type="status"] .statuspanel-label[value^="Waiting"],
statuspanel[type="status"] .statuspanel-label[value^="Transfer"] {
background: #1d1e2a !important;
border: 0px solid !important;
color: #6f535e !important;
font-family:  Open Sans;
font-weight: 600;
font-size: 13px !important; 
}
    
/*remove internal border*/
#main-window:not([chromehidden*="toolbar"])[sizemode="normal"]
{
 margin-top:-1px!important;
 margin-left:-1px!important;
 margin-right:-1px!important;
 margin-bottom:-1px !important;
 }

/*tab height */
#TabsToolbar .tabbrowser-tabs {
    /*min-height: 26px !important;
    max-height: 26px !important;*/
    margin-top: 0px !important;
    margin-bottom: 0px !important;
    text-align: center !important;
    margin-left: -4px !important;
    margin-right: -15px !important;
    width: 22px !important;
}

/* tree style settings */

#tabbrowser-tabs, .treestyletab-toolbar-inner-box {
    width: 12px !important;
}

.tab-icon-image {
    margin-left: -1px  !important;
}

.tabbrowser-tab:not([pinned]) .tab-icon-image {
    margin-left: -3px  !important;
}

/* remove shitty fog */
#TabsToolbar:not(:-moz-lwtheme)::after {display: none;
}

/* remove close button */
#tabbrowser-tabs tab .tab-close-button {
    display: none !important;
}

/* bg new tab button */
#wrapper-new-tab-button:hover {
    background-color: #44496c !important;
}

/* changes bg color of pinned tabs when updated */
.tabbrowser-tab[pinned][titlechanged]:not([selected="true"]) > .tab-stack > .tab-content {
    opacity: 0.8 !important;
  background: #926d7b !important;
}

/* library window */
#placesToolbar {
    background: #323448 !important;
}

/* bookmarks dropdown*/
menu.bookmark-item:not([query]):not([label="Bookmarks Toolbar"]), menuitem.bookmark-item, menu.bookmark-item:not([query]):not([label="Bookmarks Toolbar"])[open] { 
list-style-image: url("http://puu.sh/ilKtS/ade209857b.ico") !important;
-moz-image-region: rect(0 16px 16px 0) !important;
}

#TabsToolbar .tab-background {
    margin: 0 !important;
    background: transparent !important;
}

#TabsToolbar .tab-background-start,
#TabsToolbar .tab-background-end {
    display: none !important;
}

#TabsToolbar .tab-background-middle {
    margin: -6px -4px !important;
    background: transparent !important;
    border: 0px !important;
}

/* removes seperators between pinned tabs */
#TabsToolbar .tabbrowser-tab:after,
#TabsToolbar .tabbrowser-tab:before {
    display: none !important;
}

/* codes for restored add on bar */

.subviewbutton.panel-subview-footer {
    padding-top: 6px !important;
    padding-bottom: 6px !important;
}


/* remove back/forward buttons */
#back-button, #urlbar-go-button, #urlbar-stop-button {
    display: none;
}
#nav-bar  #back-button > .toolbarbutton-icon {
 display: none !important;
}
 #forward-button {
 display: none !important;
}


#back-button, #back-button .toolbarbutton-icon:hover, #back-button, #back-button .toolbarbutton-icon, #forward-button .toolbarbutton-icon, #forward-button {
       list-style-image:url("http://puu.sh/hq4g9/f1fb8f0055.png")!important;
       border-radius: 0px !important;
       background: transparent !important;
       background-color: transparent !important;
       border: none !important;
       padding: 2px !important;
       box-shadow: none !important;     
       margin-left: 0px !important; 
    margin-top: 0px !important; 
    margin-bottom: 1px !important;
      height: 23px !important;
       width: 19px !important;
    background-color: #323448 !important;
   }

.toolbarbutton-icon, #back-button {
       box-shadow: none !important;     
}

#back-button .toolbarbutton-icon {
-moz-image-region:rect(0, 11px, 11px, 0)!important;
}

#forward-button .toolbarbutton-icon {
-moz-image-region:rect(0, 22px, 11px, 10px)!important;
}

#urlbar-container {
     padding-bottom: 0px !important;
}

#bookmarks-menu-button:hover, .dropmarker-icon:hover {
    background: #323448 !important;
}

.dropmarker-icon {
    background: url('http://puu.sh/hq6mm/16a1e14f4f.ico') !important;
    list-style-image: url("http://puu.sh/hq6mm/16a1e14f4f.ico") !important;
    background-repeat: no-repeat  !important;
    background-color: #323448 !important;
    background-position: center !important;
    opacity: .8 !important;
    margin-left: 2px !important;
    margin-right: 0px !important;
    box-shadow: none !important;
    width: 10px !important;
    height: 10px !important;
}

#bookmarks-menu-button, #bookmarks-menu-button:hover {
    background: url('http://puu.sh/hq6mm/16a1e14f4f.ico') !important;
    list-style-image: url("http://puu.sh/hq6mm/16a1e14f4f.ico") !important;
    background-color: #161721 !important;
    background-repeat: no-repeat  !important;
    background-position: center !important;
    opacity: 1 !important;
    margin-top: -3px !important;
    margin-right: 3px !important;
    box-shadow: none !important;
    width: 10px !important;
    height: 10px !important;
}

#PanelUI-button, #PanelUI-button:hover {
    background: url('http://puu.sh/irR1O/35406c7958.png') !important;
    background-repeat: no-repeat !important;
    background-position: center !important;
    box-shadow: none !important;
    margin-top: 0px !important;
    width: 10px !important;
    height: 10px !important;
    display: none !important;
  }
 
#PanelUI-button .button-icon,
#PanelUI-button .button-text {
    display: none !important;
}
#PanelUI-menu-button {
    opacity: 0 !important;
    width: 10px !important;
    margin-right: -15px !important;
}

.dropmarker-icon-icon, .dropmarker-icon, .toolbarbutton-menubutton-dropmarker {
    display: none !important;
}

/* url height etc */
#urlbar, #nav-bar, #urlbar-container {
    border: none !important;
    height: 22px !important;
    margin-top: 0px !important;
    border-radius: 0px !important;
    box-shadow: none !important;    
}

#urlbar {
    border: 0px solid !important;
    text-align: center !important;
    margin-left: 10px !important;
    margin-top: 0px !important;
    border-radius: 0px;
    padding: 0px !important;
    height: 20px !important;
}

#nav-bar {
    /*bottom: 0 !important;
    height: 15px !important;
    text-align: left !important;
    border: 0px !important;
    position: fixed !important;
    right: 0 !important;
    width: 100% !important;
    margin-top: 0px !important;*/
}    

#urlbar-go-button, #urlbar-stop-button { 
    display: none !important;
  }

#dropmarker-icon, #historydropmarker, #autocomplete-history-dropmarker, #urlbar-history-dropmarker, #dropmarker {
    display: none !important;
}
#autocomplete-history-dropmarker, #urlbar-history-dropmarker, #urlbar-reload-button {
    display: none !important;
}
.autocomplete-history-dropmarker.urlbar-history-dropmarker {
    display: none !important;
}
#plugins-notification-icon, #notification-anchor-icon, #notification-popup-box {
    display: none !important;  
}

#bookmarks-menu-button toolbarbutton { visibility:collapse !important; }
/* remove tab scrolling */

.scrollbutton-up, .scrollbutton-down, #alltabs-button {
    display: none !important;
}


/* Addons Page */

/* background */
#categories, #view-port, #addons-page, .status-control-wrapper, .content-container, .addon.addon-view, #userstyle-sorting, #view-port-container, #greasemonkey-sort-bar, #featured-addons, .category {
  background: #121212 !important;
  background-color: #121212 !important;
  border: none !important;
}

/* text */
.advancedinfo-container, .name-container, .category, .text-link, .details.button-link {
  color: #44496c !important;
}

/* buttons */
#new-userstyle, #install-from-url,.addon-control, #userstyle-sorting-status, #userstyle-sorting-name, #userstyle-sorting-type, .sorter, #back-btn, #forward-btn, #header-utils-btn {
  background: #121212 !important;
  background-color: #121212 !important;
  border: 1px solid #44496c !important;
  color: #44496c !important;
  box-shadow: none !important;
}
