css:
@-moz-document domain("play.pokemonshowdown.com"), domain("psim.us") {
  body {
    background: url("http://i.imgur.com/4O4b5Ds.jpg");
  }
  
  .ps-room.ps-room-light {
    border-color: #534072;
    background: transparent;
  }
  
  .ps-room.ps-room-light.scrollable {
    background: rgba(232, 212, 216, 0.5);
  }
  
  .roomlist a.ilink {
    background: url("http://i.imgur.com/aY6RNWj.png?1");
    border-color: #534072;
    box-shadow: none;
    color: #534072;
    text-shadow: 0px 1px 0px #F3E8FF;
  }
  
  .roomcounters button {
    background: url("http://i.imgur.com/jIEd93S.png?2");
    border-color: #534072;
    box-shadow: none;
    color: #534072;
    text-shadow: 0px 1px 0px #F3E8FF;
  }
  
  .roomlist a.ilink:hover {
    background: url("http://i.imgur.com/l52VsVz.png?1");
    border-color: #534072;
    box-shadow: none;
    color: #534072;
    text-shadow: 0px 1px 0px #E4CFF9;
  }
  
  .roomlist a.ilink small {
    color: #534072;
  }
  
  .roomcounters button:hover {
    background: url("http://i.imgur.com/PcvlZtL.png?1");
    border-color: #534072;
    box-shadow: none;
    color: #534072;
    text-shadow: 0px 1px 0px #E4CFF9;
  }
  
  .header {
    background: rgba(232, 212, 216, 0.5);
  }
  
  .maintabbarbottom {
    border-color: #534072;
    background: #F3E8FF;
  }
  
  .tabbar a.button.notifying {
    border-color: #AE5E9B;
    background: #E598CE;
    color: #B3160F;
  }
  
  .tabbar a.button.notifying:hover {
    border-color: #AE5E9B;
    background: #EFAEDB;
    color: #B3160F;
  }
  
  .tabbar a.button.cur, .tabbar a.button.cur:hover {
    border-color: #534072;
    color: #534072;
    background: #F3E8FF;
  }
  
  .tabbar a.button {
    border-color: #534072;
    background: #EFDEFF;
    text-shadow: none;
  }
  
  .tabbar a.button:hover {
    background: #F3E8FF;
  }
  
  .tabbar a.button.subtle-notifying {
    color: #AA0058;
    border-color: #534072;
    background: #EFDEFF;
    text-shadow: none;
  }
  
  .tabbar a.button.subtle-notifying:hover {
    background: #F3E8FF;
  }
  
  .closebutton, .minimizebutton {
    color: #534072;
  } 
  
  .chat-log {
    background: #F3E8FF url("http://i.imgur.com/Uo3HGPZ.png?1") top right no-repeat;
  }
  
  .chat-log a.ilink {
    color: #7D40C1;
    font-weight: bold;
  }
  
  .chat-log a.ilink:hover {
    color: #8E61C1;
  }
  
  .textbox, .textbox:hover {
    box-shadow: none;
    border: 1px solid #534072;
  }
  
  .textbox:focus {
    box-shadow: 0px 0px 5px #B981FF;
  }
  
  .chat-log-add {
    border-color: #534072;
    background: rgba(228, 205, 253, 0.9); 
  }
  
  .userlist {
    border-color: #534072;
    color: #534072;
    background: rgba(228, 205, 253, 0.9);
  }
  
  .userlist li {
    border-color: #534072;
    background-color: rgba(244, 244, 244, 0.5);
  }
  
  .userlist li button:hover {
    background: #D3A6FF;
  }
  
  .userlist li em.group {
    color: #534072;
  }
  
  .menugroup {
    border: 1px solid #534072;
    background: rgba(201, 207, 219, 0.4)
  }
  
  .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button, .select {
    background: url("http://i.imgur.com/UrZX1Ei.png?1");
    border-color: #534072;
    box-shadow: none;
    color: #534072;
    text-shadow: 0px 1px 0px #F3E8FF;
  }
  
  .mainmenu .menugroup:nth-child(2) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(2) .button:hover, .mainmenu .menugroup:nth-child(2) p:nth-child(3) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(1) .button:hover, .mainmenu .menugroup:nth-child(3) p:nth-child(2) .button:hover, .select:hover {
    background: url("http://i.imgur.com/Kl9Y7Jd.png?1");
    border-color: #534072;
    box-shadow: none;
    color: #534072;
    text-shadow: 0px 1px 0px #E4CFF9;
  }
  
  .mainmenu .menugroup:nth-child(1) .button {
    background: url("http://i.imgur.com/aY6RNWj.png?1");
    border-color: #534072;
    box-shadow: none;
    color: #534072;
    text-shadow: 0px 1px 0px #F3E8FF;
  }
  
  .mainmenu .menugroup:nth-child(1) .button:hover {
    background: url("http://i.imgur.com/l52VsVz.png?1");
    border-color: #534072;
    box-shadow: none;
    color: #534072;
    text-shadow: 0px 1px 0px #E4CFF9;
  }
  
  .select::after {
    color: #534072;
  }
  
  .select:disabled {
    border-color: #534072;
    background: rgba(228, 205, 253, 0.55); 
    color: #534072 !important;
    text-shadow: none;
  }

  .mainmenufooter a {
    color: #E9FFFD;
    text-shadow: 0px 0px 1px #000, 0px 0px 1px #000, 0px 0px 1px #000;
  }
  
  .mainmenufooter a:hover {
    color: #F3E8FF;
  }
  
  .mainmenufooter {
    color: #E9FFFD;
    background: transparent;
    border: none;
  }
  
  .ps-popup {
    border-color: #534072;
    background: url("http://i.imgur.com/pk85LsG.jpg?1");
    box-shadow: 1px 1px 0px rgba(255, 255, 255, 0.4) inset, -1px -1px 0px rgba(221, 221, 221, 0.4) inset, 2px 2px 3px rgba(0, 0, 0, 0.2);
  }
  
  .ps-popup a.ilink, .ps-popup a {
    color: #534072;
  }
  
  .popupmenu h3 {
    color: #534072;
  }
  
  .popupmenu button.sel {
    border-color: #534072;
  }
  
  .popupmenu button.sel:hover, .popupmenu button:hover {
    border-color: #534072;
    background: rgba(255, 255, 255, 0.2);
  }
  
  .pm-log, .pm-window.focused .pm-log, .pm-log-add, .pm-window.focused .pm-log-add, .pm-window h3, .pm-window h3:hover, .pm-window.focused h3, .pm-window.focused h3:hover {
    border-color: #534072;
  }
  
  .pm-window h3, .pm-window h3:hover, .pm-window.focused h3, .pm-window.focused h3:hover {
    color: #534072;
  }
  
  .challenge {
    border-color: #534072;
    color: #534072;
    background: rgba(228, 205, 253, 0.9);
  }
  
  .battleform .buttonbar button {
    background: url("http://i.imgur.com/UrZX1Ei.png?1");
    border: 1px solid #534072;
    box-shadow: none;
    color: #534072;
    text-shadow: 0px 1px 0px #E4CFF9;
    border-radius: 3px;
    padding: 4px 2px;
  }
  
  .ps-room .battle-controls {
    background: transparent !important;
  }

}
