css:
@-moz-document domain("soundex.ru") {
body {
    font-family:Verdana;
}

#ipsLayout_sidebar {
    display:none;
}

.ipsAdvertisement {
    display:none;
}

.ipsColumn_medium {
    width:130px;
}

.ipsType_sectionHead {
    font-size:12px; /*name user*/
}

.ipsComment {
    margin-bottom:0px;
}

.ipsComment_meta {
    padding: 5px 10px 5px 0 !important;
}

.ipsType_normal {
    font-size:13px;
}

.ipsType_medium { /*changed*/
    font-size:9px;
    color:#000;
}

p {
    -webkit-margin-before:0.2em;                                            
    -webkit-margin-after: 0.2em;
}

.ipsResponsive_hidePhone {
    display:none;
}

.cAuthorPane {
    padding: 5px 5px 5px 5px !important;
}

#elLogo {
    display:none;
}

.ipsPad {
    padding:5px;
}

.ipsApp .ipsSpacer-top {
    margin-top:7px;
}


.ipsUserPhoto_large img, img.ipsUserPhoto_large, .ipsUserPhoto_large:after {
    width:100px;
    height:100px;
}

.ipsUserPhoto {
    margin:0px;
    border-radius:0px;
}

.ipsType_pageTitle, .ipsWidget.ipsWidget_horizontal .ipsWidget_title {
    font-family: Arial;
    font-size:24px;
    font-weight:normal;
    color: #004a6f !important;
    margin-bottom:2px;
}

hr.ipsHr {
    margin: 7px 0;
}

.cWidgetContainer[data-widgetArea="footer"] {
    margin-top:4px;
}

.ipsSubNavigation li:not( .ipsMenu_item ):not( .ipsMenu_sep ) {
    font-size:12px;
    font-weight:bold;
    text-decoration: underline;
    color: #006699 !important ;        
}

#elHeaderSubLinks a, .ipsBreadcrumb > ul a {
    color: #006699 !important ;        
}

.ipsPageHeader {
    padding-bottom:4px !important;
}

.ipsWidget .ipsDataItem_title{
    font-size:13px;
    font-weight:bold;
    color: #006699;
}
.ipsWidget .ipsType_medium {
    font-size: 12px;
}

/*.ipsBox:not( .ipsBox_transparent ) {
    background-color:#E3E3E3;
}*/

.ipsDataItem_main, .ipsDataItem_stats, .ipsDataItem_lastPoster, .ipsDataItem_generic, .ipsDataItem_modCheck, .ipsDataItem_icon {
    padding: 2px 10px;
}

.ipsDataItem, .ipsBox {
   border-color:#CAD1D5;
}

.bannergroup, #logo {
    display:none;
}

.ipsDataList_large .ipsDataItem_icon {
    padding: 2px 10px;
}

.ipsDataList_large .ipsDataItem_main, .ipsDataList_large .ipsDataItem_stats, .ipsDataList_large .ipsDataItem_lastPoster, .ipsDataList_large .ipsDataItem_generic {
    padding: 2px 10px;
}

.cForumRow .ipsDataItem_lastPoster {
    width:40%;
    max-width:40%;
    min-width:370px;
}

.ipsDataItem_lastPoster a {
  font:12px Verdana,Arial,sans-serif;
}
.ipsType_large {
    font-size:13px;
    font-weight:bold;
}

.ipsType_light time{
    font-size:9px;
}

.ipsDataItem_statsLarge .ipsDataItem_stats_number,.ipsDataItem_stats_type  {
    font-size:11px;
}

.ipsBox:not( .ipsBox_transparent ), .ipsAreaBackground_light {
    background:#F5F5F5;
}

.ipsDataItem .ipsType_richText {
    font-size:9px;
}

.cAuthorPane {
    border-right:1px solid #CAD1D5;
}

#ipsLayout_mainArea .ipsBox {
    border-radius:0;
    border-width:0 1px 1px 1px;
}

.ipsWidget {
    border-radius:6px !important;
    border-width:1px !important;
}

blockquote.ipsQuote div.ipsQuote_citation {
    background:#CAD1D5;
}

blockquote.ipsQuote {
    background:#FFFFFF;
}


.yjroundout:first-child{
    display:none !important;
}



h3{
  font-size:16px;
  font-weight:bold;
  font-family: Arial;
}

h5 {
  font-size:16px;
  font-family: Verdana;
}

.ipsColumn_wide {
    width:160px;
}

.ipsLayout_container {
    height:54px;
}

.category {
    width:33%;
}

div#jomclassifieds tr:nth-child(odd) {
    background:#F5F5F5;
}

div#jomclassifieds .row1, div#jomclassifieds .row0 {
    border-bottom: 1px solid #CAD1D5;
    border-top: 1px solid #CAD1D5;
}

#ipsLayout_header header,#header {
	padding-top: 0px;
    background:#000;
    background-image:url('https://copy.com/66HfmOoqcmln6tFP');
/*    
    background-image:url('https://encrypted-tbn2.gstatic.com/images?q=tbn:ANd9GcS-WugBfjPxt919eYBAQfA89f4FuaQc0--857JbWXSF_584r8fcoA');*/
    background-repeat:no-repeat;
}

#elUserNav {
    top:12px;
}

#yjsg1 {
    padding:0px;
}

#stylef6 #header {
    height:70px !important;
}

#elSearchNavContainer, #elMobileNav {
    background:#3d5e78;
/* Permalink - use to edit and share this gradient: http://colorzilla.com/gradient-editor/#7abcff+6,3d5e78+34,3d5e78+100 */
     background: -webkit-linear-gradient(top, rgba(117,166,196,1) 20%,rgba(61,94,120,1) 57%,rgba(61,94,120,1) 100%);
/*    background: linear-gradient(to top, #204066, #006699);*/
}

.ipsType_sectionTitle, .ipsAreaBackground_dark, td.cCalendar_today, .ipsApp .ipsButton_primary {
    background:#3d5e78 !important;
}

.ipsApp .ipsButton_important {
    background:#4EB539;
}
}
