css:
@-moz-document domain("immobilienscout24.de") {
  
  /* readable font */
  body {
    font-size: 17px;
  }
  
  /* content shall expand to whole width */
  #is24-content, .page-wrapper, .content-wrapper{
    outline: 0px solid red;
    max-width: 100% !important;
    width: 100% !important;
  }
  #listContainer{
    
    outline: 0px solid green;
    width: 68% !important;
  }
  
  /* do not cut title of search results */
  .title a{
    white-space: normal !important;
  }
  .resultlist_entry_data .header .title, .maxtwoliner{
    max-height: 5em !important;
  }
  
  /* hide none-informational entries */
  #maklerAd, .gutter-horizontal-s, .sourcecode, [data-report-event],  .is24-exposeId-replacement, [data-qa="is24qa-verfuegbare-services-label"]{
    display:none !important;
  }
}
