css:
@-moz-document domain("play.typeracer.com"), domain("data.typeracer.com") {
a, a:hover, a:active, a:visited, .gwt-TabBar .gwt-TabBarItem {
	text-decoration: none!important;
	transition: color 0.2s ease-in-out!important;
}
a, .gwt-TabBar .gwt-TabBarItem {
	color: #ff4081!important;	
}

.lightLabel {
	color: #FFF!important;	
}
.roomInvitePopup, .typingChallengeDialog {
	width: auto!important;
}
.countdownPopup.horizontalCountdownPopup {
	box-shadow: 0 16px 24px 2px rgba(0, 0, 0, 0.14), 0 6px 30px 5px rgba(0, 0, 0, 0.12), 0 8px 10px -5px rgba(0, 0, 0, 0.4)!important;
	clip: auto!important;
	border: none!important;
}
.themeHeader, body {
	font-family: 'Roboto', 'Noto', Verdana, sans-serif!important;
	background: #3F51B5!important;
	color: #444!important;
}
.themeHeader {
	box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.14), 0 1px 5px 0 rgba(0, 0, 0, 0.12), 0 3px 1px -2px rgba(0, 0, 0, 0.2)!important;
	position: relative!important;
}
.main {
	background: #eee!important;	
}
.popupContent > table {
	background: #FFF!important;	
}
.title h1 {
	font-family: Verdana, sans-serif;
	text-transform: uppercase!important;
	text-shadow: 1px 1px 0px rgba(0, 0, 0, 0.3)!important;
	font-size: 30px!important;
}
.title h1 a {
	color: white!important;
}
.container {
	background: none!important;
	
}
#hostpageBannerAds {
	display:none!important;	
}
.navigation {
	vertical-align: bottom!important;
	
}
.navigation a:hover {
	background: none!important;
	color: #f39c12!important;
}
.navigation a {
	transition:	color 0.2s ease-in-out!important;
	text-transform: uppercase!important;
	padding: 8px 10px!important;
	font-family: 'Roboto', 'Noto', sans-serif!important;
	font-size: 110%!important;
	color: hsl(231, 100%, 85%)!important;
}
.navigation > div {
	margin-bottom: 30px!important;
}
#tr_textHeader h1 {
	color: hsl(99, 90%, 47%)!important;
}
#tr_textHeader h2 {
	color: hsl(99, 90%, 47%)!important;
	font-weight: normal!important;
}
#tr_textHeaderAnimation {
	font-family: Roboto, noto, sans-serif!important;	
}
.footer {
	background: #3F51B5!important
}
.footer a {
	color: hsl(243, 30%, 85%)!important;
}
.footer a:hover {
	text-decoration: none!important;
	color: #FFF!important;
}
.footer a {
	transition:	color 0.1s ease-in-out!important;
}
.footer {
	color: white!important;
	box-shadow: 0 -3px 2px 0 rgba(0, 0, 0, 0.14), 0 -1px 5px 0 rgba(0, 0, 0, 0.12), 0 -3px 1px -2px rgba(0, 0, 0, 0.2)!important;
}
#tstats {
	padding-right: 50px!important;	
}
.main {
	padding: 20px 12px 0 25px!important;
}
#tstats-edit a.gwt-Anchor {
	display: inline-block!important;
	margin-right: 10px!important;
	transition: all 0.2s ease-in-out!important;
	text-decoration: none!important;
	color: #FFF!important;
	padding: 7px 14px!important;
	border: 2px solid #FFF!important;
}
#tstats-edit a.gwt-Anchor:hover {
	text-decoration: none!important;
	color: #E5E5E5!important;
	border: 2px solid #E5E5E5!important;
}
#tstats td {
	font-family: Roboto, noto, sans-serif!important;
	color: #FFF!important;
	text-transform: uppercase!important;
	white-space: nowrap!important;
}
#tstats-skill a, #tstats-wpm a, .mainUserInfoBoxWpm, #tstats-cg div.gwt-Label {
	color: #FDFF08!important;
	display: inline-block!important;
	margin-top: 10px!important;
}
.PopupWithIcon, .playerInfoPopup{
	clip: auto!important;
	box-shadow: 0 16px 24px 2px rgba(0, 0, 0, 0.14), 0 6px 30px 5px rgba(0, 0, 0, 0.12), 0 8px 10px -5px rgba(0, 0, 0, 0.4)!important;
	background-color: #FFF!important;
	border: none!important;
	padding: 0!important;
}
.playerInfoPopup .player-name {
	background: none!important;	
}
.PopupWithIcon.editUserPopup {
	width: auto!important;	
}
.popupContent .heading {
	text-transform: uppercase!important;	
}
.popupContent .heading, .playerInfoPopup .player-name {
	color: #FFF!important;
	margin: 20px 0 20px 15px!important;
	font-family: 'Roboto', 'noto', sans-serif!important;
	font-size: 16px!important;
}
.popupContent > table > tbody > tr:first-child, .popupContent > table > tbody > tr:first-child, .playerInfoPopup .dialogTop {
	background-color: #ff4081!important;
}
.popupContent > table > tbody > tr:nth-child(2), .playerInfoPopup .dialogMiddle .dialogMiddleCenter {
	margin: 13px!important;
	display: inline-block!important;
	font-size: 13px!important;
}
.popupContent td[style="width: 16px;"] {
	width: 0!important;	
}
.popupContent td[style="width: 16px;"] ~ td {
	font-size: 15px!important;
	font-family: 'Roboto', 'noto', sans-serif!important;
	font-weight: bold!important;
	color: #ff4081!important;
}
.popupContent input[type="text"], .popupContent input[type="password"] {
	outline: none!important;
	border-bottom: 1px solid #CCC!important;
	border-top: none!important;
	border-left: none!important;
	border-right: none!important;
	transition: all 0.2s linear!important;
	font-family: Verdana, sans-serif!important;
	color: #838383!important;
	padding: 2px 3px!important;
}
.popupContent .gwt-Label {
	font-size: 13px!important;	
}
.popupContent input[type="text"]:focus, .popupContent input[type="password"]:focus {
	border-bottom-color: #00BCD5!important;
}
.lblUserName, .playerInfoPopup .player-name {
	border: none!important;
	color: #FFF!important;
	margin: 15px!important;
}
.gwt-Button, .popupContent .gwt-Anchor, .playerInfoPopup .gwt-Anchor:not([target="_blank"]) {
    padding: 5px 20px!important;
	margin-bottom: 10px!important;
    background-color: #ff4081!important;
    outline: none!important;
    border: none!important;
	text-transform: uppercase!important;
	font-family: 'Roboto', 'noto', sans-serif!important;
	color: #FFF!important;
	box-shadow: 0 2px 2px 0 rgba(0, 0, 0, 0.1), 0 1px 5px 0 rgba(0, 0, 0, 0.1), 0 3px 1px -2px rgba(0, 0, 0, 0.1)!important;
	transition: all 0.2s ease-in-out!important;
}
.popupContent .gwt-Anchor {
	display: inline-block!important;
	margin: 10px!important;
}
.gwt-Button:active, .popupContent .gwt-Anchor:active, .playerInfoPopup .gwt-Anchor:active {
	box-shadow: 0 8px 10px 1px rgba(0, 0, 0, 0.14), 0 3px 14px 2px rgba(0, 0, 0, 0.12), 0 5px 5px -3px rgba(0, 0, 0, 0.4)!important;
}
.UserInfoContextMenu {
	box-shadow:	0 16px 24px 2px rgba(0, 0, 0, 0.14), 0 6px 30px 5px rgba(0, 0, 0, 0.12), 0 8px 10px -5px rgba(0, 0, 0, 0.4)!important;
	border: none!important;
}
.PopupWithIcon.DemoteSkillLevelPopup {
	padding: 0!important;
	
}
.gwt-DisclosurePanel.gwt-DisclosurePanel-open > tbody > tr:nth-child(2) {
	padding-left: 10px!important;
	display: inline-block!important;
}
.PopupWithIcon.skillLevelInfoPopup td:nth-child(2) {
	margin-left: 8px!important;
	display: inline-block!important;
}

.popupContent .timeDisplay .time {
	color: #FFF!important;	
}
.playerInfoPopup .dialogMiddleCenterInner.dialogContent > table > tbody > tr:nth-child(3) {
	display: inline-block!important;
	margin-bottom: 15px!important;
}
.playerInfoPopup .dialogMiddleCenterInner.dialogContent > table > tbody > tr:nth-child(2) > td > table > tbody > tr > td:first-child {
	border-radius: 50%!important;
	display: inline-block!important;
	overflow: hidden!important;
}
.chrome_m {
	background-color: #eee!important;	
}
.messagesPanel, .users-list {
	border: none!important;	
}

.popupContent input[type="checkbox"] {
	vertical-align: middle!important;
	margin-right: 5px!important;
}

.MainUserInfoEditor img[src="http://play.typeracer.com/clear.cache.gif"] {
	visibility: hidden!important;
}
