}

.gameView .inputPanel input.txtInput {
	border-bottom: 2px solid #838383;
	border-top: none!important;
	border-left: none!important;
	border-right: none!important;
	outline: none!important;
	background: none!important;
}
.gameView .inputPanel input.txtInput:focus {
	outline: none!important;
	border-bottom: 2px solid #00BCD5!important;
}
.gameView .inputPanel .txtInput.txtInput-error:focus, .gameView .inputPanel .txtInput.txtInput-error {
	border-bottom: 2px solid #F00!important;
	background: none!important;
}
.mainViewportHolder span[style="text-decoration: underline; color: rgb(153, 204, 0);"], .mainViewportHolder span[style="color: rgb(153, 204, 0);"] {
	text-decoration: none!important;
	color: #07AC7E!important;
}
.inputPanel span[style="text-decoration: underline; color: red;"] {
	text-decoration: none!important;
}
.scoreboard .progressBar {
	border-bottom: 1px dashed rgba(0, 0, 0, 0.5);	
}
.roomSection input.AdvancedTextBox.txtChatMsgInput {
	background: #eee!important;
	border-left:none!important;
	border-right:none!important;
	border-top:none!important;
	border-bottom: 1px solid #838383!important;
	transition: all 0.2s ease-in-out!important;
}
.roomSection input.AdvancedTextBox.txtChatMsgInput:focus {
	outline: none!important;
	border-bottom: 1px solid #00BCD5!important;
}
}
