css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("meneame.net") {
  a {
    color: #1236FF;
  }
  
  a:hover, a:focus {
    color: #0600FF;
  }
  
  a:visited {
    color: #1A1B8B;
  }
  
  /* Cabecera 1 */
  #header-top #notifier:hover {
    background: #FFF none repeat scroll 0% 0%;
    color: #00F;
  }
  
  #header-top ul#userinfo li a:hover {
    color: #008AFF ;
  }
  /* Cabecera 2 */
  .header-menu01 ul li a {
    color: #000CFF;
    /*padding-bottom: 0px;*/
  }
  
  .header-menu01 ul li a:hover {
    color: #000CFF;
    padding-bottom: 0px;
  }
  
  .header-menu01 .selected a, .header-menu01 ul li a:hover {
    border-bottom: 5px solid #000CFF;
    background: none;
/*    background: transparent url("data:image/png;base64,iVBORw0KGgoAAAANSUhEUgAAAAEAAAADCAIAAADdv/LVAAAAFElEQVQI12P4n8LAxMDAwMTAwAAADwEBaFtNWv8AAAAASUVORK5CYII=") repeat-x scroll 100% 100%;*/
  }
  
  div.news-shakeit {
    border-color: #D7D7D7;
  }
  
  .news-shakeit .votes {
    background: transparent -moz-linear-gradient(center top , #FFFBB1 0%, #FCFFE4 100%) repeat scroll 0% 0%;
  }
  
  .news-shakeit {
    color: #0015FF;
  }
  /* Menear */
  .menealo {
    border-color: #D7D7D7;
  }
  
  .menealo a {
    background: transparent -moz-linear-gradient(center top , #7CBAEA 0%, #2D77CE 100%) repeat scroll 0% 0%;
  }
  
  .menealo span {
    background: transparent -moz-linear-gradient(center top , #529EDA 0%, #1D579B 100%) repeat scroll 0% 0%;
    border-color: #0003FF #000CFF #4A59FF;
  }
  
  .menealo a:hover {
    color: white;
    background: transparent -moz-linear-gradient(center top , rgb(125, 185, 232) 0%, rgb(30, 87, 153) 100%) repeat scroll 0% 0%;
  }
  
  .menealo:hover {
    border: 1px solid #0015FF;
  }
  
  .news-shakeit .votes a {
    color: #1236FF;
  }
  
  .news-shakeit .clics {
    background-color: #E0EFFC;
    background-image: none;
    /*background: transparent -moz-linear-gradient(center top , #ABD3F5 0%, #99C9F0 100%) repeat scroll 0% 0%;*/
    /*background: transparent -moz-linear-gradient(center top , #5297CF 0%, #5297CF 100%) repeat scroll 0% 0%;*/
  }
  
  /* No publicadas */
  .news-shakeit.mnm-queued .votes, .news-shakeit.mnm-queued .clics {
    background-color: #FFFCED;
    border-color: #D7D7D7;
  }
  /* Contador de comentarios*/
  .comments-counter .counter {
    background-color: #06F;
  }
  
  /* Comentarios */
  .comment-body {
    background-color: #E2ECFF;
    border-color: #0093FF;
  }
  
  .comment-body.high {
    background-color: #ADC5FF;
  }
  
  .comment-body.author {
    background-color: #E8EFD7;
  }
  .comment-body.high.author {
    background-color: #D1DBB7;
  }
  
  .comment-body.user {
    border: 1px solid #00B7FF;
  }
  
  .comment-body .tooltip {
    color: #0065C8;
  }
  
  .comment-votes-info a {
    color: #004CFF;
  }
  
  .comment-body a strong {
    color: #03A;
  }
  
  /* Destacadas */
  .sidebox + div {
    display: none;
  }
  
  .sidebox + .sidebox {
    display: block !important;
  }
  
  #sidebar >  div {
    display: none;
  }
  
  #sidebar > .sidebox {
   display: block !important;
  }
  
  .sidebox.red .header {
    background-color: #00239E;
  }
  
  .sidebox .votes, .sidebox .clics {
    border: 1px solid #FFF;
    background-color: #FBFAC8;
    color: #0027FF;
  }
  
  .sidebox .votes.queued {
    border-color: #3D91FF;
  }
  
  .sidebox.brown .header {
    background-color: #A86C04;
  }
  
  .sidebox .cell {
    padding: 2px 0px;
  }
  
  /* Páginas */
  .pages a {
    color: #1236FF;
    border: 1px solid #1236FF;
  }
  .pages span.current, .pages a:hover {
    color: #FFF;
    background-color: #1236FF;
    border: 1px solid #1236FF;
  }
  
  .pages span.nextprev {
    border: 1px solid #C5CAFF;
    color: #C5C8FF;
  }
  
  .pages span {
    color: #1236FF;
  }
  
  /* Banner */
  #banner_right {
    display: none;
  }
  
  /* Select */
  select {
    color: #06F;
  }
  
  /* Envia comentario */
  legend, legend a, legend a:visited {
    background: #06F none repeat scroll 0% 0%;
    border-radius: 3px;
    padding: 1px 6px;
  }
  
  fieldset {
    background: #E2ECFF none repeat scroll 0% 0%;
  }
  
  .emoji-kbd-btn.active {
    border: 1px solid #00B7FF;
    background-color: #ADD9FF;
  }
  
  .emoji-panel {
    border-color: #00C0FF;
  }
  
  .emojiCat li {
    border-color: #00C0FF #00C0FF -moz-use-text-color;
  }
  
  .commentform a.emoji-tab {
    background-color: #ADD9FF;
  }
  
  .commentform a.emoji-tab.active {
    background-color: #06F;
  }
  
  /* m/ */
  label {
    color: #777;
  }
  
  .genericform {
    color: #777;
  }
  
  /* Fisgona */
  .sneaker-legend {
    color: #03F;
    background-color: #E2ECFF 
  }
  
  .sneaker-title strong {
    color: #0078FF;
  }
  
  .sneaker-published {
    color: #03F;
  }
  
  /* Botón */
  button, .button, input[type="submit"]{
    background-color: #06F;
  }
   
  button:hover, .button:hover, input[type="submit"]:hover{
    background-color: #00A5FF;
  }
  
  /* Nota */
  .note {
    color: #00A5FF;
  }
  
  #footthingy p {
    border-bottom: 1px solid #B7B7B7;
  }
}
